    makeDraggable(element) {
        let pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
        let isDragging = false;
        const header = element.querySelector('.diagnostic-header');

        // Ensure element has proper positioning
        element.style.position = 'fixed';

        header.onmousedown = dragMouseDown;

        function dragMouseDown(e) {
            e = e || window.event;
            e.preventDefault();
            e.stopPropagation();

            isDragging = true;
            pos3 = e.clientX;
            pos4 = e.clientY;

            // Add dragging class for visual feedback
            element.classList.add('dragging');

            // Attach events to document to handle dragging outside element
            document.addEventListener('mouseup', closeDragElement);
            document.addEventListener('mousemove', elementDrag);

            // Prevent text selection while dragging
            document.body.style.userSelect = 'none';
            document.body.style.webkitUserSelect = 'none';
        }

        function elementDrag(e) {
            if (!isDragging) return;

            e = e || window.event;
            e.preventDefault();
            e.stopPropagation();

            pos1 = pos3 - e.clientX;
            pos2 = pos4 - e.clientY;
            pos3 = e.clientX;
            pos4 = e.clientY;

            // Calculate new position
            let newTop = element.offsetTop - pos2;
            let newLeft = element.offsetLeft - pos1;

            // Keep element within viewport bounds
            const rect = element.getBoundingClientRect();
            const viewportWidth = window.innerWidth;
            const viewportHeight = window.innerHeight;

            // Constrain to viewport
            newLeft = Math.max(0, Math.min(newLeft, viewportWidth - rect.width));
            newTop = Math.max(0, Math.min(newTop, viewportHeight - rect.height));

            // Apply new position
            element.style.top = newTop + "px";
            element.style.left = newLeft + "px";
        }

        function closeDragElement() {
            isDragging = false;

            // Remove dragging class
            element.classList.remove('dragging');

            // Remove event listeners
            document.removeEventListener('mouseup', closeDragElement);
            document.removeEventListener('mousemove', elementDrag);

            // Restore text selection
            document.body.style.userSelect = '';
            document.body.style.webkitUserSelect = '';
        }

        // Handle touch events for mobile
        header.ontouchstart = function(e) {
            e.preventDefault();
            const touch = e.touches[0];
            dragMouseDown({
                clientX: touch.clientX,
                clientY: touch.clientY,
                preventDefault: () => e.preventDefault(),
                stopPropagation: () => e.stopPropagation()
            });
        };
    }

    copyErrorsToClipboard() {
        try {
            // Get all errors from the error log
            const allErrors = this.errorLog.slice(); // Copy array

            if (allErrors.length === 0) {
                this.showCopyFeedback('copy-errors-btn', 'No errors to copy', false);
                return;
            }

            // Format errors for clipboard
            const errorText = this.formatErrorsForClipboard(allErrors);

            // Copy to clipboard using modern API
            if (navigator.clipboard && window.isSecureContext) {
                navigator.clipboard.writeText(errorText).then(() => {
                    this.showCopyFeedback('copy-errors-btn', `Copied ${allErrors.length} errors`, true);
                }).catch(() => {
                    // Fallback to legacy method
                    this.fallbackCopyToClipboard(errorText);
                });
            } else {
                // Fallback for older browsers or non-secure contexts
                this.fallbackCopyToClipboard(errorText);
            }

        } catch (error) {
            console.error('Copy to clipboard failed:', error);
            this.showCopyFeedback('copy-errors-btn', 'Copy failed', false);
        }
    }

    formatErrorsForClipboard(errors) {
        const timestamp = new Date().toISOString();
        const header = `=== UNIFIED DIAGNOSTICS ERROR LOG ===\nExported: ${timestamp}\nTotal Errors: ${errors.length}\n\n`;

        const formattedErrors = errors.map((error, index) => {
            const errorTime = new Date(error.timestamp).toISOString();
            const errorNumber = (index + 1).toString().padStart(3, '0');

            let formattedError = `[${errorNumber}] ${errorTime} [${error.level.toUpperCase()}]\n`;
            formattedError += `Message: ${error.message}\n`;

            if (error.filename) {
                formattedError += `File: ${error.filename}:${error.lineno}:${error.colno}\n`;
            }

            if (error.stack) {
                formattedError += `Stack Trace:\n${error.stack}\n`;
            }

            formattedError += `${'â”€'.repeat(80)}\n`;

            return formattedError;
        }).join('\n');

        const footer = `\n=== END ERROR LOG ===\nGenerated by Unified Diagnostics System\nTotal entries: ${errors.length}`;

        return header + formattedErrors + footer;
    }

    fallbackCopyToClipboard(text) {
        try {
            // Create temporary textarea
            const textarea = document.createElement('textarea');
            textarea.value = text;
            textarea.style.position = 'fixed';
            textarea.style.left = '-9999px';
            textarea.style.top = '-9999px';
            document.body.appendChild(textarea);

            // Select and copy
            textarea.focus();
            textarea.select();
            const successful = document.execCommand('copy');

            // Clean up
            document.body.removeChild(textarea);

            if (successful) {
                this.showCopyFeedback('copy-errors-btn', `Copied ${this.errorLog.length} errors`, true);
            } else {
                this.showCopyFeedback('copy-errors-btn', 'Copy failed', false);
            }

        } catch (error) {
            console.error('Fallback copy failed:', error);
            this.showCopyFeedback('copy-errors-btn', 'Copy not supported', false);
        }
    }

    showCopyFeedback(buttonId, message, success) {
        const button = document.getElementById(buttonId);
        if (!button) return;

        // Store original content
        const originalText = button.textContent;
        const originalTitle = button.title;

        // Show feedback
        button.textContent = success ? 'âœ“' : 'âœ—';
        button.title = message;
        button.classList.add(success ? 'copied' : 'copy-error');

        // Reset after delay
        setTimeout(() => {
            button.textContent = originalText;
            button.title = originalTitle;
            button.classList.remove('copied', 'copy-error');
        }, 2000);
    }

    copyAllDiagnosticData() {
        try {
            // Get complete diagnostic data
            const diagnosticData = this.exportDiagnosticData();

            // Format for clipboard
            const formattedData = this.formatDiagnosticDataForClipboard(diagnosticData);

            // Copy to clipboard using modern API
            if (navigator.clipboard && window.isSecureContext) {
                navigator.clipboard.writeText(formattedData).then(() => {
                    this.showCopyFeedback('copy-all-btn', 'Complete diagnostics copied', true);
                }).catch(() => {
                    // Fallback to legacy method
                    this.fallbackCopyAllData(formattedData);
                });
            } else {
                // Fallback for older browsers or non-secure contexts
                this.fallbackCopyAllData(formattedData);
            }

        } catch (error) {
            console.error('Copy all diagnostic data failed:', error);
            this.showCopyFeedback('copy-all-btn', 'Copy failed', false);
        }
    }

    formatDiagnosticDataForClipboard(data) {
        const timestamp = new Date().toISOString();
        let output = `=== UNIFIED DIAGNOSTICS COMPLETE REPORT ===\n`;
        output += `Generated: ${timestamp}\n`;
        output += `System: ${data.systemInfo?.userAgent || 'Unknown'}\n`;
        output += `Platform: ${data.systemInfo?.platform || 'Unknown'}\n`;
        output += `Monitoring Active: ${data.monitoringActive}\n`;
        output += `State Snapshots: ${data.stateHistory?.length || 0}\n`;
        output += `Total Errors: ${data.errorLog?.length || 0}\n\n`;

        // Current System State
        if (data.stateHistory && data.stateHistory.length > 0) {
            const currentState = data.stateHistory[data.stateHistory.length - 1];
            output += `=== CURRENT SYSTEM STATE ===\n`;
            output += `Timestamp: ${new Date(currentState.timestamp).toISOString()}\n\n`;

            // Editor State
            output += `[EDITOR]\n`;
            output += `Available: ${currentState.editor?.available || false}\n`;
            output += `Ready: ${currentState.editor?.ready || false}\n`;
            output += `Current Tool: ${currentState.editor?.currentTool || 'none'}\n\n`;

            // Tools State
            output += `[TOOLS]\n`;
            output += `Tool Count: ${currentState.tools?.toolCount || 0}\n`;
            output += `Active Tool: ${currentState.tools?.activeTool || 'none'}\n`;
            if (currentState.tools?.availableTools) {
                output += `Available Tools:\n`;
                currentState.tools.availableTools.forEach(tool => {
                    output += `  - ${tool.name}: ${tool.visible ? 'visible' : 'hidden'}, ${tool.enabled ? 'enabled' : 'disabled'}\n`;
                });
            }
            output += `\n`;

            // Canvas State
            output += `[CANVAS]\n`;
            output += `Visible: ${currentState.canvas?.canvasVisible || false}\n`;
            output += `Canvas Count: ${currentState.canvas?.canvasCount || 0}\n`;
            if (currentState.canvas?.dimensions) {
                output += `Dimensions: ${currentState.canvas.dimensions.width}x${currentState.canvas.dimensions.height}\n`;
            }
            output += `\n`;

            // Shapes State
            output += `[SHAPES]\n`;
            output += `Total Shapes: ${currentState.shapes?.totalShapes || 0}\n`;
            if (currentState.shapes?.shapeTypes) {
                output += `Shape Types:\n`;
                Object.entries(currentState.shapes.shapeTypes).forEach(([type, count]) => {
                    output += `  - ${type}: ${count}\n`;
                });
            }
            output += `\n`;
        }

        // Performance Metrics
        if (data.performanceMetrics && Object.keys(data.performanceMetrics).length > 0) {
            output += `=== PERFORMANCE METRICS ===\n`;
            Object.entries(data.performanceMetrics).forEach(([key, value]) => {
                if (typeof value === 'object' && value !== null) {
                    output += `${key}:\n`;
                    Object.entries(value).forEach(([subKey, subValue]) => {
                        output += `  ${subKey}: ${subValue}\n`;
                    });
                } else {
                    output += `${key}: ${value}\n`;
                }
            });
            output += `\n`;
        }

        // Error Log Summary
        if (data.errorLog && data.errorLog.length > 0) {
            output += `=== ERROR LOG SUMMARY ===\n`;
            output += `Total Errors: ${data.errorLog.length}\n\n`;

            // Group errors by type
            const errorTypes = {};
            data.errorLog.forEach(error => {
                errorTypes[error.level] = (errorTypes[error.level] || 0) + 1;
            });

            output += `Error Breakdown:\n`;
            Object.entries(errorTypes).forEach(([type, count]) => {
                output += `  ${type.toUpperCase()}: ${count}\n`;
            });
            output += `\n`;

            // Recent errors (last 5)
            output += `Recent Errors (Last 5):\n`;
            const recentErrors = data.errorLog.slice(-5);
            recentErrors.forEach((error, index) => {
                const errorTime = new Date(error.timestamp).toLocaleTimeString();
                output += `${index + 1}. [${errorTime}] ${error.level.toUpperCase()}: ${error.message}\n`;
            });
        }

        output += `\n=== END DIAGNOSTIC REPORT ===\n`;
        output += `Generated by Unified Diagnostics System v1.0\n`;
        output += `For technical support, include this complete report\n`;

        return output;
    }

    fallbackCopyAllData(text) {
        try {
            // Create temporary textarea
            const textarea = document.createElement('textarea');
            textarea.value = text;
            textarea.style.position = 'fixed';
            textarea.style.left = '-9999px';
            textarea.style.top = '-9999px';
            document.body.appendChild(textarea);

            // Select and copy
            textarea.focus();
            textarea.select();
            const successful = document.execCommand('copy');

            // Clean up
            document.body.removeChild(textarea);

            if (successful) {
                this.showCopyFeedback('copy-all-btn', 'Complete diagnostics copied', true);
            } else {
                this.showCopyFeedback('copy-all-btn', 'Copy failed', false);
            }

        } catch (error) {
            console.error('Fallback copy all data failed:', error);
            this.showCopyFeedback('copy-all-btn', 'Copy not supported', false);
        }
    }

    startMonitoring() {
        if (this.monitoringActive) return;

        this.monitoringActive = true;

        // Regular state capture
        this.monitoringInterval = setInterval(() => {
            this.captureCurrentState();
            this.updateDashboard();
        }, 2000); // Every 2 seconds

        console.log('ğŸ”„ Unified monitoring started');
    }

    stopMonitoring() {
        if (!this.monitoringActive) return;

        this.monitoringActive = false;

        if (this.monitoringInterval) {
            clearInterval(this.monitoringInterval);
        }

        console.log('â¹ï¸ Unified monitoring stopped');
    }

    captureCurrentState() {
        const timestamp = Date.now();

        try {
            const state = {
                timestamp,
                editor: this.captureEditorState(),
                tools: this.captureToolState(),
                canvas: this.captureCanvasState(),
                shapes: this.captureShapeState(),
                input: this.captureInputState(),
                errors: this.captureErrorState(),
                performance: this.capturePerformanceState()
            };

            // Add to history
            this.stateHistory.push(state);
            if (this.stateHistory.length > this.maxHistorySize) {
                this.stateHistory.shift();
            }

            return state;
        } catch (error) {
            console.error('State capture failed:', error);
            return { error: error.message, timestamp };
        }
    }

    captureEditorState() {
        try {
            const editorState = {
                available: typeof window.tlEditor !== 'undefined',
                initialized: false,
                currentTool: null,
                ready: false
            };

            if (window.tlEditor) {
                editorState.initialized = window.tlEditor.initialized || !!window.tlEditor.store;

                // Try to get current tool
                try {
                    if (typeof window.tlEditor.getCurrentTool === 'function') {
                        editorState.currentTool = window.tlEditor.getCurrentTool();
                    }
                } catch (e) {
                    editorState.currentToolError = e.message;
                }

                editorState.ready = editorState.initialized && !editorState.currentToolError;
            }

            return editorState;
        } catch (error) {
            return { error: error.message };
        }
    }

    captureToolState() {
        try {
            const toolButtons = document.querySelectorAll('[data-tool]');
            const toolState = {
                availableTools: [],
                activeTool: null,
                toolCount: toolButtons.length
            };

            toolButtons.forEach(button => {
                const tool = {
                    name: button.dataset.tool,
                    visible: button.offsetParent !== null,
                    enabled: !button.disabled,
                    active: button.classList.contains('active')
                };

                toolState.availableTools.push(tool);

                if (tool.active) {
                    toolState.activeTool = tool.name;
                }
            });

            return toolState;
        } catch (error) {
            return { error: error.message };
        }
    }

    captureCanvasState() {
        try {
            const canvasElements = document.querySelectorAll('canvas, .tl-canvas');
            const canvasState = {
                canvasCount: canvasElements.length,
                canvasVisible: false,
                dimensions: null
            };

            if (canvasElements.length > 0) {
                const canvas = canvasElements[0];
                canvasState.canvasVisible = canvas.offsetParent !== null;

                if (canvasState.canvasVisible) {
                    const rect = canvas.getBoundingClientRect();
                    canvasState.dimensions = {
                        width: rect.width,
                        height: rect.height,
                        top: rect.top,
                        left: rect.left
                    };
                }
            }

            return canvasState;
        } catch (error) {
            return { error: error.message };
        }
    }

    captureShapeState() {
        try {
            // Check both DOM shapes and TLStore shapes
            const domShapes = document.querySelectorAll('svg rect, svg ellipse, svg path, svg g[data-shape]');
            const storeShapes = window.tlEditor?.store?.getCurrentPageShapes() || [];

            const shapeState = {
                totalShapes: Math.max(domShapes.length, storeShapes.length),
                domShapes: domShapes.length,
                storeShapes: storeShapes.length,
                shapes: storeShapes, // Include actual shape objects for comparison
                shapeTypes: {},
                recentChanges: false
            };

            // Count DOM shapes
            domShapes.forEach(shape => {
                const type = shape.tagName.toLowerCase();
                shapeState.shapeTypes[type] = (shapeState.shapeTypes[type] || 0) + 1;
            });

            // Count store shapes (more reliable for TLDraw)
            storeShapes.forEach(shape => {
                const type = shape.type || 'unknown';
                shapeState.shapeTypes[type] = (shapeState.shapeTypes[type] || 0) + 1;
            });

            // Check for recent changes (compare with previous state)
            if (this.stateHistory.length > 0) {
                const lastState = this.stateHistory[this.stateHistory.length - 1];
                const lastShapeCount = lastState.shapes?.totalShapes || 0;
                shapeState.recentChanges = shapeState.totalShapes !== lastShapeCount;
            }

            return shapeState;
        } catch (error) {
            return { error: error.message };
        }
    }

    captureInputState() {
        try {
            return {
                mouseEventsActive: !!document.onmousedown || !!document.onmousemove,
                touchEventsActive: !!document.ontouchstart || !!document.ontouchmove,
                keyboardEventsActive: !!document.onkeydown || !!document.onkeyup,
                lastInteractionTime: this.lastInteractionTime || null,
                pointerCoordinates: this.currentPointerCoordinates || { x: null, y: null },
                lastPointerUpdate: this.lastPointerUpdate || null
            };
        } catch (error) {
            return { error: error.message };
        }
    }

    captureErrorState() {
        try {
            return {
                totalErrors: this.errorLog.length,
                recentErrors: this.errorLog.slice(-5),
                errorTypes: this.getErrorTypeCounts(),
                lastErrorTime: this.errorLog.length > 0 ? 
                              this.errorLog[this.errorLog.length - 1].timestamp : null
            };
        } catch (error) {
            return { error: error.message };
        }
    }

    capturePerformanceState() {
        try {
            const perfData = {
                memory: performance.memory ? {
                    used: Math.round(performance.memory.usedJSHeapSize / 1024 / 1024),
                    total: Math.round(performance.memory.totalJSHeapSize / 1024 / 1024),
                    limit: Math.round(performance.memory.jsHeapSizeLimit / 1024 / 1024)
                } : null,
                timing: performance.timing ? {
                    loadComplete: performance.timing.loadEventEnd - performance.timing.navigationStart,
                    domReady: performance.timing.domContentLoadedEventEnd - performance.timing.navigationStart
                } : null
            };

            return perfData;
        } catch (error) {
            return { error: error.message };
        }
    }

    getErrorTypeCounts() {
        const counts = {};
        this.errorLog.forEach(error => {
            counts[error.level] = (counts[error.level] || 0) + 1;
        });
        return counts;
    }

    getUniqueErrorsWithCounts() {
        const errorGroups = new Map();

        this.errorLog.forEach((error, index) => {
            // Create a unique key based on message and level (but not timestamp or stack)
            // Make the key more specific to avoid over-grouping
            const key = `${error.level}:${error.message}:${error.filename || 'unknown'}:${error.lineno || 0}`;

            console.log(`Processing error ${index + 1}: ${error.message.substring(0, 50)}...`);

            if (errorGroups.has(key)) {
                // Update existing error group
                const existingGroup = errorGroups.get(key);
                existingGroup.count++;
                existingGroup.latestTimestamp = error.timestamp;
                existingGroup.allTimestamps.push(error.timestamp);
                console.log(`  â†³ Grouped with existing (now count: ${existingGroup.count})`);
            } else {
                // Create new error group
                errorGroups.set(key, {
                    level: error.level,
                    message: error.message,
                    filename: error.filename,
                    lineno: error.lineno,
                    colno: error.colno,
                    stack: error.stack,
                    count: 1,
                    firstTimestamp: error.timestamp,
                    latestTimestamp: error.timestamp,
                    allTimestamps: [error.timestamp]
                });
                console.log(`  â†³ Created new error group`);
            }
        });

        // Convert to array and sort by latest timestamp (most recent first)
        return Array.from(errorGroups.values()).sort((a, b) => b.latestTimestamp - a.latestTimestamp);
    }

    updateDashboard() {
        if (!this.dashboardElement) return;

        try {
            const currentState = this.stateHistory[this.stateHistory.length - 1];

            // Update system status
            const statusElement = document.getElementById('system-status');
            if (statusElement && currentState) {
                const editor = currentState.editor;
                const tools = currentState.tools;
                const canvas = currentState.canvas;

                statusElement.innerHTML = `
                    <span class="status-indicator ${editor.ready ? 'status-ok' : 'status-error'}"></span>Editor: ${editor.currentTool || 'none'}<br>
                    <span class="status-indicator ${tools.toolCount > 0 ? 'status-ok' : 'status-warning'}"></span>Tools: ${tools.toolCount} (Active: ${tools.activeTool || 'none'})<br>
                    <span class="status-indicator ${canvas.canvasVisible ? 'status-ok' : 'status-error'}"></span>Canvas: ${canvas.canvasVisible ? 'Ready' : 'Not Found'}<br>
                    <span class="status-indicator status-ok"></span>Shapes: ${currentState.shapes.totalShapes}
                `;
            }

            // Update error count to show unique errors (total occurrences)
            const errorCountElement = document.getElementById('error-count');
            if (errorCountElement) {
                const uniqueErrors = this.getUniqueErrorsWithCounts();
                const totalOccurrences = this.errorLog.length;
                const uniqueCount = uniqueErrors.length;

                if (uniqueCount !== totalOccurrences) {
                    errorCountElement.textContent = `${uniqueCount} unique (${totalOccurrences} total)`;
                } else {
                    errorCountElement.textContent = totalOccurrences;
                }
            }

            // Update error list with ALL unique errors for the session
            const errorListElement = document.getElementById('error-list');
            if (errorListElement) {
                if (this.errorLog.length === 0) {
                    errorListElement.innerHTML = '<div style="color: rgba(255, 255, 255, 0.5); font-style: italic;">No errors detected</div>';
                } else {
                    // Group errors by message to avoid duplicates but track occurrences
                    const uniqueErrors = this.getUniqueErrorsWithCounts();

                    console.log(`ğŸ“Š Displaying ${uniqueErrors.length} unique errors from ${this.errorLog.length} total errors`);

                    // Build the HTML for all errors
                    const errorListHTML = uniqueErrors.map((errorGroup, index) => {
                        return `
                            <div class="error-entry" style="margin-bottom: 8px; animation: errorSlideIn 0.3s ease ${index * 0.05}s both;">
                                <div style="display: flex; justify-content: space-between; align-items: center;">
                                    <div style="font-weight: 600; color: #ff6666; font-size: 10px;">
                                        ${new Date(errorGroup.latestTimestamp).toLocaleTimeString()} [${errorGroup.level.toUpperCase()}]
                                    </div>
                                    ${errorGroup.count > 1 ? `<div style="background: rgba(255, 0, 0, 0.3); color: white; font-size: 8px; padding: 1px 4px; border-radius: 3px; font-weight: bold;">Ã—${errorGroup.count}</div>` : ''}
                                </div>
                                <div style="margin-top: 2px; line-height: 1.2; font-size: 10px;">
                                    ${errorGroup.message.length > 100 ? errorGroup.message.substring(0, 100) + '...' : errorGroup.message}
                                </div>
                                ${errorGroup.filename ? `<div style="font-size: 9px; color: rgba(255, 255, 255, 0.6); margin-top: 2px;">ğŸ“ ${errorGroup.filename}:${errorGroup.lineno}</div>` : ''}
                                ${errorGroup.count > 1 ? `<div style="font-size: 8px; color: rgba(255, 255, 255, 0.5); margin-top: 2px;">First: ${new Date(errorGroup.firstTimestamp).toLocaleTimeString()}</div>` : ''}
                            </div>
                        `;
                    });

                    // Set the complete HTML
                    errorListElement.innerHTML = errorListHTML.join('');

                    // Auto-scroll to show latest errors
                    setTimeout(() => {
                        errorListElement.scrollTop = errorListElement.scrollHeight;
                    }, 100);
                }
            }

            // Update current state
            const currentStateElement = document.getElementById('current-state');
            if (currentStateElement && currentState) {
                const pointerInfo = currentState.input?.pointerCoordinates;
                const pointerTime = currentState.input?.lastPointerUpdate;
                const pointerDisplay = pointerInfo && pointerInfo.x !== null ? 
                    `Pointer: (${pointerInfo.x}, ${pointerInfo.y})` : 
                    'Pointer: (not tracked)';

                currentStateElement.innerHTML = `
                    Last Update: ${new Date(currentState.timestamp).toLocaleTimeString()}<br>
                    Monitoring: ${this.monitoringActive ? 'ğŸ”„' : 'â¹ï¸'}<br>
                    ${pointerDisplay}<br>
                    ${pointerTime ? `Pointer Updated: ${new Date(pointerTime).toLocaleTimeString()}<br>` : ''}
                    History: ${this.stateHistory.length}/${this.maxHistorySize}
                `;
            }

            // Update performance
            const performanceElement = document.getElementById('performance-metrics');
            if (performanceElement && currentState && currentState.performance) {
                const perf = currentState.performance;
                performanceElement.innerHTML = `
                    ${perf.memory ? `Memory: ${perf.memory.used}MB / ${perf.memory.total}MB<br>` : ''}
                    ${perf.timing ? `Load: ${perf.timing.loadComplete}ms<br>` : ''}
                    Snapshots: ${this.stateHistory.length}
                `;
            }

        } catch (error) {
            console.error('Dashboard update failed:', error);
        }
    }

