/**
 * Active Editor Tester
 * Simulates real user interactions to find and report issues
 */

class ActiveTester {
    constructor() {
        this.editor = null;
        this.testResults = [];
        this.errors = [];
        this.testId = 0;

        console.log('ğŸ¯ Active Tester initialized');
    }

    async runFullTestSuite() {
        console.log('ğŸš€ Starting full active test suite...');

        this.testResults = [];
        this.errors = [];

        try {
            // Get editor instance
            this.editor = window.tlEditor || window.editor;
            if (!this.editor) {
                throw new Error('Editor not found');
            }

            // Run all tests
            await this.testEditorInitialization();
            await this.testToolSwitching();
            await this.testShapeCreation();
            await this.testYouTubeEmbeds();
            await this.testCanvasInteractions();
            await this.testErrorRecovery();

        } catch (error) {
            this.logError('Test Suite Error', error);
        }

        this.reportResults();
        return this.getSummary();
    }

    async testEditorInitialization() {
        console.log('ğŸ” Testing editor initialization...');

        this.runTest('Editor exists', () => !!this.editor);
        this.runTest('RootState exists', () => !!this.editor.rootState);
        this.runTest('Store exists', () => !!this.editor.store);
        this.runTest('Canvas exists', () => !!this.editor.canvas);
        this.runTest('HTMLContainer exists', () => !!this.editor.htmlContainer);
        this.runTest('EmbedManager exists', () => !!this.editor.embedManager);

        // Test critical methods
        this.runTest('setTool method works', () => {
            const result = this.editor.setTool('select');
            return result !== false;
        });

        this.runTest('getCurrentTool method works', () => {
            const tool = this.editor.getCurrentTool();
            return tool !== undefined;
        });
    }

    async testToolSwitching() {
        console.log('ğŸ”§ Testing tool switching...');

        const tools = ['select', 'draw', 'rectangle', 'ellipse', 'text', 'hand'];

        for (const toolId of tools) {
            this.runTest(`Switch to ${toolId} tool`, () => {
                try {
                    this.editor.setTool(toolId);
                    const currentTool = this.editor.getCurrentTool();
                    return currentTool !== null;
                } catch (error) {
                    this.logError(`Tool switching error (${toolId})`, error);
                    return false;
                }
            });

            // Small delay between tool switches
            await this.sleep(100);
        }
    }

    async testShapeCreation() {
        console.log('ğŸ“ Testing shape creation...');

        try {
            // Test geo shape creation (rectangle)
            this.editor.setTool('rectangle');

            this.runTest('GeoTool setGeoType works', () => {
                try {
                    if (this.editor.rootState.geoTool && this.editor.rootState.geoTool.setGeoType) {
                        this.editor.rootState.geoTool.setGeoType('rectangle');
                        return true;
                    }
                    return false;
                } catch (error) {
                    this.logError('GeoTool setGeoType error', error);
                    return false;
                }
            });

            // Test shape creation via store
            this.runTest('Create rectangle shape', () => {
                try {
                    const shape = new TLShape({
                        id: `test-rect-${Date.now()}`,
                        typeName: 'geo',
                        x: 100,
                        y: 100,
                        props: {
                            geo: 'rectangle',
                            w: 100,
                            h: 50,
                            color: 'black',
                            fill: 'none'
                        }
                    });

                    this.editor.store.put(shape);
                    return true;
                } catch (error) {
                    this.logError('Shape creation error', error);
                    return false;
                }
            });

        } catch (error) {
            this.logError('Shape creation test error', error);
        }
    }

    async testYouTubeEmbeds() {
        console.log('ğŸ“º Testing YouTube embeds...');

        try {
            // Test embed definitions
            this.runTest('YouTube embed definition exists', () => {
                return window.DEFAULT_EMBED_DEFINITIONS && 
                       window.DEFAULT_EMBED_DEFINITIONS.some(def => def.type === 'youtube');
            });

            // Test embed creation
            this.runTest('Create YouTube embed', () => {
                try {
                    const testShape = {
                        id: `test-youtube-${Date.now()}`,
                        type: 'embed',
                        x: 200,
                        y: 200,
                        props: {
                            embedType: 'youtube',
                            url: 'https://www.youtube.com/watch?v=dQw4w9WgXcQ',
                            width: 640,
                            height: 360
                        }
                    };

                    const iframe = this.editor.embedManager.createEmbed(testShape);
                    const success = iframe && iframe.tagName === 'IFRAME';

                    // Clean up
                    setTimeout(() => {
                        if (iframe && iframe.parentNode) {
                            iframe.parentNode.removeChild(iframe);
                        }
                    }, 1000);

                    return success;
                } catch (error) {
                    this.logError('YouTube embed creation error', error);
                    return false;
                }
            });

            // Test embed URL conversion
            this.runTest('YouTube URL conversion works', () => {
                try {
                    const youtubeDef = window.DEFAULT_EMBED_DEFINITIONS.find(def => def.type === 'youtube');
                    if (!youtubeDef) return false;

                    const testUrl = 'https://www.youtube.com/watch?v=dQw4w9WgXcQ';
                    const embedUrl = youtubeDef.toEmbedUrl(testUrl);

                    return embedUrl && embedUrl.includes('youtube.com/embed/');
                } catch (error) {
                    this.logError('YouTube URL conversion error', error);
                    return false;
                }
            });

        } catch (error) {
            this.logError('YouTube embed test error', error);
        }
    }

    async testCanvasInteractions() {
        console.log('ğŸ¨ Testing canvas interactions...');

        try {
            // Test camera operations
            this.runTest('Get camera works', () => {
                const camera = this.editor.getCamera();
                return camera && typeof camera.x === 'number' && typeof camera.y === 'number' && typeof camera.z === 'number';
            });

            // Test camera panning
            this.runTest('Pan camera works', () => {
                try {
                    const originalCamera = this.editor.getCamera();
                    this.editor.panCamera({ x: 10, y: 10 });
                    const newCamera = this.editor.getCamera();

                    // Reset camera
                    this.editor.setCamera(originalCamera);

                    return newCamera.x !== originalCamera.x || newCamera.y !== originalCamera.y;
                } catch (error) {
                    this.logError('Camera pan error', error);
                    return false;
                }
            });

            // Test shape selection
            this.runTest('Shape selection system works', () => {
                try {
                    const shapes = this.editor.getShapes();
                    if (shapes.length > 0) {
                        this.editor.select(shapes[0].id);
                        const selectedShapes = this.editor.getSelectedShapes();
                        return selectedShapes.length > 0;
                    }
                    return true; // No shapes to select is OK
                } catch (error) {
                    this.logError('Shape selection error', error);
                    return false;
                }
            });

        } catch (error) {
            this.logError('Canvas interaction test error', error);
        }
    }

    async testErrorRecovery() {
        console.log('ğŸ”„ Testing error recovery...');

        // Test invalid tool switching
        this.runTest('Handle invalid tool gracefully', () => {
            try {
                const result = this.editor.setTool('nonexistent-tool');
                return result === false; // Should return false for invalid tools
            } catch (error) {
                // Should not throw, but if it does, that's a problem
                this.logError('Invalid tool handling error', error);
                return false;
            }
        });

        // Test null/undefined handling
        this.runTest('Handle null inputs gracefully', () => {
            try {
                // These should not crash the editor
                this.editor.setTool(null);
                this.editor.setTool(undefined);
                return true;
            } catch (error) {
                this.logError('Null input handling error', error);
                return false;
            }
        });
    }

    runTest(name, testFn) {
        this.testId++;
        const startTime = performance.now();

        try {
            const result = testFn();
            const duration = performance.now() - startTime;

            const testResult = {
                id: this.testId,
                name,
                passed: !!result,
                duration: Math.round(duration * 100) / 100,
                timestamp: new Date().toISOString()
            };

            this.testResults.push(testResult);

            if (testResult.passed) {
                console.log(`âœ… ${name} (${testResult.duration}ms)`);
            } else {
                console.error(`âŒ ${name} (${testResult.duration}ms)`);
            }

            return testResult.passed;

        } catch (error) {
            const duration = performance.now() - startTime;
            this.logError(`Test execution error: ${name}`, error);

            this.testResults.push({
                id: this.testId,
                name,
                passed: false,
                duration: Math.round(duration * 100) / 100,
                error: error.message,
                timestamp: new Date().toISOString()
            });

            return false;
        }
    }

    logError(context, error) {
        const errorInfo = {
            context,
            message: error.message || error,
            stack: error.stack,
            timestamp: new Date().toISOString()
        };

        this.errors.push(errorInfo);
        console.error(`ğŸš¨ ${context}:`, error);
    }

    reportResults() {
        const passed = this.testResults.filter(t => t.passed).length;
        const failed = this.testResults.filter(t => !t.passed).length;
        const total = this.testResults.length;

        console.log('\nğŸ“Š Active Test Results:');
        console.log(`Total: ${total}, Passed: ${passed}, Failed: ${failed}`);
        console.log(`Errors encountered: ${this.errors.length}`);

        if (failed > 0) {
            console.log('\nâŒ Failed Tests:');
            this.testResults.filter(t => !t.passed).forEach(test => {
                console.log(`  - ${test.name}${test.error ? ': ' + test.error : ''}`);
            });
        }

        if (this.errors.length > 0) {
            console.log('\nğŸš¨ Errors:');
            this.errors.forEach(error => {
                console.log(`  - ${error.context}: ${error.message}`);
            });
        }

        // Update UI
        this.updateUI(passed, failed, total);
    }

    updateUI(passed, failed, total) {
        const statusElement = document.getElementById('active-test-status');
        if (statusElement) {
            const status = failed === 0 ? 'âœ… All tests passed!' : `âŒ ${failed}/${total} tests failed`;
            statusElement.innerHTML = `
                <div style="color: ${failed === 0 ? '#10b981' : '#ef4444'};">
                    ${status}
                    ${this.errors.length > 0 ? `<br><small>${this.errors.length} errors detected</small>` : ''}
                </div>
            `;
        }
    }

    getSummary() {
        const passed = this.testResults.filter(t => t.passed).length;
        const failed = this.testResults.filter(t => !t.passed).length;

        return {
            total: this.testResults.length,
            passed,
            failed,
            errors: this.errors.length,
            success: failed === 0 && this.errors.length === 0,
            testResults: this.testResults,
            errors: this.errors
        };
    }

    sleep(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }
}

