            }

            .diagnostic-section {
                margin-bottom: 16px;
                padding: 12px;
                background: rgba(255, 0, 0, 0.03);
                border: 1px solid rgba(255, 0, 0, 0.1);
                border-radius: 12px;
                backdrop-filter: blur(5px);
                transition: all 0.2s ease;
            }

            .diagnostic-section:hover {
                background: rgba(255, 0, 0, 0.06);
