    compareStates(before, during, after, expected) {
        const comparison = {
            timestamp: Date.now(),
            testType: 'state_comparison',
            results: {
                shapes: this.compareShapeStates(before.shapes, after.shapes, expected.shapes),
                errors: this.compareErrorStates(before.errors, after.errors, expected.errors),
                performance: this.comparePerformanceStates(before.performance, after.performance),
                input: this.compareInputStates(before.input, after.input, expected.input)
            },
            success: false,
            failures: []
        };

        // Determine overall success
        comparison.success = Object.values(comparison.results).every(result => result.passed);

        if (!comparison.success) {
            comparison.failures = Object.entries(comparison.results)
                .filter(([key, result]) => !result.passed)
                .map(([key, result]) => ({
                    category: key,
                    expected: result.expected,
                    actual: result.actual,
                    difference: result.difference
                }));
        }

        // Log comparison results
        console.log('ğŸ“Š State Comparison Results:', comparison);

        // Add to diagnostic history
        this.stateComparisons = this.stateComparisons || [];
        this.stateComparisons.push(comparison);

        return comparison;
    }

    // User Mock Embed Insertion Function
    // Fixed dependency: insertEmbed() expects input field, this takes URL directly
    insertEmbedForTesting(url) {
        console.log('ğŸ”— User Mock: Inserting embed:', url);

        // CRITICAL: Check if editor is initialized
        if (!window.tlEditor) {
            console.error('âŒ insertEmbedForTesting failed: window.tlEditor is undefined');
            return false;
        }

        // Use TLDraw-style embed detection
        if (!window.detectEmbedType) {
            console.error('âŒ detectEmbedType function not found');
            return false;
        }

        const embedMatch = window.detectEmbedType(url);
        if (!embedMatch || !embedMatch.handler) {
            console.error('âŒ Embed detection failed for URL:', url);
            return false;
        }

        console.log('âœ… Embed detected:', embedMatch.type, embedMatch.embedUrl);

        const embedData = embedMatch.handler.createEmbed(embedMatch.embedUrl || url, url);
        console.log('âœ… Embed data created:', embedData);

        // Get camera position for embed placement
        const camera = window.tlEditor.getCamera();
        if (!camera) {
            console.error('âŒ Camera not available');
            return false;
        }

        // Create embed shape
        const embedShape = new window.TLShape(window.TLShapeType.EMBED, null, {
            x: -camera.x + 100,
            y: -camera.y + 100,
            props: embedData
        });

        console.log('âœ… Embed shape created:', embedShape.id);

        // Add to store
        window.tlEditor.store.put(embedShape);

        // Add to current page
        const page = window.tlEditor.store.getCurrentPage();
        if (page) {
            page.addShape(embedShape.id);
            window.tlEditor.store.put(page);
            console.log('âœ… Added embed to page:', page.id);
        }

        // Trigger render
        window.tlEditor.render();

        console.log('âœ… Embed added to canvas successfully');
        console.log('âœ… Created', embedMatch.type, 'embed:', embedShape.id);

        return true;
    }

