/**
 * Smart Error Tracker & Auto-Resolver
 * Continuously monitors for errors, prioritizes them, and attempts automatic fixes
 */

class ErrorTracker {
    constructor() {
        this.activeErrors = new Map(); // ID -> error object
        this.resolvedErrors = new Map(); // ID -> resolved error object
        this.errorQueue = []; // Priority queue
        this.fixAttempts = new Map(); // ID -> attempt count
        this.isProcessing = false;
        this.errorIdCounter = 1;
        this.maxRetries = 3;
        this.processingInterval = null;
        this.mdUpdateInterval = null;

        console.log('ğŸ” Error Tracker initialized');
        this.startMonitoring();
    }

    startMonitoring() {
        // Monitor for new errors every 2 seconds
        this.processingInterval = setInterval(() => {
            this.scanForErrors();
            this.processErrorQueue();
        }, 2000);

        // Update MD file every 10 seconds  
        this.mdUpdateInterval = setInterval(() => {
            this.updateMDFile();
        }, 10000);

        // Initial scan
        setTimeout(() => this.scanForErrors(), 1000);
    }

    stopMonitoring() {
        if (this.processingInterval) {
            clearInterval(this.processingInterval);
            this.processingInterval = null;
        }
        if (this.mdUpdateInterval) {
            clearInterval(this.mdUpdateInterval);
            this.mdUpdateInterval = null;
        }
    }

    async scanForErrors() {
        console.log('ğŸ” Scanning for errors...');

        try {
            const newErrors = [];

            // Collect errors from all monitoring systems
            await this.collectBasicTestErrors(newErrors);
            await this.collectActiveTestErrors(newErrors);
            await this.collectDiagnosticErrors(newErrors);
            await this.collectRuntimeErrors(newErrors);

            // Process new errors
            for (const error of newErrors) {
                this.addError(error);
            }

            // Check if previously active errors are now resolved
            this.checkResolvedErrors();

        } catch (error) {
            console.error('Error scanning for errors:', error);
        }
    }

    async collectBasicTestErrors(errors) {
        if (window.testResults && window.testResults.tests) {
            const failedTests = window.testResults.tests.filter(t => !t.success);

            for (const test of failedTests) {
                errors.push({
                    type: 'basic_test_failure',
                    context: test.name,
                    message: test.error || 'Test failed',
                    source: 'basic_tests',
                    priority: this.calculatePriority('basic_test_failure', test.name),
                    data: test
                });
            }
        }
    }

    async collectActiveTestErrors(errors) {
        if (window.activeTester && window.activeTester.testResults) {
            const failedTests = window.activeTester.testResults.filter(t => !t.passed);

            for (const test of failedTests) {
                errors.push({
                    type: 'active_test_failure', 
                    context: test.name,
                    message: test.error || 'Active test failed',
                    source: 'active_tests',
                    priority: this.calculatePriority('active_test_failure', test.name),
                    data: test
                });
            }

            // Also collect general errors from active tester
            if (window.activeTester.errors) {
                for (const error of window.activeTester.errors) {
                    errors.push({
                        type: 'runtime_error',
                        context: error.context,
                        message: error.message,
                        source: 'active_tester',
                        priority: this.calculatePriority('runtime_error', error.context),
                        data: error
                    });
                }
            }
        }
    }

    async collectDiagnosticErrors(errors) {
        if (window.continuousDiagnostics) {
            const status = window.continuousDiagnostics.getStatus();

            // Add recent errors
            if (status.lastErrors && status.lastErrors.length > 0) {
                for (const error of status.lastErrors) {
                    errors.push({
                        type: 'diagnostic_error',
                        context: error.type,
                        message: error.details?.message || 'Diagnostic error',
                        source: 'diagnostics',
                        priority: this.calculatePriority('diagnostic_error', error.type),
                        data: error
                    });
                }
            }

            // Add health issues
            if (status.healthIssues && status.healthIssues.length > 0) {
                for (const issue of status.healthIssues) {
                    errors.push({
                        type: 'health_issue',
                        context: 'system_health',
                        message: issue,
                        source: 'health_check',
                        priority: this.calculatePriority('health_issue', issue),
                        data: { issue }
                    });
                }
            }
        }
    }

    async collectRuntimeErrors(errors) {
        // This will be populated by global error handlers
        // For now, check if editor is properly initialized
        const editor = window.tlEditor || window.editor;

        if (!editor) {
            errors.push({
                type: 'initialization_error',
                context: 'editor_missing',
                message: 'Editor instance not found',
                source: 'initialization',
                priority: 0, // CRITICAL
                data: { component: 'editor' }
            });
        } else {
            // Check critical components
            const criticalComponents = [
                { name: 'rootState', obj: editor.rootState },
                { name: 'store', obj: editor.store },
                { name: 'canvas', obj: editor.canvas },
                { name: 'htmlContainer', obj: editor.htmlContainer },
                { name: 'embedManager', obj: editor.embedManager }
            ];

            for (const comp of criticalComponents) {
                if (!comp.obj) {
                    errors.push({
                        type: 'component_missing',
                        context: `${comp.name}_missing`,
                        message: `${comp.name} component not initialized`,
                        source: 'component_check',
                        priority: comp.name === 'rootState' || comp.name === 'store' ? 0 : 1,
                        data: { component: comp.name }
                    });
                }
            }
        }
    }

    calculatePriority(type, context) {
        // Priority: 0 = CRITICAL, 1 = HIGH, 2 = MEDIUM, 3 = LOW

        // CRITICAL (P0) - Editor won't work at all
        if (type === 'initialization_error') return 0;
        if (context && context.includes('rootState')) return 0;
        if (context && context.includes('store')) return 0;

        // HIGH (P1) - Core functionality broken
        if (type === 'component_missing') return 1;
        if (context && context.includes('setTool')) return 1;
        if (context && context.includes('geoTool')) return 1;
        if (context && context.includes('HTMLContainer')) return 1;
        if (context && context.includes('YouTube')) return 1;

        // MEDIUM (P2) - Non-critical issues
        if (type === 'health_issue') return 2;
        if (type === 'diagnostic_error') return 2;

        // LOW (P3) - Minor issues
        return 3;
    }

    addError(errorData) {
        const signature = this.getErrorSignature(errorData);

        // Check if this error already exists
        let existingError = null;
        for (const [id, error] of this.activeErrors) {
            if (error.signature === signature) {
                existingError = { id, error };
                break;
            }
        }

        if (existingError) {
            // Update existing error
            existingError.error.count++;
            existingError.error.lastSeen = new Date().toISOString();
            console.log(`ğŸ”„ Updated existing error ${existingError.id} (count: ${existingError.error.count})`);
        } else {
            // Add new error
            const errorId = `ERR-${String(this.errorIdCounter).padStart(3, '0')}`;
            this.errorIdCounter++;

            const newError = {
                id: errorId,
                ...errorData,
                signature,
                count: 1,
                firstSeen: new Date().toISOString(),
                lastSeen: new Date().toISOString(),
                status: 'active'
            };

            this.activeErrors.set(errorId, newError);
            this.addToQueue(newError);

            console.log(`ğŸš¨ New error detected: ${errorId} - ${errorData.context}`);
        }
    }

    getErrorSignature(errorData) {
        // Create a unique signature for the error to detect duplicates
        return `${errorData.type}:${errorData.context}:${errorData.message}`;
    }

    addToQueue(error) {
        // Add to priority queue, sorted by priority (0 = highest)
        const insertIndex = this.errorQueue.findIndex(e => e.priority > error.priority);
        if (insertIndex === -1) {
            this.errorQueue.push(error);
        } else {
            this.errorQueue.splice(insertIndex, 0, error);
        }
    }

    async processErrorQueue() {
        if (this.isProcessing || this.errorQueue.length === 0) {
            return;
        }

        this.isProcessing = true;
        console.log(`ğŸ”§ Processing error queue (${this.errorQueue.length} errors)`);

        try {
            const error = this.errorQueue.shift();
            const attempts = this.fixAttempts.get(error.id) || 0;

            if (attempts >= this.maxRetries) {
                console.log(`âŒ Max retries reached for ${error.id}, marking as failed`);
                error.status = 'failed';
                this.removeFromActiveQueue(error.id);
            } else {
                console.log(`ğŸ› ï¸ Attempting fix for ${error.id} (attempt ${attempts + 1}/${this.maxRetries})`);

                const success = await this.attemptFix(error);
                this.fixAttempts.set(error.id, attempts + 1);

                if (success) {
                    console.log(`âœ… Fix successful for ${error.id}`);
                    await this.verifyFix(error);
                } else {
                    console.log(`âŒ Fix failed for ${error.id}, will retry`);
                    // Re-add to queue for retry
                    this.addToQueue(error);
                }
            }

        } catch (error) {
            console.error('Error processing queue:', error);
        }

        this.isProcessing = false;
    }

    async attemptFix(error) {
        console.log(`ğŸ”§ Attempting to fix: ${error.type} - ${error.context}`);

        try {
            switch (error.type) {
                case 'basic_test_failure':
                case 'active_test_failure':
                    return await this.fixTestFailure(error);

                case 'component_missing':
                    return await this.fixMissingComponent(error);

                case 'initialization_error':
                    return await this.fixInitializationError(error);

                case 'health_issue':
                    return await this.fixHealthIssue(error);

                case 'runtime_error':
                    return await this.fixRuntimeError(error);

                default:
                    console.log(`âš ï¸ No fix strategy for error type: ${error.type}`);
                    return false;
            }
        } catch (fixError) {
            console.error(`Fix attempt failed for ${error.id}:`, fixError);
            return false;
        }
    }

    async fixTestFailure(error) {
        const context = error.context.toLowerCase();

        // setTool related issues
        if (context.includes('settool')) {
            return await this.ensureSetToolMethod();
        }

        // geoTool related issues  
        if (context.includes('geotool')) {
            return await this.ensureGeoToolMethod();
        }

        // HTMLContainer issues
        if (context.includes('htmlcontainer')) {
            return await this.ensureHTMLContainer();
        }

        // YouTube embed issues
        if (context.includes('youtube')) {
            return await this.ensureYouTubeEmbed();
        }

        return false;
    }

    async fixMissingComponent(error) {
        const component = error.data?.component;

        switch (component) {
            case 'rootState':
                return await this.reinitializeRootState();
            case 'store':
                return await this.reinitializeStore();
            case 'htmlContainer':
                return await this.reinitializeHTMLContainer();
            case 'embedManager':
                return await this.reinitializeEmbedManager();
            default:
                return false;
        }
    }

    async ensureSetToolMethod() {
        const editor = window.tlEditor || window.editor;
        if (!editor || !editor.rootState) return false;

        if (typeof editor.rootState.setTool !== 'function') {
            // The fix should already be in place, but verify
            console.log('ğŸ”§ setTool method missing - this should have been fixed');
            return false;
        }

        return true;
    }

    async ensureGeoToolMethod() {
        const editor = window.tlEditor || window.editor;
        if (!editor || !editor.rootState) return false;

        if (!editor.rootState.geoTool) {
            console.log('ğŸ”§ geoTool missing - this should have been fixed');
            return false;
        }

        return true;
    }

    async ensureHTMLContainer() {
        return !!window.HTMLContainer && !!(window.tlEditor || window.editor)?.htmlContainer;
    }

    async ensureYouTubeEmbed() {
        const hasDefinitions = !!window.DEFAULT_EMBED_DEFINITIONS;
        const hasYoutube = hasDefinitions && window.DEFAULT_EMBED_DEFINITIONS.some(def => def.type === 'youtube');
        const hasManager = !!(window.tlEditor || window.editor)?.embedManager;

        return hasDefinitions && hasYoutube && hasManager;
    }

    async reinitializeRootState() {
        const editor = window.tlEditor || window.editor;
        if (!editor || !window.RootState) return false;

        try {
            editor.rootState = new window.RootState(editor);
            editor.rootState.enter();
            console.log('ğŸ”„ RootState reinitialized');
            return true;
        } catch (error) {
            console.error('Failed to reinitialize RootState:', error);
            return false;
        }
    }

    async reinitializeStore() {
        // Store reinitialization is complex and risky
        console.log('âš ï¸ Store reinitialization not implemented - requires manual intervention');
        return false;
    }

    async reinitializeHTMLContainer() {
        const editor = window.tlEditor || window.editor;
        if (!editor || !window.HTMLContainer) return false;

        try {
            editor.htmlContainer = new window.HTMLContainer(editor.container, editor.getCamera(), editor.store);
            console.log('ğŸ”„ HTMLContainer reinitialized');
            return true;
        } catch (error) {
            console.error('Failed to reinitialize HTMLContainer:', error);
            return false;
        }
    }

    async reinitializeEmbedManager() {
        const editor = window.tlEditor || window.editor;
        if (!editor || !window.EmbedManager || !editor.htmlContainer) return false;

        try {
            editor.embedManager = new window.EmbedManager(editor.htmlContainer, editor.store);
            console.log('ğŸ”„ EmbedManager reinitialized');
            return true;
        } catch (error) {
            console.error('Failed to reinitialize EmbedManager:', error);
            return false;
        }
    }

    async fixInitializationError(error) {
        if (error.context === 'editor_missing') {
            // Try to reinitialize the editor
            const container = document.getElementById('canvas-container');
            if (container && window.TLDrawEditor) {
                try {
                    window.tlEditor = new window.TLDrawEditor(container);
                    console.log('ğŸ”„ Editor reinitialized');
                    return true;
                } catch (error) {
                    console.error('Failed to reinitialize editor:', error);
                    return false;
                }
            }
        }

        return false;
    }

    async fixHealthIssue(error) {
        // Most health issues are fixed by component reinitialization
        return await this.fixMissingComponent(error);
    }

    async fixRuntimeError(error) {
        // Runtime errors usually need specific handling
        console.log(`âš ï¸ Runtime error fix not implemented: ${error.context}`);
        return false;
    }

    async verifyFix(error) {
        console.log(`ğŸ” Verifying fix for ${error.id}...`);

        // Wait a moment for fix to take effect
        await this.sleep(1000);

        // Re-run relevant tests to verify the fix
        let isFixed = false;

        try {
            switch (error.type) {
                case 'basic_test_failure':
                    if (window.runAutomatedEditorTests) {
                        const results = window.runAutomatedEditorTests();
                        const stillFailing = results.tests?.some(t => 
                            !t.success && t.name === error.context
                        );
                        isFixed = !stillFailing;
                    }
                    break;

                case 'active_test_failure':
                    if (window.activeTester) {
                        const results = await window.activeTester.runFullTestSuite();
                        const stillFailing = results.testResults?.some(t => 
                            !t.passed && t.name === error.context
                        );
                        isFixed = !stillFailing;
                    }
                    break;

                case 'component_missing':
                    const editor = window.tlEditor || window.editor;
                    const component = error.data?.component;
                    isFixed = !!(editor && editor[component]);
                    break;

                case 'initialization_error':
                    isFixed = !!(window.tlEditor || window.editor);
                    break;

                default:
                    // For other types, assume fixed if no longer detected
                    isFixed = true;
            }

        } catch (verifyError) {
            console.error('Verification error:', verifyError);
            isFixed = false;
        }

        if (isFixed) {
            this.markAsResolved(error);
        } else {
            console.log(`âŒ Fix verification failed for ${error.id}`);
        }

        return isFixed;
    }

    markAsResolved(error) {
        error.status = 'resolved';
        error.resolvedAt = new Date().toISOString();

        this.resolvedErrors.set(error.id, error);
        this.activeErrors.delete(error.id);
        this.fixAttempts.delete(error.id);

        console.log(`âœ… Error ${error.id} marked as resolved`);
    }

    checkResolvedErrors() {
        // Check if any active errors are now resolved naturally
        for (const [id, error] of this.activeErrors) {
            // Simple check: if error hasn't been seen recently, it might be resolved
            const lastSeen = new Date(error.lastSeen);
            const now = new Date();
            const timeDiff = now - lastSeen;

            if (timeDiff > 60000) { // 1 minute without seeing the error
                console.log(`ğŸ” Checking if ${id} is naturally resolved...`);
                this.verifyFix(error);
            }
        }
    }

    removeFromActiveQueue(errorId) {
        this.errorQueue = this.errorQueue.filter(e => e.id !== errorId);
    }

    async updateMDFile() {
        console.log('ğŸ“ Updating error tracking MD file...');

        try {
            const mdContent = this.generateMDContent();

            // In a real implementation, you would write to the file system
            // For now, we'll update a global variable and log the content
            window.errorTrackingMD = mdContent;

            console.log('ğŸ“ MD file updated with current error status');

        } catch (error) {
            console.error('Failed to update MD file:', error);
        }
    }

    generateMDContent() {
        const now = new Date().toISOString();
        const activeCount = this.activeErrors.size;
        const resolvedCount = this.resolvedErrors.size;

        let content = `# TLDraw Canvas Editor - Error Tracking & Resolution Log

**Last updated:** ${now}  
**Status:** ${activeCount > 0 ? 'ğŸ”„ Processing Errors' : 'ğŸŸ¢ All Clear'}  
**Auto-fix enabled:** âœ… Yes

---

