startPolling() {
    let lastCamera = null;
    let lastShapeCount = 0;

    this.pollingInterval = setInterval(() => {
        try {
            // Check for camera changes
            if (this.editor && this.editor.getCamera) {
                const currentCamera = this.editor.getCamera();
                if (JSON.stringify(currentCamera) !== JSON.stringify(lastCamera)) {
                    lastCamera = currentCamera;
                    this.updateAllEmbedPositions();
                }
