            // Check for shape changes
            if (this.editor && this.editor.store) {
                const shapes = this.editor.store.getCurrentPageShapes ? 
                    this.editor.store.getCurrentPageShapes() : [];
                if (shapes.length !== lastShapeCount) {
                    lastShapeCount = shapes.length;
                    this.handleShapeChanges({ added: shapes.slice(-1) });
                }
            }
        } catch (e) {
            console.warn('Polling error:', e);
        }
    }, 100); // Poll every 100ms
}

