/**
 * TLDraw-Compliant Modular Embed Architecture
 * Built to match TLDraw's official embed system with iframe/HTML/JSON structure
 */

/**
 * Core TLDraw Embed Architecture
 * Following official TLDraw patterns for maximum compatibility
 */
class TLDrawEmbedSystem {
    constructor(editor) {
        this.editor = editor;
        this.embedRegistry = new Map();
        this.htmlContainer = null;
        this.assetManager = new TLDrawAssetManager();
        this.embedShapeUtil = new TLDrawEmbedShapeUtil();
        this.initialize();
    }

    initialize() {
        this.setupHTMLContainer();
        this.registerEmbedTypes();
        this.setupEventListeners();
        console.log('ğŸ¯ TLDraw Embed System initialized with modular architecture');
    }

    /**
     * HTML Container Management (TLDraw Pattern)
     * Creates overlay container for iframe and HTML embeds
     */
    setupHTMLContainer() {
        this.htmlContainer = document.createElement('div');
        this.htmlContainer.className = 'tl-html-container';
        this.htmlContainer.style.cssText = `
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 1000;
        `;

        // Mount to editor canvas parent
        const canvasParent = this.editor.canvas.parentElement;
        canvasParent.appendChild(this.htmlContainer);

        console.log('âœ… HTML Container created following TLDraw pattern');
    }

    /**
     * Setup Event Listeners for Embed System
     * Handles camera changes and cleanup
     */
    setupEventListeners() {
        // Listen for camera changes to update embed positions
        if (this.editor) {
            // Check if editor has event emitter methods
            if (this.editor.on && typeof this.editor.on === 'function') {
                this.editor.on('camera-change', () => {
                    this.updateAllEmbedPositions();
                });

                // Listen for shape changes to handle embed updates
                this.editor.on('shape-change', (changes) => {
                    this.handleShapeChanges(changes);
                });
            } else if (this.editor.addEventListener && typeof this.editor.addEventListener === 'function') {
                // Alternative event listener approach
                this.editor.addEventListener('camera-change', () => {
                    this.updateAllEmbedPositions();
                });

                this.editor.addEventListener('shape-change', (changes) => {
                    this.handleShapeChanges(changes);
                });
            } else {
                // Fallback: Poll for changes if no event system available
                console.log('âš ï¸ Editor does not support events, using polling fallback');
                this.startPolling();
            }
        }

        // Cleanup on page unload
        window.addEventListener('beforeunload', () => {
            this.cleanup();
        });

        console.log('âœ… Embed system event listeners initialized');
    }

    /**
     * Update positions of all embed elements
     */
    updateAllEmbedPositions() {
        if (!this.htmlContainer) return;

        const embedElements = this.htmlContainer.querySelectorAll('[data-shape-id]');
        embedElements.forEach(element => {
            const shapeId = element.getAttribute('data-shape-id');
            this.updateEmbedPosition(shapeId);
        });
    }

    /**
     * Handle shape changes for embed updates
     */
    handleShapeChanges(changes) {
        changes.forEach(change => {
            if (change.type === 'embed' || change.type === 'youtube') {
                const embedElement = this.htmlContainer.querySelector(`[data-shape-id="${change.id}"]`);
                if (embedElement) {
                    this.updateEmbedElement(change, embedElement);
                }
            }
        });
    }

    /**
     * Update individual embed element
     */
    updateEmbedElement(shape, element) {
        const camera = this.editor.getCamera();
        const screenPos = camera.pageToScreen({
            x: shape.x,
            y: shape.y
        });

        element.style.left = `${screenPos.x}px`;
        element.style.top = `${screenPos.y}px`;
        element.style.width = `${shape.props.w}px`;
        element.style.height = `${shape.props.h}px`;
        element.style.transform = `scale(${camera.z})`;
    }

    /**
     * Cleanup resources
     */
    cleanup() {
        if (this.htmlContainer) {
            this.htmlContainer.remove();
        }
    }

    /**
     * Register Embed Types (Modular Pattern)
     * Each embed type is a self-contained module
     */
    registerEmbedTypes() {
        // YouTube - Uses iframe (TLDraw standard)
        this.registerEmbedType('youtube', new YouTubeEmbedModule());

        // Twitter - Uses HTML + JSON
        this.registerEmbedType('twitter', new TwitterEmbedModule());

        // Vimeo - Uses iframe
        this.registerEmbedType('vimeo', new VimeoEmbedModule());

        // Figma - Uses iframe with special permissions
        this.registerEmbedType('figma', new FigmaEmbedModule());

        // CodePen - Uses iframe
        this.registerEmbedType('codepen', new CodePenEmbedModule());

        // Generic HTML - Uses HTML container
        this.registerEmbedType('html', new HTMLEmbedModule());

        console.log('âœ… Available embed modules registered:', Array.from(this.embedRegistry.keys()));
    }

    /**
     * Register Individual Embed Type
     */
    registerEmbedType(type, module) {
        this.embedRegistry.set(type, module);
        module.initialize(this);
    }

    /**
     * Create Embed (TLDraw Pattern)
     * Follows TLDraw's embed shape creation pattern
     */
    createEmbed(url, embedType, position = { x: 0, y: 0 }) {
        const module = this.embedRegistry.get(embedType);
        if (!module) {
            throw new Error(`Embed type "${embedType}" not registered`);
        }

        // Parse URL and get embed data
        const embedData = module.parseURL(url);
        if (!embedData) {
            throw new Error(`Failed to parse ${embedType} URL: ${url}`);
        }

        // Create TLDraw embed shape
        const embedShape = this.createEmbedShape(embedType, embedData, position);

        // Create embed element (iframe or HTML)
        const embedElement = module.createElement(embedData);

        // Mount to HTML container
        this.mountEmbed(embedShape, embedElement);

        // Store in assets if needed
        if (embedData.requiresAssetStorage) {
            this.assetManager.storeEmbedAsset(embedShape.id, embedData);
        }

        console.log(`âœ… Created ${embedType} embed:`, embedShape.id);
        return embedShape;
    }

    /**
     * Create TLDraw Embed Shape
     * Uses official TLDraw shape structure
     */
    createEmbedShape(embedType, embedData, position) {
        const embedShape = {
            id: this.generateShapeId(),
            type: 'embed',
            x: position.x,
            y: position.y,
            props: {
                w: embedData.width,
                h: embedData.height,
                url: embedData.embedUrl,
                doesResize: embedData.doesResize || false,
                overridePermissions: embedData.permissions || {},
                embedType: embedType,
                metadata: embedData.metadata || {}
            },
            meta: {
                originalUrl: embedData.originalUrl,
                title: embedData.title,
                description: embedData.description,
                thumbnail: embedData.thumbnail
            }
        };

        // Add to TLDraw store
        this.editor.createShapes([embedShape]);
        return embedShape;
    }

    /**
     * Mount Embed Element
     * Positions and styles embed element within HTML container
     */
    mountEmbed(embedShape, embedElement) {
        // Apply TLDraw positioning
        const camera = this.editor.getCamera();
        const screenPos = camera.pageToScreen({
            x: embedShape.x,
            y: embedShape.y
        });

        embedElement.style.cssText = `
            position: absolute;
            left: ${screenPos.x}px;
            top: ${screenPos.y}px;
            width: ${embedShape.props.w}px;
            height: ${embedShape.props.h}px;
            transform: scale(${camera.z});
            transform-origin: top left;
            pointer-events: auto;
            border: none;
            border-radius: 4px;
            box-shadow: 0 2px 8px rgba(0,0,0,0.1);
        `;

        embedElement.setAttribute('data-shape-id', embedShape.id);
        this.htmlContainer.appendChild(embedElement);

        // Listen for camera changes to update position
        this.editor.on('camera-change', () => {
            this.updateEmbedPosition(embedShape.id);
        });
    }

    /**
     * Update Embed Position (Camera sync)
     */
    updateEmbedPosition(shapeId) {
        const embedElement = this.htmlContainer.querySelector(`[data-shape-id="${shapeId}"]`);
        const shape = this.editor.getShape(shapeId);

        if (embedElement && shape) {
            const camera = this.editor.getCamera();
            const screenPos = camera.pageToScreen({
                x: shape.x,
                y: shape.y
            });

            embedElement.style.left = `${screenPos.x}px`;
            embedElement.style.top = `${screenPos.y}px`;
            embedElement.style.transform = `scale(${camera.z})`;
        }
    }

    generateShapeId() {
        return `embed_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }
}

/**
 * YouTube Embed Module (Iframe Pattern)
 * Handles YouTube-specific iframe creation and URL parsing
 */
class YouTubeEmbedModule {
    initialize(embedSystem) {
        this.embedSystem = embedSystem;
        this.name = 'YouTube';
        this.pattern = /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([a-zA-Z0-9_-]+)/;
    }

    parseURL(url) {
        const match = url.match(this.pattern);
        if (!match) return null;

        const videoId = match[1];
        return {
            videoId: videoId,
            embedUrl: `https://www.youtube-nocookie.com/embed/${videoId}?rel=0&modestbranding=1`,
            originalUrl: url,
            width: 560,
            height: 315,
            doesResize: true,
            title: `YouTube Video: ${videoId}`,
            permissions: {
                allowFullscreen: true,
                allowAutoplay: false
            },
            metadata: {
                platform: 'youtube',
                videoId: videoId
            }
        };
    }

    createElement(embedData) {
        const iframe = document.createElement('iframe');
        iframe.src = embedData.embedUrl;
        iframe.frameBorder = '0';
        iframe.allow = 'accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture';
        iframe.allowFullscreen = true;
        iframe.className = 'tl-embed tl-embed-youtube';

        // Add load event listener for timeout handling
        iframe.addEventListener('load', () => {
            console.log('âœ… YouTube iframe loaded successfully');
        });

        iframe.addEventListener('error', (e) => {
            console.error('âŒ YouTube iframe load error:', e);
        });

        return iframe;
    }
}

/**
 * Twitter Embed Module (HTML + JSON Pattern)
 * Uses Twitter's widget system with JSON configuration
 */
class TwitterEmbedModule {
    initialize(embedSystem) {
        this.embedSystem = embedSystem;
        this.name = 'Twitter';
        this.pattern = /twitter\.com\/\w+\/status\/(\d+)/;
        this.scriptLoaded = false;
        this.loadTwitterScript();
    }

    loadTwitterScript() {
        if (this.scriptLoaded) return;

        const script = document.createElement('script');
        script.src = 'https://platform.twitter.com/widgets.js';
        script.async = true;
        document.head.appendChild(script);
        this.scriptLoaded = true;
    }

    parseURL(url) {
        const match = url.match(this.pattern);
        if (!match) return null;

        const tweetId = match[1];
        return {
            tweetId: tweetId,
            embedUrl: url,
            originalUrl: url,
            width: 550,
            height: 400,
            doesResize: true,
            title: `Twitter Tweet: ${tweetId}`,
            requiresAssetStorage: true,
            metadata: {
                platform: 'twitter',
                tweetId: tweetId,
                embedType: 'html'
            }
        };
    }

    createElement(embedData) {
        const container = document.createElement('div');
        container.className = 'tl-embed tl-embed-twitter';

        // Create blockquote for Twitter embed
        const blockquote = document.createElement('blockquote');
        blockquote.className = 'twitter-tweet';
        blockquote.innerHTML = `<a href="${embedData.originalUrl}">Loading tweet...</a>`;

        container.appendChild(blockquote);

        // Process with Twitter widgets
        if (window.twttr && window.twttr.widgets) {
            window.twttr.widgets.load(container);
        }

        return container;
    }
}

