/**
 * HTML Embed Module (HTML Pattern)
 * Handles generic HTML content embedding
 */
class HTMLEmbedModule {
    initialize(embedSystem) {
        this.embedSystem = embedSystem;
        this.name = 'HTML';
        this.pattern = /.*/; // Matches any URL as fallback
    }

