        iframe.sandbox = 'allow-scripts allow-same-origin';
        iframe.className = 'tl-embed tl-embed-html';

        iframe.addEventListener('load', () => {
            console.log('âœ… HTML iframe loaded successfully');
        });

        iframe.addEventListener('error', (e) => {
