    createElement(embedData) {
        const iframe = document.createElement('iframe');
        iframe.src = embedData.embedUrl;
        iframe.frameBorder = '0';
        iframe.sandbox = 'allow-scripts allow-same-origin';
        iframe.className = 'tl-embed tl-embed-html';

        iframe.addEventListener('load', () => {
            console.log('âœ… HTML iframe loaded successfully');
        });

        iframe.addEventListener('error', (e) => {
            console.error('âŒ HTML iframe load error:', e);
        });

        return iframe;
    }
}

/**
 * Asset Manager (JSON Pattern)
 * Manages embed metadata and assets in JSON format
 */
class TLDrawAssetManager {
    constructor() {
        this.assets = new Map();
        this.assetStore = 'tldraw_embed_assets';
        this.loadAssets();
    }

    storeEmbedAsset(shapeId, embedData) {
        const asset = {
            id: shapeId,
            type: 'embed',
            platform: embedData.metadata.platform,
            url: embedData.originalUrl,
            embedUrl: embedData.embedUrl,
            metadata: embedData.metadata,
            timestamp: Date.now()
        };

        this.assets.set(shapeId, asset);
        this.saveAssets();
        console.log('ğŸ’¾ Stored embed asset:', shapeId);
    }

    getEmbedAsset(shapeId) {
        return this.assets.get(shapeId);
    }

    saveAssets() {
        const assetsJSON = JSON.stringify(Array.from(this.assets.entries()));
        localStorage.setItem(this.assetStore, assetsJSON);
    }

    loadAssets() {
        try {
            const assetsJSON = localStorage.getItem(this.assetStore);
            if (assetsJSON) {
                const assetEntries = JSON.parse(assetsJSON);
                this.assets = new Map(assetEntries);
                console.log('ğŸ“‚ Loaded embed assets:', this.assets.size);
            }
        } catch (e) {
            console.warn('âš ï¸ Failed to load embed assets:', e);
        }
    }

    exportAssets() {
        return {
            embeds: Array.from(this.assets.values()),
            exportDate: new Date().toISOString(),
            version: '1.0'
        };
    }
}

/**
 * Embed Shape Util (TLDraw Pattern)
 * Custom shape utility for embed shapes
 */
class TLDrawEmbedShapeUtil {
    constructor() {
        this.type = 'embed';
        this.defaultProps = {
            w: 400,
            h: 300,
            url: '',
            doesResize: false,
            overridePermissions: {}
        };
    }

    getDefaultProps() {
        return this.defaultProps;
    }

    getBounds(shape) {
        return {
            x: shape.x,
            y: shape.y,
            w: shape.props.w,
            h: shape.props.h
        };
    }

    render(shape) {
        // TLDraw renders embed shapes as placeholder rectangles
        // Actual content is rendered in HTML container
        return {
            type: 'rect',
            x: shape.x,
            y: shape.y,
            width: shape.props.w,
            height: shape.props.h,
            fill: 'rgba(0,0,0,0.1)',
            stroke: '#666',
            strokeWidth: 1,
            rx: 4
        };
    }
}

// Export for global use
if (typeof window !== 'undefined') {
    window.TLDrawEmbedSystem = TLDrawEmbedSystem;
    window.YouTubeEmbedModule = YouTubeEmbedModule;
    window.TwitterEmbedModule = TwitterEmbedModule;
    window.VimeoEmbedModule = VimeoEmbedModule;
    window.FigmaEmbedModule = FigmaEmbedModule;
    window.CodePenEmbedModule = CodePenEmbedModule;
    window.HTMLEmbedModule = HTMLEmbedModule;
    window.TLDrawAssetManager = TLDrawAssetManager;

    console.log('ğŸš€ TLDraw Embed Architecture loaded');
    console.log('   â€¢ Iframe support for YouTube, Vimeo, Figma, CodePen');
    console.log('   â€¢ HTML support for Twitter, custom embeds');
    console.log('   â€¢ JSON asset management for metadata');
    console.log('   â€¢ Modular, extensible architecture');
}

// Export for Node.js
if (typeof module !== 'undefined' && module.exports) {
    module.exports = {
        TLDrawEmbedSystem,
        YouTubeEmbedModule,
        TwitterEmbedModule,
        VimeoEmbedModule,
        FigmaEmbedModule,
        CodePenEmbedModule,
        HTMLEmbedModule,
        TLDrawAssetManager
    };
}

