/**
 * Automated YouTube Embed Fix Test
 * Tests the YouTube embed timeout fix and three-finger drag functionality
 */

// Test configuration
const TEST_CONFIG = {
    testVideoId: 'M7lc1UVf-VE', // The problematic video from error logs
    testUrl: 'https://www.youtube.com/watch?v=M7lc1UVf-VE',
    expectedEmbedUrl: 'https://www.youtube-nocookie.com/embed/M7lc1UVf-VE?rel=0&modestbranding=1',
    timeoutDuration: 10000 // 10 seconds
};

class YouTubeEmbedTest {
    constructor() {
        this.testResults = {
            embedDefinitionTest: false,
            urlGenerationTest: false,
            htmlContainerTest: false,
            timeoutPreventionTest: false,
            threeFingerDragTest: false
        };
        this.startTime = Date.now();
        console.log('ğŸ§ª Starting YouTube Embed Fix Test Suite');
    }

    // Test 1: Verify embed definition works
    testEmbedDefinition() {
        console.log('\nğŸ“ Test 1: Embed Definition Functionality');

        try {
            // Check if embed definitions are loaded
            if (typeof window !== 'undefined' && window.DEFAULT_EMBED_DEFINITIONS) {
                const youtubeDefinition = window.DEFAULT_EMBED_DEFINITIONS.find(def => def.type === 'youtube');

                if (youtubeDefinition) {
                    console.log('âœ… YouTube embed definition found');

                    // Test URL conversion
                    const embedUrl = youtubeDefinition.toEmbedUrl(TEST_CONFIG.testUrl);
                    console.log('ğŸ”— Generated embed URL:', embedUrl);

                    if (embedUrl && embedUrl.includes('youtube-nocookie.com')) {
                        console.log('âœ… URL generation working (using nocookie domain)');
                        this.testResults.embedDefinitionTest = true;
                        this.testResults.urlGenerationTest = true;
                    } else {
                        console.error('âŒ URL generation failed or not using nocookie domain');
                    }
                } else {
                    console.error('âŒ YouTube embed definition not found');
                }
            } else {
                console.error('âŒ Embed definitions not loaded');
            }
        } catch (error) {
            console.error('âŒ Embed definition test failed:', error.message);
        }
    }

    // Test 2: Verify HTML container system works
    testHTMLContainer() {
        console.log('\nğŸ–¼ï¸ Test 2: HTML Container System');

        try {
            if (typeof window !== 'undefined' && window.HTMLContainer && window.EmbedManager) {
                console.log('âœ… HTMLContainer and EmbedManager classes available');

                // Create mock objects for testing
                const mockCanvas = document.createElement('div');
                const mockCamera = { x: 0, y: 0, z: 1 };
                const mockStore = { get: () => null, getCurrentPageShapes: () => [] };

                // Test container creation
                const htmlContainer = new window.HTMLContainer(mockCanvas, mockCamera, mockStore);
                console.log('âœ… HTMLContainer instance created');

                const embedManager = new window.EmbedManager(htmlContainer, mockStore);
                console.log('âœ… EmbedManager instance created');

                this.testResults.htmlContainerTest = true;
            } else {
                console.error('âŒ HTMLContainer or EmbedManager not available');
            }
        } catch (error) {
            console.error('âŒ HTML container test failed:', error.message);
        }
    }

    // Test 3: Verify timeout prevention
    testTimeoutPrevention() {
        console.log('\nâ° Test 3: Timeout Prevention');

        try {
            // Create a mock embed shape
            const mockShape = {
                id: 'test-embed-' + Date.now(),
                type: 'embed',
                x: 100,
                y: 100,
                props: {
                    embedType: 'youtube',
                    url: TEST_CONFIG.testUrl,
                    width: 800,
                    height: 450
                }
            };

            console.log('ğŸ¬ Created mock YouTube embed shape:', mockShape.id);

            // Simulate the embed creation process
            if (typeof window !== 'undefined' && window.EmbedManager) {
                const mockCanvas = document.createElement('div');
                const mockCamera = { x: 0, y: 0, z: 1 };
                const mockStore = { get: () => mockShape, getCurrentPageShapes: () => [mockShape] };

                const htmlContainer = new window.HTMLContainer(mockCanvas, mockCamera, mockStore);
                const embedManager = new window.EmbedManager(htmlContainer, mockStore);

                // Track timeout warnings
                let timeoutWarnings = 0;
                const originalWarn = console.warn;
                console.warn = function(...args) {
                    if (args[0] && args[0].includes('embed load timeout')) {
                        timeoutWarnings++;
                    }
                    originalWarn.apply(console, args);
                };

                // Try to create the embed
                const iframe = embedManager.createEmbed(mockShape);

                if (iframe) {
                    console.log('âœ… Iframe created without immediate timeout');

                    // Wait a short time to see if timeout warnings occur
                    setTimeout(() => {
                        console.warn = originalWarn; // Restore original

                        if (timeoutWarnings === 0) {
                            console.log('âœ… No timeout warnings detected');
                            this.testResults.timeoutPreventionTest = true;
                        } else {
                            console.error(`âŒ ${timeoutWarnings} timeout warnings detected`);
                        }
                    }, 2000);
                } else {
                    console.error('âŒ Failed to create iframe');
                }
            }
        } catch (error) {
            console.error('âŒ Timeout prevention test failed:', error.message);
        }
    }

    // Test 4: Verify three-finger drag system
    testThreeFingerDrag() {
        console.log('\nğŸ‘† Test 4: Three-Finger Drag System');

        try {
            if (typeof window !== 'undefined' && window.UniversalInputSystem) {
                console.log('âœ… UniversalInputSystem available');

                // Check if the system has pointer handling
                const mockEditor = {
                    canvas: document.createElement('canvas'),
                    store: { get: () => null, put: () => {} },
                    camera: { x: 0, y: 0, z: 1 },
                    renderer: { scheduleRender: () => {} },
                    rootState: { setCurrentTool: () => {} }
                };

                const inputSystem = new window.UniversalInputSystem(mockEditor);
                console.log('âœ… UniversalInputSystem instance created');

                // Test if three pointer handling exists
                if (typeof inputSystem.handleThreePointerDown === 'function') {
                    console.log('âœ… Three-pointer handling methods available');
                    this.testResults.threeFingerDragTest = true;
                } else {
                    console.error('âŒ Three-pointer handling methods missing');
                }
            } else {
                console.error('âŒ UniversalInputSystem not available');
            }
        } catch (error) {
            console.error('âŒ Three-finger drag test failed:', error.message);
        }
    }

    // Test 5: Integration test - simulate full workflow
    testFullWorkflow() {
        console.log('\nğŸ”„ Test 5: Full Workflow Integration');

        try {
            console.log('ğŸ“ Step 1: User inserts YouTube URL');
            const testUrl = TEST_CONFIG.testUrl;

            console.log('ğŸ“ Step 2: URL detection and conversion');
            if (typeof window !== 'undefined' && window.detectEmbedType) {
                const embedInfo = window.detectEmbedType(testUrl);
                console.log('âœ… Embed detected:', embedInfo.type);
                console.log('ğŸ”— Embed URL:', embedInfo.embedUrl);
            }

            console.log('ğŸ“ Step 3: Shape creation simulation');
            const mockShape = {
                id: 'workflow-test-' + Date.now(),
                type: 'embed',
                x: 200,
                y: 200,
                props: {
                    embedType: 'youtube',
                    url: testUrl,
                    width: 800,
                    height: 450
                }
            };

            console.log('ğŸ“ Step 4: Iframe creation simulation');
            // This would normally create the actual iframe
            console.log('âœ… Full workflow simulation completed');

        } catch (error) {
            console.error('âŒ Full workflow test failed:', error.message);
        }
    }

    // Run all tests
    runAllTests() {
        console.log('ğŸš€ Running YouTube Embed Fix Test Suite\n');

        this.testEmbedDefinition();
        this.testHTMLContainer();
        this.testTimeoutPrevention();
        this.testThreeFingerDrag();
        this.testFullWorkflow();

        // Summary
        setTimeout(() => {
            this.printSummary();
        }, 3000); // Wait for async tests
    }

    // Print test summary
    printSummary() {
        const elapsed = Date.now() - this.startTime;
        const passed = Object.values(this.testResults).filter(Boolean).length;
        const total = Object.keys(this.testResults).length;

        console.log('\n' + '='.repeat(50));
        console.log('ğŸ“Š TEST SUMMARY');
        console.log('='.repeat(50));
        console.log(`â±ï¸  Total time: ${elapsed}ms`);
        console.log(`âœ… Passed: ${passed}/${total} tests`);
        console.log(`âŒ Failed: ${total - passed}/${total} tests`);
        console.log('');

        // Detailed results
        Object.entries(this.testResults).forEach(([test, passed]) => {
            const status = passed ? 'âœ… PASS' : 'âŒ FAIL';
            console.log(`${status} ${test}`);
        });

        console.log('');
        if (passed === total) {
            console.log('ğŸ‰ All tests passed! YouTube embed fix is working correctly.');
        } else {
