/**
 * TLDraw HTMLContainer Implementation
 * 
 * This component ensures that iframe-based HTML content (like YouTube embeds) 
 * stays fixed to its correct position on the canvas, even when the user scrolls, 
 * pans, or zooms.
 * 
 * Based on TLDraw's HTMLContainer.tsx architecture:
 * - Counter-Zooming: HTML container scales inversely to canvas zoom
 * - World-Coordinate Locking: Iframe anchored to canvas coordinates
 * - Pointer Event Control: Allows iframe interaction without breaking canvas
 */

class HTMLContainer {
    constructor(canvasContainer, camera, store) {
        this.canvasContainer = canvasContainer;
        this.camera = camera;
        this.store = store;

        // Container tracking
        this.containers = new Map(); // shapeId -> container element
        this.htmlElements = new Map(); // shapeId -> HTML element (iframe, etc)

        // Create the HTML layer that sits above canvas
        this.createHTMLLayer();

        // Set up universal canvas interaction management
        this.setupCanvasInteractionHooks();

        console.log('ğŸ–¼ï¸ HTMLContainer initialized with TLDraw pattern');
    }

    createHTMLLayer() {
        // Create the HTML layer container
        this.htmlLayer = document.createElement('div');
        this.htmlLayer.id = 'html-layer';
        this.htmlLayer.className = 'tl-html-layer';

        // Critical: Position absolutely and allow pointer events
        this.htmlLayer.style.cssText = `
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 100;
            transform-origin: 0 0;
        `;

        this.canvasContainer.appendChild(this.htmlLayer);
        console.log('ğŸ“º HTML layer created');
    }

    /**
     * Core method: Add HTML element for a shape
     * Follows TLDraw's HTMLContainer pattern exactly
     */
    addHTMLElement(shape, element) {
        const shapeId = shape.id;

        // Create container wrapper
        const container = document.createElement('div');
        container.className = 'tl-html-container';
        container.setAttribute('data-shape-id', shapeId);

        // Critical: Set up counter-zooming and world-coordinate positioning
        this.setupContainerTransform(container, shape);

        // Add the actual HTML element (iframe, etc.)
        container.appendChild(element);

        // CRITICAL: Set up universal movability for ALL container types
        this.setupUniversalContainerInteraction(container, shape, element);

        // Add to HTML layer
        this.htmlLayer.appendChild(container);

        // Track references
        this.containers.set(shapeId, container);
        this.htmlElements.set(shapeId, element);

        console.log(`âœ… HTML element added for shape ${shapeId}`);
        return container;
    }

    /**
     * TLDraw's core positioning logic:
     * 1. Counter-zooming (inverse scale)
     * 2. World-coordinate locking (transform-based positioning)
     */
    setupContainerTransform(container, shape) {
        const { x, y } = shape;
        const { width, height } = shape.props;
        const camera = this.camera;

        if (!camera) {
            console.warn('No camera available for transform');
            return;
        }

        // DEBUG CHUNK 10: Use proper transform positioning for HTTP server
        const screenX = (x + camera.x) * camera.z;
        const screenY = (y + camera.y) * camera.z;
        const inverseScale = 1 / camera.z;

        container.style.cssText = `
            position: absolute;
            transform: translate(${screenX}px, ${screenY}px) scale(${inverseScale});
            transform-origin: 0 0;
            width: ${width}px;
            height: ${height}px;
            pointer-events: auto;
            z-index: 100;
            overflow: visible;
        `;

        console.log('ğŸ”§ DEBUG CHUNK 10: Using proper transform positioning for HTTP server:', {
            pageCoords: { x, y },
            screenCoords: { x: screenX, y: screenY },
            scale: inverseScale,
            camera: { x: camera.x, y: camera.y, z: camera.z }
        });
    }

    /**
     * UNIVERSAL CONTAINER INTERACTION SYSTEM with BaseBoxShapeUtil integration
     * Makes ANY HTML container (iframe, div, canvas, etc.) movable and selectable
     */
    setupUniversalContainerInteraction(container, shape, element) {
        const shapeId = shape.id;

        // Prevent container from blocking canvas interactions by default
        container.style.pointerEvents = 'auto';

        // Track drag state for BaseBoxShapeUtil integration
        let isDragging = false;
        let dragStart = null;
        let shapeUtil = null;

        // Get shape util for transformation handlers
        if (window.tlEditor && window.tlEditor.shapeUtils) {
            shapeUtil = window.tlEditor.shapeUtils.get(shape.type);
        }

        // Enhanced mousedown handler with BaseBoxShapeUtil integration
        container.addEventListener('mousedown', (e) => {
            console.log(`ğŸ–±ï¸ Container clicked for shape ${shapeId}, type: ${shape.type}`);

            // CRITICAL: Select the shape first
            this.selectShape(shape);

            // Start transformation if shape util supports it
            if (shapeUtil && shapeUtil.canTranslate) {
                isDragging = true;
                dragStart = { x: e.clientX, y: e.clientY };

                // Call TLDraw's onTranslateStart
                if (shapeUtil.onTranslateStart) {
                    const editor = window.tlEditor || window.editor;
                    shapeUtil.onTranslateStart(shape, editor);
                }

                console.log(`âœ… Started BaseBoxShapeUtil translation for ${shapeId}`);
            }

            // CRITICAL: Create synthetic canvas event for selection
            // But only if UniversalInputSystem is not handling it
            if (!window.UniversalInputSystem || !window.tlEditor?.universalInput) {
                this.createSyntheticCanvasEvent(e, 'pointerdown');
            }

            // Stop propagation to prevent double handling
            e.stopPropagation();
        });

        // Enhanced mousemove handler for drag operations
        document.addEventListener('mousemove', (e) => {
            if (isDragging && dragStart && shapeUtil) {
                const delta = {
                    x: e.clientX - dragStart.x,
                    y: e.clientY - dragStart.y
                };

                // ENHANCED: Smooth dragging with reduced threshold and improved coordinate conversion
                if (shapeUtil.onTranslate && (Math.abs(delta.x) > 1 || Math.abs(delta.y) > 1)) {
                    const editor = window.tlEditor || window.editor;
                    const camera = editor.getCamera();

                    // IMPROVED: More accurate screen-to-page coordinate conversion
