/**
 * TLDraw HTMLContainer Implementation
 * 
 * This component ensures that iframe-based HTML content (like YouTube embeds) 
 * stays fixed to its correct position on the canvas, even when the user scrolls, 
 * pans, or zooms.
 * 
 * Based on TLDraw's HTMLContainer.tsx architecture:
 * - Counter-Zooming: HTML container scales inversely to canvas zoom
 * - World-Coordinate Locking: Iframe anchored to canvas coordinates
 * - Pointer Event Control: Allows iframe interaction without breaking canvas
 */

class HTMLContainer {
    constructor(canvasContainer, camera, store) {
        this.canvasContainer = canvasContainer;
        this.camera = camera;
        this.store = store;

        // Container tracking
        this.containers = new Map(); // shapeId -> container element
        this.htmlElements = new Map(); // shapeId -> HTML element (iframe, etc)

        // Create the HTML layer that sits above canvas
        this.createHTMLLayer();

        // Set up universal canvas interaction management
        this.setupCanvasInteractionHooks();

        console.log('ğŸ–¼ï¸ HTMLContainer initialized with TLDraw pattern');
    }

    createHTMLLayer() {
        // Create the HTML layer container
        this.htmlLayer = document.createElement('div');
        this.htmlLayer.id = 'html-layer';
        this.htmlLayer.className = 'tl-html-layer';

        // Critical: Position absolutely and allow pointer events
        this.htmlLayer.style.cssText = `
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            pointer-events: none;
            z-index: 100;
            transform-origin: 0 0;
        `;

        this.canvasContainer.appendChild(this.htmlLayer);
        console.log('ğŸ“º HTML layer created');
    }

    /**
     * Core method: Add HTML element for a shape
     * Follows TLDraw's HTMLContainer pattern exactly
     */
    addHTMLElement(shape, element) {
        const shapeId = shape.id;

        // Create container wrapper
        const container = document.createElement('div');
        container.className = 'tl-html-container';
        container.setAttribute('data-shape-id', shapeId);

        // Critical: Set up counter-zooming and world-coordinate positioning
        this.setupContainerTransform(container, shape);

        // Add the actual HTML element (iframe, etc.)
        container.appendChild(element);

        // CRITICAL: Set up universal movability for ALL container types
        this.setupUniversalContainerInteraction(container, shape, element);

        // Add to HTML layer
        this.htmlLayer.appendChild(container);

        // Track references
        this.containers.set(shapeId, container);
        this.htmlElements.set(shapeId, element);

        console.log(`âœ… HTML element added for shape ${shapeId}`);
        return container;
    }

    /**
     * TLDraw's core positioning logic:
     * 1. Counter-zooming (inverse scale)
     * 2. World-coordinate locking (transform-based positioning)
     */
    setupContainerTransform(container, shape) {
        const { x, y } = shape;
        const { width, height } = shape.props;
        const camera = this.camera;

        if (!camera) {
            console.warn('No camera available for transform');
            return;
        }

        // DEBUG CHUNK 10: Use proper transform positioning for HTTP server
        const screenX = (x + camera.x) * camera.z;
        const screenY = (y + camera.y) * camera.z;
        const inverseScale = 1 / camera.z;

        container.style.cssText = `
            position: absolute;
            transform: translate(${screenX}px, ${screenY}px) scale(${inverseScale});
            transform-origin: 0 0;
            width: ${width}px;
            height: ${height}px;
            pointer-events: auto;
            z-index: 100;
            overflow: visible;
        `;

        console.log('ğŸ”§ DEBUG CHUNK 10: Using proper transform positioning for HTTP server:', {
            pageCoords: { x, y },
            screenCoords: { x: screenX, y: screenY },
            scale: inverseScale,
            camera: { x: camera.x, y: camera.y, z: camera.z }
        });
    }

    /**
     * UNIVERSAL CONTAINER INTERACTION SYSTEM with BaseBoxShapeUtil integration
     * Makes ANY HTML container (iframe, div, canvas, etc.) movable and selectable
     */
    setupUniversalContainerInteraction(container, shape, element) {
        const shapeId = shape.id;

        // Prevent container from blocking canvas interactions by default
        container.style.pointerEvents = 'auto';

        // Track drag state for BaseBoxShapeUtil integration
        let isDragging = false;
        let dragStart = null;
        let shapeUtil = null;

        // Get shape util for transformation handlers
        if (window.tlEditor && window.tlEditor.shapeUtils) {
            shapeUtil = window.tlEditor.shapeUtils.get(shape.type);
        }

        // Enhanced mousedown handler with BaseBoxShapeUtil integration
        container.addEventListener('mousedown', (e) => {
            console.log(`ğŸ–±ï¸ Container clicked for shape ${shapeId}, type: ${shape.type}`);

            // CRITICAL: Select the shape first
            this.selectShape(shape);

            // Start transformation if shape util supports it
            if (shapeUtil && shapeUtil.canTranslate) {
                isDragging = true;
                dragStart = { x: e.clientX, y: e.clientY };

                // Call TLDraw's onTranslateStart
                if (shapeUtil.onTranslateStart) {
                    const editor = window.tlEditor || window.editor;
                    shapeUtil.onTranslateStart(shape, editor);
                }

                console.log(`âœ… Started BaseBoxShapeUtil translation for ${shapeId}`);
            }

            // CRITICAL: Create synthetic canvas event for selection
            // But only if UniversalInputSystem is not handling it
            if (!window.UniversalInputSystem || !window.tlEditor?.universalInput) {
                this.createSyntheticCanvasEvent(e, 'pointerdown');
            }

            // Stop propagation to prevent double handling
            e.stopPropagation();
        });

        // Enhanced mousemove handler for drag operations
        document.addEventListener('mousemove', (e) => {
            if (isDragging && dragStart && shapeUtil) {
                const delta = {
                    x: e.clientX - dragStart.x,
                    y: e.clientY - dragStart.y
                };

                // ENHANCED: Smooth dragging with reduced threshold and improved coordinate conversion
                if (shapeUtil.onTranslate && (Math.abs(delta.x) > 1 || Math.abs(delta.y) > 1)) {
                    const editor = window.tlEditor || window.editor;
                    const camera = editor.getCamera();

                    // IMPROVED: More accurate screen-to-page coordinate conversion
                    const pageDelta = camera ? {
                        x: delta.x / camera.z,
                        y: delta.y / camera.z
                    } : delta;

                    shapeUtil.onTranslate(shape, editor, pageDelta);

                    // CRITICAL FIX: Don't update dragStart during drag to prevent jumping
                    // This was causing the glitchy behavior - dragStart should remain constant
                    // dragStart = { x: e.clientX, y: e.clientY }; // REMOVED - causes jumping

                    console.log(`ğŸ”„ Smooth BaseBoxShapeUtil translating ${shapeId} by`, pageDelta);
                }
            }
        });

        // Enhanced mouseup handler
        document.addEventListener('mouseup', (e) => {
            if (isDragging && shapeUtil) {
                // Call TLDraw's onTranslateEnd
                if (shapeUtil.onTranslateEnd) {
                    const editor = window.tlEditor || window.editor;
                    shapeUtil.onTranslateEnd(shape, editor);
                }

                console.log(`âœ… Finished BaseBoxShapeUtil translation for ${shapeId}`);
            }

            isDragging = false;
            dragStart = null;
        });

        // Universal focus handler for keyboard navigation
        if (element.addEventListener) {
            element.addEventListener('focus', () => {
                this.selectShape(shape);
                console.log(`ğŸ¯ Container focused for shape ${shapeId}`);
            });
        }

        // Universal load/error handlers for any container type
        if (element.addEventListener) {
            element.addEventListener('load', () => {
                console.log(`âœ… Container content loaded for shape ${shapeId}`);
                container.classList.remove('tl-html-container--loading');
            });

            element.addEventListener('error', () => {
                console.warn(`âŒ Container content failed to load for shape ${shapeId}`);
                container.classList.add('tl-html-container--error');
            });
        }

        // Add loading state initially
        container.classList.add('tl-html-container--loading');
    }

    /**
     * Create synthetic canvas event to enable shape dragging
     * This makes ANY HTML container draggable through the canvas system
     */
    createSyntheticCanvasEvent(originalEvent, eventType) {
        const canvas = this.canvasContainer.querySelector('canvas');
        if (!canvas) {
            console.warn('Canvas not found for synthetic event');
            return;
        }

        // Create synthetic pointer event that matches original
        const syntheticEvent = new PointerEvent(eventType, {
            clientX: originalEvent.clientX,
            clientY: originalEvent.clientY,
            pointerId: originalEvent.pointerId || 1,
            button: originalEvent.button || 0,
            buttons: originalEvent.buttons || 1,
            bubbles: true,
            cancelable: true
        });

        // Dispatch to canvas so the shape can be dragged
        canvas.dispatchEvent(syntheticEvent);
        console.log(`ğŸ¯ Synthetic ${eventType} event dispatched to canvas`);
    }

    /**
     * Universal shape selection - works with any editor system
     */
    selectShape(shape) {
        if (window.tlEditor && window.tlEditor.select) {
            window.tlEditor.select(shape.id);
        } else if (window.editor && window.editor.select) {
            window.editor.select(shape.id);
        } else {
            console.warn('No editor found for shape selection');
        }
    }

    /**
     * Update all container positions when camera changes
     * This is called on every camera transform (pan, zoom, scroll)
     */
    updateAllPositions() {
        for (const [shapeId, container] of this.containers) {
            const shape = this.store.get(shapeId);
            if (shape && (shape.type === 'embed' || shape.type === 'youtube')) {
                this.setupContainerTransform(container, shape);
            }
        }
    }

    /**
     * Update specific shape's container (called by BaseBoxShapeUtil)
     */
    updateShapeContainer(shapeId) {
        const container = this.containers.get(shapeId);
        const shape = this.store.get(shapeId);

        if (container && shape) {
            this.setupContainerTransform(container, shape);
            console.log(`ğŸ”„ Updated container for shape ${shapeId}`);
        }
    }

    /**
     * TLDraw's pointer event control pattern
     * Temporarily disable HTML interactions during canvas operations
     */
    setInteractionMode(mode) {
        const pointerEvents = mode === 'canvas' ? 'none' : 'auto';

        for (const container of this.containers.values()) {
            container.style.pointerEvents = pointerEvents;

            // Add visual feedback for interaction mode
            if (mode === 'canvas') {
                container.classList.add('tl-html-container--canvas-interaction');
            } else {
                container.classList.remove('tl-html-container--canvas-interaction');
            }
        }

        // Also control the HTML layer itself
        this.htmlLayer.style.pointerEvents = mode === 'canvas' ? 'none' : 'auto';

        console.log(`ğŸ”„ HTML container interaction mode: ${mode}`);
    }

    /**
     * UNIVERSAL CANVAS INTERACTION HOOKS
     * Automatically manage container interactions during canvas operations
     */
    setupCanvasInteractionHooks() {
        const canvas = this.canvasContainer.querySelector('canvas');
        if (!canvas) return;

        // Hook into canvas mouse events to manage container interactions
        // Only setup if UniversalInputSystem is not active
        if (!window.UniversalInputSystem || !window.tlEditor?.universalInput) {
            canvas.addEventListener('mousedown', (e) => {
                // Check if we're starting a canvas interaction
                const isCanvasInteraction = !this.isPointOverContainer(e.clientX, e.clientY);

                if (isCanvasInteraction) {
                    this.setInteractionMode('canvas');

                    // Re-enable after interaction ends
                    const handleMouseUp = () => {
                        setTimeout(() => {
                            this.setInteractionMode('html');
                        }, 50);
                        document.removeEventListener('mouseup', handleMouseUp);
                    };
                    document.addEventListener('mouseup', handleMouseUp);
                }
            });
        }
    }

    /**
     * Check if a point is over any HTML container
     */
    isPointOverContainer(clientX, clientY) {
        for (const container of this.containers.values()) {
            const rect = container.getBoundingClientRect();
            if (clientX >= rect.left && clientX <= rect.right &&
                clientY >= rect.top && clientY <= rect.bottom) {
                return true;
            }
        }
        return false;
    }

    /**
     * Remove HTML element for a shape
     */
    removeHTMLElement(shapeId) {
        const container = this.containers.get(shapeId);
        const element = this.htmlElements.get(shapeId);

        if (container && container.parentNode) {
            container.parentNode.removeChild(container);
        }

        this.containers.delete(shapeId);
        this.htmlElements.delete(shapeId);

        console.log(`ğŸ—‘ï¸ HTML element removed for shape ${shapeId}`);
    }

    /**
     * Clean up HTML elements for shapes that no longer exist
     */
    cleanup() {
        const existingShapeIds = new Set();
        const shapes = this.store.getCurrentPageShapes ? this.store.getCurrentPageShapes() : [];

        shapes.forEach(shape => {
            if (shape.type === 'embed') {
                existingShapeIds.add(shape.id);
            }
        });

        for (const shapeId of this.containers.keys()) {
            if (!existingShapeIds.has(shapeId)) {
                this.removeHTMLElement(shapeId);
            }
        }
    }

    /**
     * Get HTML element for a shape
     */
    getHTMLElement(shapeId) {
        return this.htmlElements.get(shapeId);
    }

    /**
     * Get container for a shape
     */
    getContainer(shapeId) {
        return this.containers.get(shapeId);
    }

    /**
     * Set visibility for specific shape's HTML element
     */
    setVisibility(shapeId, visible) {
        const container = this.containers.get(shapeId);
        if (container) {
            container.style.display = visible ? 'block' : 'none';
        }
    }

    /**
     * Set visibility for all HTML elements
     */
    setAllVisibility(visible) {
        for (const container of this.containers.values()) {
            container.style.display = visible ? 'block' : 'none';
        }
    }

    /**
     * Update camera reference and refresh all positions
     */
    updateCamera(camera) {
        this.camera = camera;
        this.updateAllPositions();
    }

    /**
     * Destroy the HTML container system
     */
    destroy() {
        // Remove all HTML elements
        for (const shapeId of this.containers.keys()) {
            this.removeHTMLElement(shapeId);
        }

        // Remove HTML layer
        if (this.htmlLayer && this.htmlLayer.parentNode) {
            this.htmlLayer.parentNode.removeChild(this.htmlLayer);
        }

        // Clear references
        this.containers.clear();
        this.htmlElements.clear();

        console.log('ğŸ§¹ HTMLContainer destroyed');
    }
}

/**
 * Universal Embed Manager
 * Modular manager for creating and managing ALL embed types using HTMLContainer
 * Works with YouTube, Vimeo, Twitter, Figma, CodePen, and generic embeds
 */
class EmbedManager {
    constructor(htmlContainer, store) {
        this.htmlContainer = htmlContainer;
        this.store = store;
        this.embedDefinitions = null;

        this.initializeEmbedDefinitions();
        console.log('ğŸŒ Universal Embed Manager initialized');
    }

    initializeEmbedDefinitions() {
        // Use global embed definitions if available
        if (window.DEFAULT_EMBED_DEFINITIONS) {
            this.embedDefinitions = new Map();
            window.DEFAULT_EMBED_DEFINITIONS.forEach(def => {
                this.embedDefinitions.set(def.type, def);
            });
            console.log(`ğŸ“‹ Loaded ${this.embedDefinitions.size} embed definitions`);
        } else {
            console.warn('âš ï¸ DEFAULT_EMBED_DEFINITIONS not found');
        }
    }

    /**
     * Universal embed creation - works with ALL embed types with BaseBoxShapeUtil support
     */
    createEmbed(shape) {
        const { embedType, url, width, height } = shape.props;

        console.log('ğŸ¥ Creating embed:', {
            type: embedType,
            url: url,
            dimensions: { width, height },
            shapeId: shape.id
        });

        if (!this.embedDefinitions) {
            console.error('Embed definitions not loaded');
            return null;
        }

        // Stop error flood - check for undefined/null embedType first
        if (!embedType || embedType === 'undefined') {
            // Silent fail for undefined types to prevent spam
            return null;
        }

        const embedDef = this.embedDefinitions.get(embedType);
        if (!embedDef) {
            // Only log this once per session to prevent spam
            if (!this.loggedMissingTypes) this.loggedMissingTypes = new Set();
            if (!this.loggedMissingTypes.has(embedType)) {
                console.error(`Embed definition not found for type: ${embedType}`);
                this.loggedMissingTypes.add(embedType);
            }
            return null;
        }

        const embedUrl = embedDef.toEmbedUrl(url);
        if (!embedUrl) {
            // Only log URL validation errors once per URL to prevent spam
            if (!this.loggedInvalidUrls) this.loggedInvalidUrls = new Set();
            if (!this.loggedInvalidUrls.has(url)) {
                console.error(`Invalid URL for ${embedType}:`, url);
                this.loggedInvalidUrls.add(url);
            }
            return null;
        }

        console.log('âœ… Embed URL generated:', embedUrl);

        // Create iframe element with enhanced settings for BaseBoxShapeUtil
        const iframe = document.createElement('iframe');
        iframe.className = `embed-iframe embed-iframe--${embedType}`;
        iframe.src = embedUrl;
        iframe.title = `${embedDef.title} embed`;
        iframe.frameBorder = '0';
        iframe.loading = 'eager'; // Immediate loading for better UX
        iframe.referrerPolicy = 'strict-origin-when-cross-origin';

        // Enhanced iframe attributes for better compatibility
        iframe.setAttribute('allowfullscreen', 'true');
        iframe.setAttribute('webkitallowfullscreen', 'true');  
        iframe.setAttribute('mozallowfullscreen', 'true');

        // DEBUG CHUNK 1: Enhanced logging for troubleshooting
        console.group(`ğŸ” DEBUG: Creating ${embedType} iframe`);
        console.log('Original URL:', url);
        console.log('Embed URL:', embedUrl);
        console.log('Window origin:', window.location.origin);
        console.log('Protocol:', window.location.protocol);
        console.log('Iframe attributes:', {
            src: iframe.src,
            allow: iframe.allow,
            allowfullscreen: iframe.getAttribute('allowfullscreen'),
            loading: iframe.loading,
            referrerPolicy: iframe.referrerPolicy
        });
        console.groupEnd();

        // DEBUG CHUNK 6: Force explicit dimensions and minimal styles
        const embedWidth = width || 800;
        const embedHeight = height || 450;

        iframe.style.cssText = `
            width: ${embedWidth}px;
            height: ${embedHeight}px;
            border: none;
            display: block;
            background: transparent;
            position: relative;
            z-index: 1;
        `;

        console.log('ğŸ”§ DEBUG CHUNK 6: Explicit iframe dimensions:', {
            width: embedWidth,
            height: embedHeight,
            style: iframe.style.cssText
        });

        // DEBUG: Log the embed URL being used
        console.log(`ğŸ” Creating ${embedType} embed:`, {
            originalUrl: url,
            embedUrl: embedUrl,
            dimensions: { width: embedWidth, height: embedHeight }
        });

        // DEBUG CHUNK 8: DISABLED - Using actual iframe for HTTP server test
        // Test div has been disabled, now using real YouTube iframe

        // Apply embed-specific permissions
        this.applyEmbedPermissions(iframe, embedDef);

        // Add to HTML container system (this handles ALL interaction automatically)
        const container = this.htmlContainer.addHTMLElement(shape, iframe);

        // ENHANCED: Comprehensive load/error handling with fallbacks
        iframe.addEventListener('load', () => {
            console.log(`âœ… ${embedType} iframe loaded successfully:`, iframe.src);
            container.classList.remove('embed-loading', 'embed-error');
            container.classList.add('embed-loaded');

            // YouTube-specific post-load validation
            if (embedType === 'youtube') {
                this.validateYouTubeEmbed(iframe, container);
            }
        });

        iframe.addEventListener('error', (e) => {
            console.error(`âŒ ${embedType} iframe failed to load:`, iframe.src, e);
            this.handleEmbedError(iframe, container, embedType, url, shape);
        });

        // ENHANCED: Timeout fallback for slow loading embeds
        const loadTimeout = setTimeout(() => {
            if (!container.classList.contains('embed-loaded')) {
                console.warn(`â° ${embedType} embed load timeout:`, iframe.src);
                this.handleEmbedTimeout(iframe, container, embedType, url, shape);
            }
        }, 10000); // 10 second timeout

        iframe.addEventListener('load', () => {
            clearTimeout(loadTimeout);
        });

        // Additional YouTube-specific debugging
        if (embedType === 'youtube') {
            // Try to detect common YouTube embed issues
            setTimeout(() => {
                const rect = iframe.getBoundingClientRect();
                console.log('ğŸ” YouTube iframe status after 2s:', {
                    visible: rect.width > 0 && rect.height > 0,
                    dimensions: { width: rect.width, height: rect.height },
                    src: iframe.src,
                    parentVisible: container.style.display !== 'none'
                });
            }, 2000);
        }

        console.log(`âœ… ${embedDef.title} embed created for shape ${shape.id}`);
        console.log('ğŸ” Final iframe element:', {
            src: iframe.src,
            className: iframe.className,
            dimensions: { width: iframe.style.width, height: iframe.style.height },
            attributes: {
                allowfullscreen: iframe.getAttribute('allowfullscreen'),
                loading: iframe.loading
            }
        });

        return iframe;
    }

    // ENHANCEMENT: YouTube embed validation
    validateYouTubeEmbed(iframe, container) {
        setTimeout(() => {
            const rect = iframe.getBoundingClientRect();
            const isVisible = rect.width > 0 && rect.height > 0;

            console.log('ğŸ” YouTube validation:', {
                visible: isVisible,
                dimensions: { width: rect.width, height: rect.height },
                src: iframe.src,
                containerVisible: container.style.display !== 'none'
            });

            if (!isVisible) {
                console.warn('âš ï¸ YouTube iframe not visible after load');
                this.showEmbedWarning(container, 'Video may not be visible');
            }
        }, 1000);
    }

    // ENHANCEMENT: Handle embed errors with fallbacks
    handleEmbedError(iframe, container, embedType, originalUrl, shape) {
        container.classList.remove('embed-loading');
        container.classList.add('embed-error');

        if (embedType === 'youtube') {
            console.log('ğŸ”„ Attempting YouTube fallback strategies...');

            // Try different YouTube domains
            const videoId = this.extractYouTubeVideoId(originalUrl);
            if (videoId) {
                const fallbackUrls = [
                    `https://www.youtube.com/embed/${videoId}?rel=0&modestbranding=1`,
                    `https://youtube.com/embed/${videoId}?rel=0&modestbranding=1`,
                    `https://m.youtube.com/embed/${videoId}?rel=0&modestbranding=1`
                ];

                this.tryFallbackUrls(iframe, fallbackUrls, container, 0);
            } else {
                this.showEmbedError(container, 'Invalid YouTube URL');
            }
        } else {
            this.showEmbedError(container, `Failed to load ${embedType} embed`);
        }
    }

    // ENHANCEMENT: Handle embed timeouts
    handleEmbedTimeout(iframe, container, embedType, originalUrl, shape) {
        if (embedType === 'youtube') {
            // For YouTube, try a lighter embed
            const videoId = this.extractYouTubeVideoId(originalUrl);
            if (videoId) {
                const lightUrl = `https://www.youtube-nocookie.com/embed/${videoId}?rel=0&modestbranding=1&controls=1&showinfo=0`;
                console.log('ğŸ”„ Trying lightweight YouTube embed:', lightUrl);
                iframe.src = lightUrl;
            }
        }

        this.showEmbedWarning(container, 'Embed is loading slowly...');
    }

    // ENHANCEMENT: Try fallback URLs sequentially
    tryFallbackUrls(iframe, urls, container, index) {
        if (index >= urls.length) {
            this.showEmbedError(container, 'All fallback attempts failed');
            return;
        }

        const fallbackUrl = urls[index];
        console.log(`ğŸ”„ Trying fallback ${index + 1}/${urls.length}:`, fallbackUrl);

        // Set up one-time listeners for this attempt
        const onLoad = () => {
            console.log('âœ… Fallback succeeded:', fallbackUrl);
            container.classList.remove('embed-error');
            container.classList.add('embed-loaded');
            cleanup();
        };

        const onError = () => {
            console.log('âŒ Fallback failed:', fallbackUrl);
            cleanup();
            setTimeout(() => {
                this.tryFallbackUrls(iframe, urls, container, index + 1);
            }, 1000);
        };

        const cleanup = () => {
            iframe.removeEventListener('load', onLoad);
            iframe.removeEventListener('error', onError);
        };

        iframe.addEventListener('load', onLoad);
        iframe.addEventListener('error', onError);
        iframe.src = fallbackUrl;
    }

    // ENHANCEMENT: Extract YouTube video ID from various URL formats
    extractYouTubeVideoId(url) {
        const patterns = [
            /(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([a-zA-Z0-9_-]{11})/,
            /youtube\.com\/v\/([a-zA-Z0-9_-]{11})/,
            /youtube\.com\/.*[?&]v=([a-zA-Z0-9_-]{11})/
        ];

        for (const pattern of patterns) {
            const match = url.match(pattern);
            if (match) {
                return match[1];
            }
        }

        return null;
    }

    // ENHANCEMENT: Show error message in container
    showEmbedError(container, message) {
        const errorDiv = document.createElement('div');
        errorDiv.className = 'embed-error-message';
        errorDiv.innerHTML = `
            <div style="
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: center;
                height: 100%;
                background: #f5f5f5;
                border: 2px dashed #ccc;
                border-radius: 8px;
                padding: 20px;
                color: #666;
                font-family: system-ui, sans-serif;
            ">
                <div style="font-size: 24px; margin-bottom: 8px;">âš ï¸</div>
                <div style="font-weight: 600; margin-bottom: 4px;">Embed Error</div>
                <div style="font-size: 12px; text-align: center;">${message}</div>
                <button onclick="location.reload()" style="
                    margin-top: 12px;
                    padding: 6px 12px;
                    background: #007bff;
                    color: white;
                    border: none;
                    border-radius: 4px;
                    cursor: pointer;
                    font-size: 11px;
                ">Retry</button>
            </div>
        `;

        // Replace iframe with error message
        const iframe = container.querySelector('iframe');
        if (iframe) {
            iframe.style.display = 'none';
        }
        container.appendChild(errorDiv);
    }

    // ENHANCEMENT: Show warning message overlay
    showEmbedWarning(container, message) {
        const warningDiv = document.createElement('div');
        warningDiv.className = 'embed-warning-overlay';
        warningDiv.innerHTML = `
            <div style="
                position: absolute;
                top: 8px;
                right: 8px;
                background: rgba(255, 193, 7, 0.9);
                color: #856404;
                padding: 4px 8px;
                border-radius: 4px;
                font-size: 11px;
                font-weight: 600;
                z-index: 10;
                pointer-events: none;
            ">
                âš ï¸ ${message}
            </div>
        `;

        container.style.position = 'relative';
        container.appendChild(warningDiv);

        // Auto-remove after 5 seconds
        setTimeout(() => {
            if (warningDiv.parentNode) {
                warningDiv.parentNode.removeChild(warningDiv);
            }
        }, 5000);
    }

    /**
     * Apply embed-specific iframe permissions
     */
    applyEmbedPermissions(iframe, embedDef) {
        // DEBUG CHUNK 2: Enhanced YouTube-specific permissions
        if (embedDef.type === 'youtube') {
            // YouTube requires specific permissions to work properly
            const youtubePermissions = [
                'accelerometer',
                'autoplay',
                'clipboard-write',
                'encrypted-media',
                'gyroscope',
                'picture-in-picture',
                'web-share'
            ];

            iframe.allow = youtubePermissions.join('; ');

            // Remove any sandbox restrictions for YouTube
            iframe.removeAttribute('sandbox');

            // Ensure fullscreen is properly set
            iframe.allowFullscreen = true;
            iframe.setAttribute('allowfullscreen', '');
            iframe.setAttribute('webkitallowfullscreen', '');
            iframe.setAttribute('mozallowfullscreen', '');

            console.log('ğŸ”§ DEBUG CHUNK 2: YouTube permissions applied:', {
                allow: iframe.allow,
                sandbox: iframe.sandbox,
                allowfullscreen: iframe.allowfullscreen
            });

            return;
        }

        // Original permission logic for other embeds
        const basePermissions = [
            'accelerometer',
            'autoplay', 
            'clipboard-write',
            'encrypted-media',
            'gyroscope',
            'picture-in-picture'
        ];

        // Add override permissions from embed definition
        const overridePermissions = embedDef.overridePermissions || {};
        const permissions = [...basePermissions];

        // Add embed-specific permissions
        Object.keys(overridePermissions).forEach(perm => {
            if (overridePermissions[perm] && !permissions.includes(perm)) {
                permissions.push(perm);
            }
        });

        iframe.allow = permissions.join('; ');

        // Set fullscreen support for video embeds
        if (['youtube', 'vimeo'].includes(embedDef.type)) {
            iframe.allowFullscreen = true;
        }

        // Set sandbox for security
        if (embedDef.type === 'twitter') {
            iframe.sandbox = 'allow-scripts allow-same-origin allow-popups';
        } else if (embedDef.type === 'codepen') {
            iframe.sandbox = 'allow-scripts allow-same-origin allow-forms';
        }
    }


    /**
     * Update or recreate embed if URL changed - works for all embed types
     */
    updateEmbed(shape) {
        const existingIframe = this.htmlContainer.getHTMLElement(shape.id);
        const { embedType, url } = shape.props;

        if (existingIframe) {
            const embedDef = this.embedDefinitions.get(embedType);
            if (!embedDef) return existingIframe;

            const newEmbedUrl = embedDef.toEmbedUrl(url);

            if (existingIframe.src !== newEmbedUrl) {
                // URL changed, recreate
                this.htmlContainer.removeHTMLElement(shape.id);
                return this.createEmbed(shape);
            } else {
                // Just update container transform
                const container = this.htmlContainer.getContainer(shape.id);
                if (container) {
                    this.htmlContainer.setupContainerTransform(container, shape);
                }
                return existingIframe;
            }
        } else {
            // Create new embed
            return this.createEmbed(shape);
        }
    }

    /**
     * Get supported embed types
     */
    getSupportedTypes() {
        return this.embedDefinitions ? Array.from(this.embedDefinitions.keys()) : [];
    }

    /**
     * Check if embed type is supported
     */
    isTypeSupported(embedType) {
        return this.embedDefinitions ? this.embedDefinitions.has(embedType) : false;
    }
}

