    70// Initialize authentication integration
    71function initializeAuth() {
    72    // Check for existing user session
    73    const storedUser = localStorage.getItem('curatated_user');
    74    const guestUser = sessionStorage.getItem('curatated_guest');
    75    
    76    if (storedUser || guestUser) {
    77        const user = JSON.parse(storedUser || guestUser);
    78        updateUIForLoggedInUser(user);
