    87// Update UI for logged in user
    88function updateUIForLoggedInUser(user) {
    89    // Update user avatar
    90    const userAvatar = document.getElementById('user-avatar-btn');
    91    if (userAvatar) {
    92        if (user.profile.avatar) {
    93            userAvatar.innerHTML = `<img src="${user.profile.avatar}" alt="Avatar">`;
