   101    // Update main action button
   102    const mainActionBtn = document.getElementById('main-action-btn');
   103    if (mainActionBtn) {
   104        mainActionBtn.textContent = '+ Canvas';
   105        mainActionBtn.onclick = () => window.location.href = './canvas.html';
   106    }
   107    
   108    // Show user dropdown options for guests
   109    const userDropdown = document.getElementById('user-dropdown');
   110    if (userDropdown && user.isGuest) {
   111        // Add upgrade option for guests
   112        const upgradeItem = document.createElement('a');
   113        upgradeItem.href = './auth.html';
   114        upgradeItem.className = 'dropdown-item';
   115        upgradeItem.textContent = 'Upgrade Account';
   116        upgradeItem.style.color = 'var(--accent-green)';
   117        
   118        const divider = userDropdown.querySelector('.dropdown-divider');
   119        if (divider) {
   120            userDropdown.insertBefore(upgradeItem, divider);
   121        }
   122    }
   123}
