   125// Update UI for logged out user
   126function updateUIForLoggedOutUser() {
   127    // Update main action button
   128    const mainActionBtn = document.getElementById('main-action-btn');
   129    if (mainActionBtn) {
   130        mainActionBtn.textContent = 'Sign In';
   131        mainActionBtn.onclick = () => window.location.href = './auth.html';
   132    }
   133    
   134    // Update user avatar to show sign in
   135    const userAvatar = document.getElementById('user-avatar-btn');
   136    if (userAvatar) {
   137        userAvatar.onclick = () => window.location.href = './auth.html';
   138        userAvatar.title = 'Sign In';
   139    }
   140}
