   142// Setup user dropdown functionality
   143function setupUserDropdown() {
   144    const userAvatar = document.getElementById('user-avatar-btn');
   145    const userDropdown = document.getElementById('user-dropdown');
   146    
   147    if (userAvatar && userDropdown) {
   148        // Check if user is logged in
   149        const storedUser = localStorage.getItem('curatated_user');
   150        const guestUser = sessionStorage.getItem('curatated_guest');
   151        
   152        if (storedUser || guestUser) {
   153            userAvatar.addEventListener('click', (e) => {
   154                e.stopPropagation();
   155                const isVisible = userDropdown.style.display === 'block';
   156                userDropdown.style.display = isVisible ? 'none' : 'block';
   157            });
   158            
   159            // Close dropdown when clicking outside
   160            document.addEventListener('click', (e) => {
   161                if (!userAvatar.contains(e.target) && !userDropdown.contains(e.target)) {
   162                    userDropdown.style.display = 'none';
   163                }
   164            });
   165        }
   166    }
   167}
