   169// Generate star rating HTML
   170function generateStars(rating) {
   171    const fullStars = Math.floor(rating);
   172    const hasHalfStar = rating % 1 !== 0;
   173    let starsHTML = '';
   174    
   175    for (let i = 0; i < fullStars; i++) {
   176        starsHTML += '';
   177    }
   178    
   179    if (hasHalfStar) {
   180        starsHTML += '';
   181    }
   182    
   183    return starsHTML;
   184}
