   186// Create canvas item HTML
   187function createCanvasItem(canvas) {
   188    return `
   189        <div class="canvas-item" data-id="${canvas.id}">
   190            <div class="canvas-preview"></div>
   191            <div class="canvas-info">
   192                <h4 class="canvas-title">${canvas.title}</h4>
   193                <p class="canvas-author">by ${canvas.author}</p>
   194                <div class="canvas-stats">
   195                    <div class="rating">
   196                        <span class="stars">${generateStars(canvas.rating)}</span>
   197                        <span>${canvas.rating}</span>
   198                    </div>
   199                    <div class="likes">${canvas.likes}</div>
   200                </div>
   201            </div>
   202        </div>
   203    `;
   204}
   205
   206// Populate carousels with sample data
   207function populateCarousels() {
   208    const carousels = [
   209        { id: 'popular-carousel', data: sampleCanvases.popular },
   210        { id: 'recent-carousel', data: sampleCanvases.recent },
   211        { id: 'art-carousel', data: sampleCanvases.art },
   212        { id: 'tech-carousel', data: sampleCanvases.tech },
   213        { id: 'community-carousel', data: sampleCanvases.community }
   214    ];
   215
   216    carousels.forEach(carousel => {
   217        const track = document.querySelector(`#${carousel.id} .carousel-track`);
   218        if (track) {
   219            track.innerHTML = carousel.data.map(createCanvasItem).join('');
   220        }
   221    });
   222}
   223
   224// Setup carousel controls
   225function setupCarouselControls() {
   226    const prevButtons = document.querySelectorAll('.carousel-btn-prev');
   227    const nextButtons = document.querySelectorAll('.carousel-btn-next');
   228
   229    prevButtons.forEach(button => {
   230        button.addEventListener('click', () => {
   231            const carouselId = button.getAttribute('data-carousel');
   232            moveCarousel(carouselId, -1);
   233        });
   234    });
   235
   236    nextButtons.forEach(button => {
   237        button.addEventListener('click', () => {
   238            const carouselId = button.getAttribute('data-carousel');
   239            moveCarousel(carouselId, 1);
   240        });
   241    });
   242
   243    // Add canvas item click handlers
   244    document.addEventListener('click', (event) => {
   245        const canvasItem = event.target.closest('.canvas-item');
   246        if (canvasItem) {
   247            const canvasId = canvasItem.getAttribute('data-id');
   248            handleCanvasClick(canvasId);
   249        }
   250    });
   251}
   252
   253// Move carousel
   254function moveCarousel(carouselId, direction) {
   255    const carousel = document.getElementById(carouselId);
   256    const track = carousel.querySelector('.carousel-track');
   257    const items = track.querySelectorAll('.canvas-item');
   258    const state = carouselState[carouselId];
   259    
   260    if (!items.length) return;
   261
   262    const totalItems = items.length;
   263    const maxIndex = Math.max(0, totalItems - state.itemsPerView);
   264    
   265    state.currentIndex += direction;
   266    state.currentIndex = Math.max(0, Math.min(state.currentIndex, maxIndex));
   267    
   268    const itemWidth = items[0].offsetWidth + 16; // Including gap
   269    const translateX = -state.currentIndex * itemWidth;
   270    
   271    track.style.transform = `translateX(${translateX}px)`;
   272    
   273    // Update button states
   274    updateCarouselButtons(carouselId, state.currentIndex, maxIndex);
   275}
   276
   277// Update carousel button states
   278function updateCarouselButtons(carouselId, currentIndex, maxIndex) {
   279    const carousel = document.getElementById(carouselId);
   280    const prevBtn = carousel.querySelector('.carousel-btn-prev');
   281    const nextBtn = carousel.querySelector('.carousel-btn-next');
   282    
   283    prevBtn.style.opacity = currentIndex === 0 ? '0.5' : '1';
   284    prevBtn.style.cursor = currentIndex === 0 ? 'not-allowed' : 'pointer';
   285    
   286    nextBtn.style.opacity = currentIndex >= maxIndex ? '0.5' : '1';
   287    nextBtn.style.cursor = currentIndex >= maxIndex ? 'not-allowed' : 'pointer';
   288}
   289
   290// Handle canvas item click
   291function handleCanvasClick(canvasId) {
   292    console.log(`Canvas clicked: ${canvasId}`);
   293    
   294    // Find the canvas data
   295    let canvasData = null;
   296    for (const category in sampleCanvases) {
   297        canvasData = sampleCanvases[category].find(c => c.id == canvasId);
   298        if (canvasData) break;
   299    }
   300    
   301    if (canvasData) {
   302        showCanvasModal(canvasData);
   303    }
   304}
   305
   306// Show canvas modal
   307function showCanvasModal(canvas) {
   308    const modal = document.createElement('div');
   309    modal.innerHTML = `
   310        <div style="
   311            position: fixed;
   312            top: 0;
   313            left: 0;
   314            width: 100%;
   315            height: 100%;
   316            background: rgba(0,0,0,0.8);
   317            z-index: 1000;
   318            display: flex;
   319            align-items: center;
   320            justify-content: center;
   321        " onclick="this.remove()">
   322            <div style="
   323                background: var(--bg-secondary);
   324                border: 1px solid var(--border-color);
   325                border-radius: 12px;
   326                padding: 2rem;
   327                max-width: 500px;
   328                width: 90%;
   329                color: var(--text-primary);
   330            " onclick="event.stopPropagation()">
   331                <div style="
   332                    width: 100%;
   333                    height: 200px;
   334                    background: linear-gradient(135deg, var(--accent-green), var(--accent-orange));
   335                    border-radius: 8px;
   336                    margin-bottom: 1.5rem;
   337                    display: flex;
   338                    align-items: center;
   339                    justify-content: center;
   340                    font-size: 3rem;
   341                "></div>
   342                <h2 style="margin-bottom: 0.5rem; color: var(--text-primary);">${canvas.title}</h2>
   343                <p style="color: var(--text-muted); margin-bottom: 1rem;">by ${canvas.author}</p>
   344                <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 1.5rem;">
   345                    <div style="color: var(--accent-orange);">
   346                        ${generateStars(canvas.rating)} ${canvas.rating}
   347                    </div>
   348                    <div style="color: var(--text-muted);"> ${canvas.likes} likes</div>
   349                </div>
   350                <p style="color: var(--text-secondary); margin-bottom: 1.5rem;">
   351                    This is a preview of the "${canvas.title}" canvas. Click "View Canvas" to explore the full interactive experience.
   352                </p>
   353                <div style="display: flex; gap: 1rem; justify-content: flex-end;">
   354                    <button onclick="this.closest('div[onclick]').remove()" style="
   355                        background: var(--bg-tertiary);
   356                        color: var(--text-primary);
   357                        border: 1px solid var(--border-color);
   358                        padding: 0.5rem 1rem;
   359                        border-radius: 4px;
   360                        cursor: pointer;
   361                    ">Close</button>
   362                    <button onclick="window.location.href='./canvas.html?id=${canvas.id}'" style="
   363                        background: var(--accent-green);
   364                        color: var(--bg-primary);
   365                        border: none;
   366                        padding: 0.5rem 1rem;
   367                        border-radius: 4px;
   368                        cursor: pointer;
   369                        font-weight: 500;
   370                    ">View Canvas</button>
   371                </div>
   372            </div>
   373        </div>
   374    `;
   375    
   376    document.body.appendChild(modal);
   377}
   378
   379// Setup responsive carousels
   380function setupResponsiveCarousels() {
   381    function updateItemsPerView() {
   382        const width = window.innerWidth;
   383        let itemsPerView = 5;
   384        
   385        if (width < 768) {
   386            itemsPerView = 2;
   387        } else if (width < 1024) {
   388            itemsPerView = 3;
   389        } else if (width < 1200) {
   390            itemsPerView = 4;
   391        }
   392        
   393        // Update all carousel states
   394        Object.keys(carouselState).forEach(carouselId => {
   395            carouselState[carouselId].itemsPerView = itemsPerView;
   396            carouselState[carouselId].currentIndex = 0;
   397            
   398            // Reset carousel position
   399            const track = document.querySelector(`#${carouselId} .carousel-track`);
   400            if (track) {
   401                track.style.transform = 'translateX(0px)';
   402            }
   403        });
   404    }
   405    
   406    updateItemsPerView();
   407    window.addEventListener('resize', updateItemsPerView);
   408}
   409
   410// Initialize button states on load
   411window.addEventListener('load', () => {
   412    Object.keys(carouselState).forEach(carouselId => {
   413        const carousel = document.getElementById(carouselId);
   414        const track = carousel?.querySelector('.carousel-track');
   415        const items = track?.querySelectorAll('.canvas-item');
   416        
   417        if (items?.length) {
   418            const maxIndex = Math.max(0, items.length - carouselState[carouselId].itemsPerView);
   419            updateCarouselButtons(carouselId, 0, maxIndex);
   420        }
   421    });
   422});

