  1351â†’        function exportCanvas(format) {
  1352â†’            if (!window.tlEditor) {
  1353â†’                alert('Editor not loaded');
  1354â†’                return;
  1355â†’            }
  1356â†’            
  1357â†’            const handler = exportHandlers[format];
  1358â†’            if (!handler) {
  1359â†’                alert(`Export format "${format}" not supported yet`);
  1360â†’                return;
  1361â†’            }
  1362â†’            
  1363â†’            const result = handler(window.tlEditor);
  1364â†’            const link = document.createElement('a');
  1365â†’            link.download = result.filename;
  1366â†’            
  1367â†’            if (result.type === 'dataURL') {
  1368â†’                link.href = result.data;
  1369â†’            } else {
  1370â†’                const blob = new Blob([result.data], { type: result.mimeType });
  1371â†’                link.href = URL.createObjectURL(blob);
  1372â†’            }
  1373â†’            
  1374â†’            document.body.appendChild(link);
  1375â†’            link.click();
  1376â†’            document.body.removeChild(link);
  1377â†’            console.log('Canvas exported as', format);
  1378â†’        }
