  1380â†’        function generateSVGFromShapes(shapes) {
  1381â†’            let svg = '<svg width="800" height="600" xmlns="http://www.w3.org/2000/svg">';
  1382â†’            shapes.forEach(shape => {
  1383â†’                if (shape.type === 'geo' && shape.props.geo === 'rectangle') {
  1384â†’                    svg += `<rect x="${shape.x}" y="${shape.y}" width="${shape.props.w}" height="${shape.props.h}" 
  1385â†’                            fill="${shape.props.fill === 'none' ? 'transparent' : shape.props.color}" 
  1386â†’                            stroke="${shape.props.color}" stroke-width="${shape.props.strokeWidth || 2}"/>`;
  1387â†’                } else if (shape.type === 'geo' && shape.props.geo === 'ellipse') {
  1388â†’                    svg += `<ellipse cx="${shape.x + shape.props.w/2}" cy="${shape.y + shape.props.h/2}" 
  1389â†’                            rx="${shape.props.w/2}" ry="${shape.props.h/2}"
  1390â†’                            fill="${shape.props.fill === 'none' ? 'transparent' : shape.props.color}" 
  1391â†’                            stroke="${shape.props.color}" stroke-width="${shape.props.strokeWidth || 2}"/>`;
  1392â†’                }
  1393â†’                // Add more shape types as needed
  1394â†’            });
  1395â†’            svg += '</svg>';
  1396â†’            return svg;
  1397â†’        }
