  1530â†’        function setupDragAndDrop() {
  1531â†’            const canvas = document.getElementById('canvas-container');
  1532â†’            if (!canvas) return;
  1533â†’            
  1534â†’            canvas.addEventListener('dragover', (e) => {
  1535â†’                e.preventDefault();
