  1538â†’            });
  1539â†’            
  1540â†’            canvas.addEventListener('dragleave', (e) => {
  1541â†’                canvas.style.outline = '';
  1542â†’            });
  1543â†’            
  1544â†’            canvas.addEventListener('drop', async (e) => {
  1545â†’                e.preventDefault();
  1546â†’                canvas.style.outline = '';
  1547â†’                
  1548â†’                const files = Array.from(e.dataTransfer.files);
  1549â†’                const imageFiles = files.filter(f => f.type.startsWith('image/'));
  1550â†’                
  1551â†’                for (const file of imageFiles) {
  1552â†’                    const reader = new FileReader();
  1553â†’                    reader.onload = (event) => {
  1554â†’                        const dataUrl = event.target.result;
  1555â†’                        
  1556â†’                        // Create image at drop position
  1557â†’                        const camera = window.tlEditor.getCamera();
  1558â†’                        const dropPoint = {
  1559â†’                            x: e.clientX - canvas.getBoundingClientRect().left,
  1560â†’                            y: e.clientY - canvas.getBoundingClientRect().top
  1561â†’                        };
  1562â†’                        const pagePoint = camera ? camera.screenToPage(dropPoint) : dropPoint;
  1563â†’                        
  1564â†’                        // Create temporary image to get dimensions
  1565â†’                        const img = new Image();
  1566â†’                        img.onload = () => {
  1567â†’                            const maxSize = 400;
  1568â†’                            let width = img.width;
  1569â†’                            let height = img.height;
  1570â†’                            
  1571â†’                            if (width > maxSize || height > maxSize) {
  1572â†’                                const scale = Math.min(maxSize / width, maxSize / height);
  1573â†’                                width *= scale;
  1574â†’                                height *= scale;
  1575â†’                            }
  1576â†’                            
  1577â†’                            const imageShape = new TLShape('image', null, {
  1578â†’                                x: pagePoint.x - width / 2,
  1579â†’                                y: pagePoint.y - height / 2,
  1580â†’                                props: {
  1581â†’                                    width: width,
  1582â†’                                    height: height,
  1583â†’                                    url: dataUrl,
  1584â†’                                    fileName: file.name
  1585â†’                                }
  1586â†’                            });
  1587â†’                            
  1588â†’                            window.tlEditor.createShapes([imageShape]);
  1589â†’                            console.log('Dropped image:', imageShape);
  1590â†’                        };
  1591â†’                        img.src = dataUrl;
  1592â†’                    };
  1593â†’                    reader.readAsDataURL(file);
  1594â†’                }
  1595â†’            });
  1596â†’        }
