  1757â†’            }
  1758â†’            
  1759â†’            function updateShapeFromUserInput(point) {
  1760â†’                if (!currentShape || !startPoint) return;
  1761â†’                
  1762â†’                try {
  1763â†’                    // Calculate size from start to current point
  1764â†’                    const width = Math.abs(point.x - startPoint.x);
