/**
 * Notion Context Menu Integration for Curatated Canvas
 * Seamlessly integrates the Notion-style context menu with existing systems
 */

(function() {
    'use strict';

    // Wait for all systems to load
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', initializeNotionContextMenu);
    } else {
        initializeNotionContextMenu();
    }

    function initializeNotionContextMenu() {
        console.log('ğŸ¨ Initializing Notion Context Menu Integration...');

        // Wait for editor systems to be available
        const checkEditor = () => {
            // Check multiple possible editor references
            const editor = window.canvasEditor || 
                          window.tlEditor || 
                          window.editor ||
                          document.querySelector('[data-editor]');

            if (editor && typeof NotionContextMenuSystem !== 'undefined') {
                setupNotionContextMenu(editor);
                return true;
            }
            return false;
        };

        // Try immediate initialization
        if (checkEditor()) {
            return;
        }

        // Poll for editor availability
        let attempts = 0;
        const maxAttempts = 50; // 10 second timeout
        const pollInterval = setInterval(() => {
            attempts++;

            if (checkEditor()) {
                clearInterval(pollInterval);
                console.log('âœ… Notion context menu initialized after polling');
            } else if (attempts >= maxAttempts) {
                clearInterval(pollInterval);
                console.warn('âš ï¸ Notion context menu initialization timeout - editor not found');

                // Fallback: Create minimal integration
                createFallbackIntegration();
            }
        }, 200);
    }

    function setupNotionContextMenu(editor) {
        console.log('ğŸ¨ Setting up Notion Context Menu for editor:', editor);

        try {
            // Create the Notion context menu system
            const notionContextMenu = new NotionContextMenuSystem(editor);

            // Attach to editor
            editor.notionContextMenu = notionContextMenu;

            // Enhanced integration with existing systems
            if (editor.toolManager) {
                enhanceToolManagerIntegration(notionContextMenu, editor.toolManager);
            }

            if (editor.inputManager) {
                enhanceInputManagerIntegration(notionContextMenu, editor.inputManager);
            }

            // Register additional sections based on existing capabilities
            registerExistingSystemSections(notionContextMenu, editor);

            // Global access for debugging/extension
            window.notionContextMenu = notionContextMenu;

            console.log('âœ… Notion Context Menu successfully integrated with editor');

            // Dispatch integration event
            const event = new CustomEvent('notionContextMenuReady', {
                detail: { notionContextMenu, editor }
            });
            document.dispatchEvent(event);

        } catch (error) {
            console.error('âŒ Failed to setup Notion Context Menu:', error);
            createFallbackIntegration();
        }
    }

    function enhanceToolManagerIntegration(contextMenu, toolManager) {
        console.log('ğŸ¨ Enhancing tool manager integration...');

        // Override showContextMenu to use Notion system
        if (toolManager.showContextMenu) {
            const originalShowContextMenu = toolManager.showContextMenu.bind(toolManager);

            toolManager.showContextMenu = function(event) {
                if (contextMenu) {
                    contextMenu.show(event);
                } else {
                    originalShowContextMenu(event);
                }
            };
        }

        // Enhance tool switching
        contextMenu.setTool = function(toolId) {
            if (toolManager.setTool) {
                toolManager.setTool(toolId);
            } else if (toolManager.currentTool !== undefined) {
                toolManager.currentTool = toolId;
            }

            // Update toolbar visual state
            updateToolbarState(toolId);
        };
    }

    function enhanceInputManagerIntegration(contextMenu, inputManager) {
        console.log('ğŸ¨ Enhancing input manager integration...');

        // Override context menu handling
        if (inputManager.showContextMenu) {
            const originalShowContextMenu = inputManager.showContextMenu.bind(inputManager);

            inputManager.showContextMenu = function(event) {
                if (contextMenu) {
                    contextMenu.show(event);
                } else {
                    originalShowContextMenu(event);
                }
            };
        }
    }

    function registerExistingSystemSections(contextMenu, editor) {
        console.log('ğŸ¨ Registering sections for existing systems...');

        // Register embed system integration if available
        if (window.embedSystem || window.TLDrawEmbedSystem) {
            contextMenu.registerSection('advanced-embeds', {
                title: 'Advanced',
                priority: 450,
                condition: () => true,
                items: () => [
                    {
                        id: 'spotify',
                        label: 'Spotify Track',
                        description: 'Embed a Spotify track',
                        icon: 'ğŸµ',
                        action: (point) => contextMenu.showEmbedModal('spotify', point)
                    },
                    {
                        id: 'apple-music',
                        label: 'Apple Music',
                        description: 'Embed Apple Music content',
                        icon: 'ğŸ',
                        action: (point) => contextMenu.showEmbedModal('apple-music', point)
                    },
                    {
                        id: 'custom-embed',
                        label: 'Custom Embed',
                        description: 'Embed custom content',
                        icon: 'ğŸ”—',
                        action: (point) => contextMenu.showEmbedModal('custom', point)
                    }
                ]
            });
        }

        // Register diagnostic section if unified diagnostics available
        if (window.unifiedDiagnostics) {
            contextMenu.registerSection('diagnostics', {
                title: 'Debug',
                priority: 600,
                condition: () => true,
                collapsed: true,
                items: () => [
                    {
                        id: 'show-diagnostics',
                        label: 'Show Diagnostics',
                        description: 'Open diagnostic dashboard',
                        icon: 'ğŸ”',
                        action: () => {
                            if (window.unifiedDiagnostics.showDashboard) {
                                window.unifiedDiagnostics.showDashboard();
                            }
                        }
                    },
                    {
                        id: 'capture-state',
                        label: 'Capture State',
                        description: 'Capture current system state',
                        icon: 'ğŸ“Š',
                        action: () => {
                            if (window.unifiedDiagnostics.captureCurrentState) {
                                const state = window.unifiedDiagnostics.captureCurrentState();
                                console.log('ğŸ“Š State captured:', state);
                            }
                        }
                    }
                ]
            });
        }
    }

    function updateToolbarState(activeTool) {
        // Update toolbar button states
        const toolbarButtons = document.querySelectorAll('.toolbar-btn');
        toolbarButtons.forEach(btn => {
            const tool = btn.getAttribute('data-tool');
            if (tool === activeTool) {
                btn.classList.add('active');
            } else {
                btn.classList.remove('active');
            }
        });
    }

    function createFallbackIntegration() {
        console.log('ğŸ¨ Creating fallback context menu integration...');

        // Create a minimal context menu for cases where editor is not available
        const fallbackMenu = {
            show: function(event) {
                console.log('ğŸ¨ Fallback context menu triggered');

                // Create simple context menu
                const menu = document.createElement('div');
                menu.className = 'notion-context-menu';
                menu.innerHTML = `
                    <div class="notion-context-menu-section">
                        <div class="notion-menu-item" onclick="this.parentElement.parentElement.remove()">
                            <div class="notion-menu-item-icon">ğŸ¨</div>
                            <div class="notion-menu-item-content">
                                <div class="notion-menu-item-label">Context Menu Active</div>
                                <div class="notion-menu-item-description">Notion-style context menu is working</div>
                            </div>
                        </div>
                        <div class="notion-menu-item" onclick="window.location.reload()">
                            <div class="notion-menu-item-icon">ğŸ”„</div>
                            <div class="notion-menu-item-content">
                                <div class="notion-menu-item-label">Reload Page</div>
                                <div class="notion-menu-item-description">Refresh to retry initialization</div>
                            </div>
                        </div>
                    </div>
                `;

                // Position menu
                menu.style.left = event.clientX + 'px';
                menu.style.top = event.clientY + 'px';

                document.body.appendChild(menu);

                // Auto-remove after 5 seconds
                setTimeout(() => {
                    if (menu.parentElement) {
                        menu.remove();
                    }
                }, 5000);
            }
        };

        // Attach to document for right-click events
        document.addEventListener('contextmenu', (event) => {
            // Only handle canvas area context menus
            if (event.target.closest('.canvas-container') || 
                event.target.tagName.toLowerCase() === 'canvas') {
                event.preventDefault();
                fallbackMenu.show(event);
            }
        });

        window.notionContextMenu = fallbackMenu;
    }

    // Global convenience functions
    window.showNotionContextMenu = function(event) {
        if (window.notionContextMenu) {
            window.notionContextMenu.show(event);
        } else {
            console.warn('ğŸ¨ Notion context menu not available');
        }
    };

    // Enhanced right-click detection for canvas areas
    document.addEventListener('contextmenu', (event) => {
        // Check if right-click happened on canvas or canvas container
        const isCanvasArea = event.target.closest('.canvas-container') ||
                           event.target.tagName.toLowerCase() === 'canvas' ||
                           event.target.classList.contains('tldraw__editor');

        if (isCanvasArea && window.notionContextMenu && window.notionContextMenu.show) {
            event.preventDefault();
            event.stopPropagation();
            window.notionContextMenu.show(event);
        }
    });

    console.log('ğŸ¨ Notion Context Menu Integration script loaded');

})();

