    def move_pointer_humanlike(self, target_x, target_y, description="Moving pointer"):
        """Move pointer with human-like trajectory and visual feedback"""
        print(f"   ğŸ–±ï¸ {description}...")

        # Show pointer
        self.driver.execute_script("""
            const pointer = document.getElementById('user-mock-pointer');
            if (pointer) {
                pointer.style.display = 'block';
            }
        """)

        # Get current mouse position (approximate)
        current_pos = self.driver.execute_script("""
            return {
                x: window.innerWidth / 2,
                y: window.innerHeight / 2
            };
        """)

        # Create smooth trajectory with multiple intermediate points
        import math
        steps = 15
        for i in range(steps + 1):
            progress = i / steps

            # Add slight curve to make movement more human-like
            curve_offset = math.sin(progress * math.pi) * 20

            x = current_pos['x'] + (target_x - current_pos['x']) * progress + curve_offset
            y = current_pos['y'] + (target_y - current_pos['y']) * progress

            # Update pointer position
            self.driver.execute_script(f"""
                const pointer = document.getElementById('user-mock-pointer');
                if (pointer) {{
                    pointer.style.left = '{x}px';
                    pointer.style.top = '{y}px';
                    pointer.style.display = 'block';
                }}
            """)

            # Human-like timing variation
            import random
            delay = 0.02 + (random.random() * 0.01)  # 20-30ms variation
