#!/usr/bin/env python3
"""
Context Menu Actions Test - Validates Actual Canvas State Changes
Tests that right-click menu actions actually create shapes and change canvas state
"""

import time
from selenium import webdriver
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.support.ui import WebDriverWait

class ContextMenuActionsTest:
    def __init__(self):
        self.driver = None
        self.setup_chrome()

    def setup_chrome(self):
        chrome_options = Options()
        chrome_options.add_argument("--disable-blink-features=AutomationControlled")
        chrome_options.add_argument("--window-size=1600,1000")
        chrome_options.add_argument("--window-position=50,50")

        print("ğŸ§ª Starting Chrome for Context Menu Actions testing...")
        self.driver = webdriver.Chrome(options=chrome_options)
        self.driver.execute_script("Object.defineProperty(navigator, 'webdriver', {get: () => undefined})")

    def test_context_menu_actions(self):
        try:
            print("ğŸŒ Loading canvas page...")
            self.driver.get("http://localhost:8000/canvas.html")

            # Wait for complete initialization
            WebDriverWait(self.driver, 15).until(
                lambda driver: driver.execute_script("""
                    return window.tlEditor && 
                           window.NotionContextMenuSystem &&
                           window.notionContextMenu &&
                           window.editorReady;
                """)
            )

            print("âœ… Canvas and context menu systems loaded!")

            # Test 1: Rectangle creation from context menu
            self.test_rectangle_creation()
            time.sleep(2)

            # Test 2: Text creation from context menu
            self.test_text_creation()
            time.sleep(2)

            # Test 3: Tool switching from context menu
            self.test_tool_switching()
            time.sleep(2)

            # Test 4: Verify canvas state changes
            self.verify_canvas_state()

            print("\nğŸ‰ ALL TESTS COMPLETED!")
            print("ğŸ‘ï¸ Browser will stay open for 20 seconds for visual verification...")
            time.sleep(20)

        except Exception as e:
            print(f"âŒ Test error: {e}")
            self.driver.save_screenshot("context_menu_actions_error.png")
            time.sleep(10)

        finally:
            if self.driver:
                self.driver.quit()

    def setup_pointer_highlighting(self):
        """Setup visual pointer highlighting system"""
        self.driver.execute_script("""
            // Remove existing highlight if any
            const existing = document.getElementById('user-mock-pointer');
            if (existing) existing.remove();

            // Create pointer highlight element
            const pointer = document.createElement('div');
            pointer.id = 'user-mock-pointer';
            pointer.style.cssText = `
                position: fixed;
                width: 20px;
                height: 20px;
                background: radial-gradient(circle, rgba(255,0,0,0.8) 0%, rgba(255,0,0,0.3) 50%, transparent 100%);
                border: 2px solid #ff0000;
                border-radius: 50%;
                pointer-events: none;
                z-index: 10000;
                transition: all 0.1s ease;
                transform: translate(-50%, -50%);
                display: none;
            `;
            document.body.appendChild(pointer);

            // Create click ripple effect
            window.createClickRipple = function(x, y, color = '#00ff00') {
                const ripple = document.createElement('div');
                ripple.style.cssText = `
                    position: fixed;
                    left: ${x}px;
                    top: ${y}px;
                    width: 10px;
                    height: 10px;
                    background: ${color};
                    border: 2px solid ${color};
                    border-radius: 50%;
                    pointer-events: none;
                    z-index: 10001;
                    transform: translate(-50%, -50%);
                    animation: clickRipple 0.6s ease-out forwards;
                `;

                // Add ripple animation
                if (!document.getElementById('ripple-styles')) {
                    const style = document.createElement('style');
                    style.id = 'ripple-styles';
                    style.textContent = `
                        @keyframes clickRipple {
                            0% { 
                                transform: translate(-50%, -50%) scale(1);
                                opacity: 1;
                            }
                            100% { 
                                transform: translate(-50%, -50%) scale(4);
                                opacity: 0;
                            }
                        }
                    `;
                    document.head.appendChild(style);
                }

                document.body.appendChild(ripple);
                setTimeout(() => ripple.remove(), 600);
            };

            console.log('ğŸ¯ Pointer highlighting system activated');
        """)

    def move_pointer_humanlike(self, target_x, target_y, description="Moving pointer"):
        """Move pointer with human-like trajectory and visual feedback"""
        print(f"   ğŸ–±ï¸ {description}...")

        # Show pointer
        self.driver.execute_script("""
            const pointer = document.getElementById('user-mock-pointer');
            if (pointer) {
                pointer.style.display = 'block';
            }
        """)

        # Get current mouse position (approximate)
        current_pos = self.driver.execute_script("""
            return {
                x: window.innerWidth / 2,
                y: window.innerHeight / 2
            };
        """)

        # Create smooth trajectory with multiple intermediate points
        import math
        steps = 15
        for i in range(steps + 1):
            progress = i / steps

            # Add slight curve to make movement more human-like
            curve_offset = math.sin(progress * math.pi) * 20

            x = current_pos['x'] + (target_x - current_pos['x']) * progress + curve_offset
            y = current_pos['y'] + (target_y - current_pos['y']) * progress

            # Update pointer position
            self.driver.execute_script(f"""
                const pointer = document.getElementById('user-mock-pointer');
                if (pointer) {{
                    pointer.style.left = '{x}px';
                    pointer.style.top = '{y}px';
                    pointer.style.display = 'block';
                }}
            """)

            # Human-like timing variation
            import random
            delay = 0.02 + (random.random() * 0.01)  # 20-30ms variation
            time.sleep(delay)

