/* ARC Browser Style Theme Picker */

/* Theme Button in Navigation */
.theme-btn {
    background: transparent;
    border: none;
    color: var(--text-secondary);
    cursor: pointer;
    padding: 0.5rem;
    border-radius: 4px;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
}

.theme-btn:hover {
    background: var(--bg-tertiary);
    color: var(--text-primary);
}

.theme-btn svg {
    transition: transform 0.3s ease;
}

.theme-btn:hover svg {
    transform: rotate(45deg);
}

/* Theme Modal */
.theme-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 2000;
    backdrop-filter: blur(8px);
}

.theme-modal-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.6);
    display: flex;
    align-items: center;
    justify-content: center;
}

.theme-picker-container {
    background: var(--bg-secondary);
    border: 1px solid var(--border-color);
    border-radius: 20px;
    padding: 2rem;
    width: 280px;
    box-shadow: 0 20px 60px rgba(0, 0, 0, 0.4);
    position: relative;
    overflow: hidden;
}

/* Add noise texture to container */
.theme-picker-container::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
        radial-gradient(circle at 1px 1px, rgba(255,255,255,0.15) 1px, transparent 0);
    background-size: 20px 20px;
    opacity: var(--noise-opacity, 0.2);
    pointer-events: none;
    z-index: -1;
}

.theme-picker-container > * {
    position: relative;
    z-index: 1;
}

/* Theme Header */
.theme-picker-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 1.5rem;
}

.theme-preview-circle {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    background: var(--theme-preview-bg, var(--bg-primary));
    border: 2px solid var(--border-color);
    position: relative;
    overflow: hidden;

