/**
 * Notion-Style Context Menu System for Curatated Canvas
 * Enterprise-grade modular architecture with TLDraw integration
 * Inspired by Notion's context menu UX patterns
 */

class NotionContextMenuSystem {
    constructor(editor) {
        this.editor = editor;
        this.activeMenu = null;
        this.sections = new Map();
        this.shortcuts = new Map();

        this.initializeSystem();
        this.registerDefaultSections();
        this.bindEvents();

        console.log('ğŸ¨ Notion Context Menu System initialized');
    }

    initializeSystem() {
        // Create global styles for Notion-style menus
        this.injectNotionStyles();

        // Initialize clipboard API if available
        this.clipboardSupported = navigator.clipboard && window.isSecureContext;

        // Track selection state
        this.selectionState = {
            hasSelection: false,
            selectedShapes: [],
            selectionType: null
        };
    }

    injectNotionStyles() {
        if (document.getElementById('notion-context-menu-styles')) return;

        const styleSheet = document.createElement('style');
        styleSheet.id = 'notion-context-menu-styles';
        styleSheet.textContent = `
            /* Notion-Style Context Menu - Enhanced with our theme system */
            .notion-context-menu {
                position: fixed;
                background: var(--bg-secondary, rgba(30, 35, 40, 0.98));
                backdrop-filter: blur(16px) saturate(180%);
                -webkit-backdrop-filter: blur(16px) saturate(180%);
                border: 1px solid var(--border-color, rgba(69, 85, 102, 0.3));
                border-radius: 12px;
                box-shadow: 
                    0 0 0 1px rgba(255, 255, 255, 0.03),
                    0 16px 48px rgba(0, 0, 0, 0.4),
                    0 4px 16px rgba(0, 0, 0, 0.2),
                    inset 0 1px 0 rgba(255, 255, 255, 0.05);
                padding: 6px;
                z-index: 5000;
                min-width: 280px;
                max-width: 360px;
                font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', system-ui, sans-serif;
                font-size: 14px;
                font-weight: 400;
                line-height: 1.5;
                animation: notionMenuFadeIn 0.2s cubic-bezier(0.16, 1, 0.3, 1);
                user-select: none;
                overflow: hidden;
                transform-origin: top left;
            }

            /* Add subtle noise texture like Notion */
            .notion-context-menu::before {
                content: '';
                position: absolute;
                top: 0;
                left: 0;
                right: 0;
                bottom: 0;
                background-image: 
                    radial-gradient(circle at 1px 1px, rgba(255,255,255,0.08) 1px, transparent 0);
                background-size: 16px 16px;
                opacity: 0.4;
                pointer-events: none;
                border-radius: inherit;
            }

            /* Dark theme support */
            [data-theme="dark"] .notion-context-menu,
            .dark .notion-context-menu {
                background: rgba(37, 37, 37, 0.95);
                border-color: rgba(255, 255, 255, 0.08);
                color: rgba(255, 255, 255, 0.9);
            }

            .notion-context-menu-section {
                margin-bottom: 4px;
                position: relative;
                z-index: 1;
            }

            .notion-context-menu-section:last-child {
                margin-bottom: 0;
            }

            .notion-context-menu-section:not(:last-child)::after {
                content: '';
                position: absolute;
                bottom: -2px;
                left: 8px;
                right: 8px;
                height: 1px;
                background: linear-gradient(90deg, transparent, var(--border-color, rgba(69, 85, 102, 0.2)), transparent);
                opacity: 0.6;
            }

            .notion-menu-section-title {
                padding: 12px 16px 6px;
                font-size: 11px;
                font-weight: 600;
                color: var(--text-muted, rgba(102, 119, 136, 0.8));
                text-transform: uppercase;
                letter-spacing: 0.6px;
                user-select: none;
                display: flex;
                align-items: center;
                gap: 8px;
            }

            .notion-menu-section-title::before {
                content: '';
                width: 3px;
                height: 3px;
                background: var(--accent-green, #65626F);
                border-radius: 50%;
                opacity: 0.6;
            }

            [data-theme="dark"] .notion-menu-section-title,
            .dark .notion-menu-section-title {
                color: rgba(255, 255, 255, 0.5);
            }

            .notion-menu-item {
                display: flex;
                align-items: center;
                padding: 8px 12px;
                border-radius: 6px;
                cursor: pointer;
                transition: all 0.15s ease;
                gap: 12px;
                font-size: 14px;
                color: rgba(0, 0, 0, 0.87);
                position: relative;
                line-height: 1.4;
            }

            [data-theme="dark"] .notion-menu-item,
            .dark .notion-menu-item {
                color: rgba(255, 255, 255, 0.87);
            }

            .notion-menu-item:hover:not(.disabled) {
                background: rgba(0, 0, 0, 0.04);
                color: rgba(0, 0, 0, 1);
            }

            [data-theme="dark"] .notion-menu-item:hover:not(.disabled),
            .dark .notion-menu-item:hover:not(.disabled) {
                background: rgba(255, 255, 255, 0.08);
                color: rgba(255, 255, 255, 1);
            }

            .notion-menu-item.disabled {
                opacity: 0.4;
                cursor: not-allowed;
            }

            .notion-menu-item-icon {
                width: 18px;
                height: 18px;
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 16px;
                flex-shrink: 0;
            }

            .notion-menu-item-content {
                flex: 1;
                display: flex;
                flex-direction: column;
                gap: 2px;
            }

            .notion-menu-item-label {
                font-weight: 500;
                color: inherit;
            }

            .notion-menu-item-description {
                font-size: 12px;
                opacity: 0.6;
                line-height: 1.3;
            }

            .notion-menu-item-shortcut {
                font-size: 12px;
                color: rgba(0, 0, 0, 0.45);
                font-family: 'SF Mono', 'Monaco', 'Inconsolata', 'Roboto Mono', monospace;
                font-weight: 500;
                flex-shrink: 0;
                padding: 2px 6px;
                background: rgba(0, 0, 0, 0.06);
                border-radius: 4px;
            }

            [data-theme="dark"] .notion-menu-item-shortcut,
            .dark .notion-menu-item-shortcut {
                color: rgba(255, 255, 255, 0.5);
                background: rgba(255, 255, 255, 0.1);
            }

            .notion-menu-divider {
                height: 1px;
                background: rgba(0, 0, 0, 0.06);
                margin: 6px 0;
            }

            [data-theme="dark"] .notion-menu-divider,
            .dark .notion-menu-divider {
                background: rgba(255, 255, 255, 0.08);
            }

            .notion-menu-item.danger {
                color: #dc3545;
            }

            .notion-menu-item.danger:hover:not(.disabled) {
                background: rgba(220, 53, 69, 0.1);
                color: #dc3545;
            }

            .notion-submenu-indicator {
                margin-left: auto;
                opacity: 0.5;
                font-size: 12px;
            }

            /* Animation */
            @keyframes notionMenuFadeIn {
                from {
                    opacity: 0;
                    transform: scale(0.95);
                }
                to {
                    opacity: 1;
                    transform: scale(1);
                }
            }

            /* Responsive adjustments */
            @media (max-width: 768px) {
                .notion-context-menu {
                    min-width: 200px;
                    font-size: 16px;
                }

                .notion-menu-item {
                    padding: 12px;
                }
            }
        `;

        document.head.appendChild(styleSheet);
        console.log('ğŸ¨ Notion context menu styles injected');
    }

    registerDefaultSections() {
        // Quick Actions Section (Cut, Copy, Paste)
        this.registerSection('quick-actions', {
            priority: 100,
            condition: () => true,
            items: () => [
                {
                    id: 'cut',
                    label: 'Cut',
                    icon: 'âœ‚ï¸',
                    shortcut: this.getShortcutText('âŒ˜', 'X'),
                    disabled: () => !this.selectionState.hasSelection,
                    action: () => this.executeAction('cut')
                },
                {
                    id: 'copy',
                    label: 'Copy',
                    icon: 'ğŸ“‹',
                    shortcut: this.getShortcutText('âŒ˜', 'C'),
                    disabled: () => !this.selectionState.hasSelection,
                    action: () => this.executeAction('copy')
                },
                {
                    id: 'paste',
                    label: 'Paste',
                    icon: 'ğŸ“„',
                    shortcut: this.getShortcutText('âŒ˜', 'V'),
                    disabled: () => !this.canPaste(),
                    action: () => this.executeAction('paste')
                }
            ]
        });

        // Selection Actions
        this.registerSection('selection-actions', {
            priority: 200,
            condition: () => this.selectionState.hasSelection,
            items: () => [
                {
                    id: 'duplicate',
                    label: 'Duplicate',
                    icon: 'ğŸ“‘',
                    shortcut: this.getShortcutText('âŒ˜', 'D'),
                    action: () => this.executeAction('duplicate')
                },
                {
                    id: 'delete',
                    label: 'Delete',
                    icon: 'ğŸ—‘ï¸',
                    shortcut: 'âŒ«',
                    danger: true,
                    action: () => this.executeAction('delete')
                }
            ]
        });

        // Create Section
        this.registerSection('create', {
            title: 'Create',
            priority: 300,
            condition: () => !this.selectionState.hasSelection,
            items: () => [
                {
                    id: 'text',
                    label: 'Text',
                    description: 'Add a text box',
                    icon: 'ğŸ“',
                    shortcut: 'T',
                    action: (point) => this.createShape('text', point)
                },
                {
                    id: 'rectangle',
                    label: 'Rectangle',
                    description: 'Draw a rectangle',
                    icon: 'â¬œ',
                    shortcut: 'R',
                    action: (point) => this.createShape('rectangle', point)
                },
                {
                    id: 'ellipse',
                    label: 'Circle',
                    description: 'Draw a circle or ellipse',
                    icon: 'â­•',
                    shortcut: 'O',
                    action: (point) => this.createShape('ellipse', point)
                },
                {
                    id: 'arrow',
                    label: 'Arrow',
                    description: 'Draw an arrow',
                    icon: 'â¡ï¸',
                    shortcut: 'A',
                    action: (point) => this.createShape('arrow', point)
                }
            ]
        });

        // Insert Section
        this.registerSection('insert', {
            title: 'Insert',
            priority: 400,
            condition: () => true,
            items: () => [
                {
                    id: 'youtube',
                    label: 'YouTube Video',
                    description: 'Embed a YouTube video',
                    icon: 'ğŸ“º',
                    action: (point) => this.showEmbedModal('youtube', point)
                },
                {
                    id: 'image',
                    label: 'Image',
                    description: 'Upload or embed an image',
                    icon: 'ğŸ–¼ï¸',
                    action: (point) => this.showEmbedModal('image', point)
                },
                {
                    id: 'web',
                    label: 'Web Content',
                    description: 'Embed web content',
                    icon: 'ğŸŒ',
                    action: (point) => this.showEmbedModal('web', point)
                }
            ]
        });

        // Tools Section
        this.registerSection('tools', {
            title: 'Tools',
            priority: 500,
            condition: () => true,
            collapsed: true, // Start collapsed for cleaner UX
            items: () => [
                {
                    id: 'select',
                    label: 'Select Tool',
                    icon: 'â†–ï¸',
                    shortcut: 'V',
                    action: () => this.setTool('select')
                },
                {
                    id: 'draw',
                    label: 'Draw Tool',
                    icon: 'âœï¸',
                    shortcut: 'D',
                    action: () => this.setTool('draw')
                },
                {
                    id: 'hand',
                    label: 'Pan Tool',
                    icon: 'âœ‹',
                    shortcut: 'H',
                    action: () => this.setTool('hand')
                }
            ]
        });

        console.log('ğŸ¨ Default context menu sections registered');
    }

    registerSection(id, config) {
        this.sections.set(id, {
            id,
            title: config.title || null,
            priority: config.priority || 1000,
            condition: config.condition || (() => true),
            items: config.items || (() => []),
            collapsed: config.collapsed || false
        });
    }

    show(event, contextData = {}) {
        console.log('ğŸ¨ Showing Notion context menu at:', event.clientX, event.clientY);

        // Hide any existing menu
        this.hide();

        // Update selection state
        this.updateSelectionState(contextData);

        // Get click point in canvas coordinates
        const clickPoint = this.getCanvasPoint(event);

        // Build menu sections
        const sections = this.buildMenuSections(clickPoint);

        if (sections.length === 0) {
            console.log('ğŸ¨ No menu sections to show');
            return;
        }

        // Create and show menu
        const menu = this.createMenuElement(sections, clickPoint);
        this.positionMenu(menu, event);

        document.body.appendChild(menu);
        this.activeMenu = menu;

        // Add click outside handler
        setTimeout(() => {
            document.addEventListener('click', this.handleClickOutside.bind(this), { once: true });
            document.addEventListener('contextmenu', this.handleClickOutside.bind(this), { once: true });
        }, 0);
    }

    updateSelectionState(contextData) {
        // Get current selection from editor
        const selectedShapes = this.getSelectedShapes();

        this.selectionState = {
            hasSelection: selectedShapes.length > 0,
            selectedShapes: selectedShapes,
            selectionType: this.getSelectionType(selectedShapes),
            clickPoint: contextData.clickPoint || null
        };
    }

    buildMenuSections(clickPoint) {
        const sections = [];

        // Get all sections sorted by priority
        const sortedSections = Array.from(this.sections.values())
            .filter(section => section.condition())
            .sort((a, b) => a.priority - b.priority);

        for (const section of sortedSections) {
            const items = section.items()
                .filter(item => !item.condition || item.condition())
                .map(item => ({
                    ...item,
                    disabled: typeof item.disabled === 'function' ? item.disabled() : item.disabled || false
                }));

            if (items.length > 0) {
                sections.push({
                    ...section,
                    items: items
                });
            }
        }

        return sections;
    }

    createMenuElement(sections, clickPoint) {
        const menu = document.createElement('div');
        menu.className = 'notion-context-menu';
        menu.setAttribute('role', 'menu');

        sections.forEach((section, index) => {
            if (index > 0) {
                const divider = document.createElement('div');
                divider.className = 'notion-menu-divider';
                menu.appendChild(divider);
            }

            const sectionEl = document.createElement('div');
            sectionEl.className = 'notion-context-menu-section';

            if (section.title) {
                const titleEl = document.createElement('div');
                titleEl.className = 'notion-menu-section-title';
                titleEl.textContent = section.title;
                sectionEl.appendChild(titleEl);
            }

            section.items.forEach(item => {
                const itemEl = this.createMenuItem(item, clickPoint);
                sectionEl.appendChild(itemEl);
            });

            menu.appendChild(sectionEl);
        });

        return menu;
    }

    createMenuItem(item, clickPoint) {
        const itemEl = document.createElement('div');
        itemEl.className = `notion-menu-item ${item.disabled ? 'disabled' : ''} ${item.danger ? 'danger' : ''}`;
        itemEl.setAttribute('role', 'menuitem');
        itemEl.setAttribute('data-item-id', item.id);

        // Icon
        if (item.icon) {
            const iconEl = document.createElement('div');
            iconEl.className = 'notion-menu-item-icon';
            iconEl.textContent = item.icon;
            itemEl.appendChild(iconEl);
        }

        // Content (label + description)
        const contentEl = document.createElement('div');
        contentEl.className = 'notion-menu-item-content';

        const labelEl = document.createElement('div');
        labelEl.className = 'notion-menu-item-label';
        labelEl.textContent = item.label;
        contentEl.appendChild(labelEl);

        if (item.description) {
            const descEl = document.createElement('div');
            descEl.className = 'notion-menu-item-description';
            descEl.textContent = item.description;
            contentEl.appendChild(descEl);
        }

        itemEl.appendChild(contentEl);

        // Shortcut
        if (item.shortcut) {
            const shortcutEl = document.createElement('div');
            shortcutEl.className = 'notion-menu-item-shortcut';
            shortcutEl.textContent = item.shortcut;
            itemEl.appendChild(shortcutEl);
        }

        // Click handler
        if (!item.disabled && item.action) {
            itemEl.addEventListener('click', (e) => {
                e.preventDefault();
                e.stopPropagation();
                this.hide();
                item.action(clickPoint);
            });
        }

        return itemEl;
    }

    positionMenu(menu, event) {
        const rect = menu.getBoundingClientRect();
        const viewportWidth = window.innerWidth;
        const viewportHeight = window.innerHeight;

        let x = event.clientX;
        let y = event.clientY;

        // Adjust for viewport boundaries
        if (x + rect.width > viewportWidth) {
            x = viewportWidth - rect.width - 10;
        }

        if (y + rect.height > viewportHeight) {
            y = viewportHeight - rect.height - 10;
        }

        // Ensure minimum distance from edges
        x = Math.max(10, x);
        y = Math.max(10, y);

        menu.style.left = `${x}px`;
        menu.style.top = `${y}px`;
    }

    hide() {
        if (this.activeMenu) {
            this.activeMenu.remove();
            this.activeMenu = null;
        }
    }

    handleClickOutside(event) {
        if (this.activeMenu && !this.activeMenu.contains(event.target)) {
            this.hide();
        }
    }

    bindEvents() {
        // Keyboard shortcuts
        document.addEventListener('keydown', (e) => {
            if ((e.metaKey || e.ctrlKey) && !e.shiftKey && !e.altKey) {
                switch (e.key.toLowerCase()) {
                    case 'x':
                        if (this.selectionState.hasSelection) {
                            e.preventDefault();
                            this.executeAction('cut');
                        }
                        break;
                    case 'c':
                        if (this.selectionState.hasSelection) {
                            e.preventDefault();
                            this.executeAction('copy');
                        }
                        break;
                    case 'v':
                        if (this.canPaste()) {
                            e.preventDefault();
                            this.executeAction('paste');
                        }
                        break;
                    case 'd':
                        if (this.selectionState.hasSelection) {
                            e.preventDefault();
                            this.executeAction('duplicate');
                        }
                        break;
                }
            }
        });

        // Hide menu on escape
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape') {
                this.hide();
            }
        });
    }

    // Utility Methods
    getCanvasPoint(event) {
        const canvas = this.editor.canvas;
        if (!canvas) return { x: 0, y: 0 };

        const rect = canvas.getBoundingClientRect();
        return {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };
    }

    getSelectedShapes() {
        if (this.editor.getSelectedShapes) {
            return this.editor.getSelectedShapes();
        }

        // Fallback to store access
        if (this.editor.store && this.editor.store.query) {
            return this.editor.store.query.selectedShapes();
        }

        return [];
    }

    getSelectionType(shapes) {
        if (shapes.length === 0) return null;
        if (shapes.length === 1) return shapes[0].type;
        return 'multiple';
    }

    canPaste() {
        return this.clipboardSupported || (window.clipboardData && window.clipboardData.getData);
    }

    getShortcutText(modifier, key) {
        const isMac = navigator.platform.toLowerCase().includes('mac');
        const modifierText = isMac ? modifier : modifier.replace('âŒ˜', 'Ctrl+');
        return `${modifierText}${key}`;
    }

    // Action Methods
    executeAction(action) {
        console.log('ğŸ¨ Executing action:', action);

        // CRITICAL FIX: Ensure we have a valid editor reference
        if (!this.editor) {
            console.error('âŒ No editor available for action:', action);
            // Try to find editor globally
            this.editor = window.tlEditor || window.canvasEditor || window.editor;
            if (!this.editor) {
                alert('Canvas editor not available. Please refresh the page.');
                return;
            }
        }

        switch (action) {
            case 'cut':
                this.cutSelection();
                break;
            case 'copy':
                this.copySelection();
                break;
            case 'paste':
                this.pasteContent();
                break;
            case 'duplicate':
                this.duplicateSelection();
                break;
            case 'delete':
                this.deleteSelection();
                break;
            default:
                console.warn('ğŸ¨ Unknown action:', action);
        }
    }

    cutSelection() {
        this.copySelection();
        this.deleteSelection();
    }

    copySelection() {
        if (!this.selectionState.hasSelection) return;

        // Delegate to editor's copy functionality
        if (this.editor.copy) {
            this.editor.copy();
        } else if (this.editor.copyShapes) {
            this.editor.copyShapes(this.selectionState.selectedShapes);
        }
    }

    pasteContent() {
        if (this.editor.paste) {
            this.editor.paste();
        }
    }

    duplicateSelection() {
        if (!this.selectionState.hasSelection) return;

        if (this.editor.duplicate) {
            this.editor.duplicate();
        } else if (this.editor.duplicateShapes) {
            this.editor.duplicateShapes(this.selectionState.selectedShapes);
        }
    }

    deleteSelection() {
        if (!this.selectionState.hasSelection) return;

        if (this.editor.deleteShapes) {
            this.editor.deleteShapes(this.selectionState.selectedShapes);
        } else if (this.editor.delete) {
            this.editor.delete();
        }
    }

    createShape(type, point) {
        console.log('ğŸ¨ Creating shape:', type, 'at:', point);

        // CRITICAL FIX: Proper shape creation with error handling
        try {
            if (!this.editor) {
                this.editor = window.tlEditor || window.canvasEditor || window.editor;
            }

            if (!this.editor) {
                console.error('âŒ No editor available for shape creation');
                alert('Canvas editor not available');
                return;
            }

            // Method 1: Direct shape creation API
            if (this.editor.createShapes) {
                const shapeData = this.getShapeData(type, point);
                console.log('ğŸ¨ Creating shape with data:', shapeData);

                const createdShapes = this.editor.createShapes([shapeData]);
                console.log('âœ… Shape created successfully:', createdShapes);
                return createdShapes;
            }

            // Method 2: Tool-based creation
            if (this.editor.setCurrentTool) {
                console.log('ğŸ¨ Setting tool to:', type);
                this.editor.setCurrentTool(type);

                // Update toolbar UI
                this.updateToolbarUI(type);

                // If we have a point, try to create at that location
                if (point && this.editor.rootState) {
                    this.createShapeAtPoint(type, point);
                }
            }

        } catch (error) {
            console.error('âŒ Shape creation failed:', error);
            alert('Failed to create shape: ' + error.message);
        }
    }

    getShapeData(type, point) {
        const baseData = {
            x: point.x,
            y: point.y
        };

        switch (type) {
            case 'text':
                return {
                    type: 'text',
                    ...baseData,
                    props: {
                        text: 'Type here...',
                        color: 'black',
                        size: 'medium'
                    }
                };
            case 'rectangle':
                return {
                    type: 'geo',
                    ...baseData,
                    props: {
                        geo: 'rectangle',
                        w: 100,
                        h: 60,
                        color: 'black',
                        fill: 'none'
                    }
                };
            case 'ellipse':
                return {
                    type: 'geo',
                    ...baseData,
                    props: {
                        geo: 'ellipse',
                        w: 100,
                        h: 100,
                        color: 'black',
                        fill: 'none'
                    }
                };
            case 'arrow':
                return {
                    type: 'arrow',
                    ...baseData,
                    props: {
                        start: { x: 0, y: 0 },
                        end: { x: 100, y: 0 },
                        color: 'black',
                        arrowheadStart: 'none',
                        arrowheadEnd: 'arrow'
                    }
                };
            default:
                return {
                    type: 'geo',
                    ...baseData,
                    props: {
                        geo: 'rectangle',
                        w: 100,
                        h: 60,
                        color: 'black',
                        fill: 'none'
                    }
                };
        }
    }

    updateToolbarUI(tool) {
        const toolButtons = document.querySelectorAll('.toolbar-btn[data-tool]');
        toolButtons.forEach(btn => {
            if (btn.dataset.tool === tool) {
                btn.classList.add('active');
            } else {
                btn.classList.remove('active');
            }
        });
    }

    createShapeAtPoint(type, point) {
        try {
            // Use the tool system to create shape at specific point
            const toolState = this.editor.rootState;
            if (!toolState) return;

            if (type === 'rectangle' || type === 'ellipse') {
                const geoTool = toolState.geoTool;
                if (geoTool) {
                    const creating = geoTool.children.get('creating');
                    if (creating && creating.createGeoShape) {
                        const shape = creating.createGeoShape(point);
                        creating.currentShape = shape;
                        creating.startPoint = point;
                        // Auto-complete with reasonable size
                        creating.updateShape({ x: point.x + 100, y: point.y + 60 });
                        console.log('âœ… Shape created via tool system');
                    }
                }
            }
        } catch (error) {
            console.error('âŒ Tool-based shape creation failed:', error);
        }
    }

    showEmbedModal(embedType, point) {
        console.log('ğŸ¨ Showing embed modal:', embedType, 'at:', point);

        if (this.editor.showEmbedModal) {
            this.editor.showEmbedModal(embedType, point);
        } else if (window.showEmbedModal) {
            window.showEmbedModal(embedType, point);
        }
    }

    setTool(toolId) {
        console.log('ğŸ¨ Setting tool:', toolId);

        // CRITICAL FIX: Ensure editor reference and proper tool setting
        try {
            if (!this.editor) {
                this.editor = window.tlEditor || window.canvasEditor || window.editor;
            }

            if (!this.editor) {
                console.error('âŒ No editor available for tool setting');
                alert('Canvas editor not available');
                return;
            }

            // Method 1: Direct setCurrentTool
            if (this.editor.setCurrentTool) {
                this.editor.setCurrentTool(toolId);
                console.log('âœ… Tool set via setCurrentTool:', toolId);
            } 
            // Method 2: Tool manager
            else if (this.editor.toolManager && this.editor.toolManager.setTool) {
                this.editor.toolManager.setTool(toolId);
                console.log('âœ… Tool set via toolManager:', toolId);
            }
            // Method 3: Direct property assignment
            else if (this.editor.currentTool !== undefined) {
                this.editor.currentTool = toolId;
                console.log('âœ… Tool set via property:', toolId);
            }

            // Update toolbar UI
            this.updateToolbarUI(toolId);

            // Verify tool was set
            setTimeout(() => {
                const currentTool = this.editor.getCurrentTool ? this.editor.getCurrentTool() : 
                                  this.editor.currentTool || 'unknown';
                console.log('ğŸ¨ Tool verification - expected:', toolId, 'actual:', currentTool);

                if (currentTool !== toolId) {
                    console.warn('âš ï¸ Tool mismatch detected');
                }
            }, 100);

        } catch (error) {
            console.error('âŒ Tool setting failed:', error);
            alert('Failed to set tool: ' + error.message);
        }
    }
}

// Auto-initialize when script loads
if (typeof window !== 'undefined') {
    window.NotionContextMenuSystem = NotionContextMenuSystem;
    console.log('ğŸ¨ Notion Context Menu System class available globally');
}

// Export for module systems
if (typeof module !== 'undefined' && module.exports) {
    module.exports = NotionContextMenuSystem;
}

