    registerSection(id, config) {
        this.sections.set(id, {
            id,
            title: config.title || null,
            priority: config.priority || 1000,
            condition: config.condition || (() => true),
            items: config.items || (() => []),
            collapsed: config.collapsed || false
        });
    }

    show(event, contextData = {}) {
        console.log('ğŸ¨ Showing Notion context menu at:', event.clientX, event.clientY);

        // Hide any existing menu
        this.hide();

        // Update selection state
        this.updateSelectionState(contextData);

        // Get click point in canvas coordinates
        const clickPoint = this.getCanvasPoint(event);

        // Build menu sections
        const sections = this.buildMenuSections(clickPoint);

        if (sections.length === 0) {
            console.log('ğŸ¨ No menu sections to show');
            return;
        }

        // Create and show menu
        const menu = this.createMenuElement(sections, clickPoint);
        this.positionMenu(menu, event);

        document.body.appendChild(menu);
        this.activeMenu = menu;

        // Add click outside handler
        setTimeout(() => {
            document.addEventListener('click', this.handleClickOutside.bind(this), { once: true });
            document.addEventListener('contextmenu', this.handleClickOutside.bind(this), { once: true });
        }, 0);
    }

    updateSelectionState(contextData) {
        // Get current selection from editor
        const selectedShapes = this.getSelectedShapes();

        this.selectionState = {
            hasSelection: selectedShapes.length > 0,
            selectedShapes: selectedShapes,
            selectionType: this.getSelectionType(selectedShapes),
            clickPoint: contextData.clickPoint || null
        };
    }

    buildMenuSections(clickPoint) {
        const sections = [];

        // Get all sections sorted by priority
        const sortedSections = Array.from(this.sections.values())
            .filter(section => section.condition())
            .sort((a, b) => a.priority - b.priority);

        for (const section of sortedSections) {
            const items = section.items()
                .filter(item => !item.condition || item.condition())
                .map(item => ({
                    ...item,
                    disabled: typeof item.disabled === 'function' ? item.disabled() : item.disabled || false
                }));

            if (items.length > 0) {
                sections.push({
                    ...section,
                    items: items
                });
            }
        }

        return sections;
    }

    createMenuElement(sections, clickPoint) {
        const menu = document.createElement('div');
        menu.className = 'notion-context-menu';
        menu.setAttribute('role', 'menu');

        sections.forEach((section, index) => {
            if (index > 0) {
                const divider = document.createElement('div');
                divider.className = 'notion-menu-divider';
                menu.appendChild(divider);
            }

            const sectionEl = document.createElement('div');
            sectionEl.className = 'notion-context-menu-section';

            if (section.title) {
                const titleEl = document.createElement('div');
                titleEl.className = 'notion-menu-section-title';
                titleEl.textContent = section.title;
                sectionEl.appendChild(titleEl);
            }

            section.items.forEach(item => {
                const itemEl = this.createMenuItem(item, clickPoint);
                sectionEl.appendChild(itemEl);
            });

            menu.appendChild(sectionEl);
        });

        return menu;
    }

    createMenuItem(item, clickPoint) {
        const itemEl = document.createElement('div');
        itemEl.className = `notion-menu-item ${item.disabled ? 'disabled' : ''} ${item.danger ? 'danger' : ''}`;
        itemEl.setAttribute('role', 'menuitem');
        itemEl.setAttribute('data-item-id', item.id);

        // Icon
        if (item.icon) {
            const iconEl = document.createElement('div');
            iconEl.className = 'notion-menu-item-icon';
            iconEl.textContent = item.icon;
            itemEl.appendChild(iconEl);
        }

        // Content (label + description)
        const contentEl = document.createElement('div');
        contentEl.className = 'notion-menu-item-content';

        const labelEl = document.createElement('div');
        labelEl.className = 'notion-menu-item-label';
        labelEl.textContent = item.label;
        contentEl.appendChild(labelEl);

        if (item.description) {
            const descEl = document.createElement('div');
            descEl.className = 'notion-menu-item-description';
            descEl.textContent = item.description;
            contentEl.appendChild(descEl);
        }

        itemEl.appendChild(contentEl);

        // Shortcut
        if (item.shortcut) {
            const shortcutEl = document.createElement('div');
            shortcutEl.className = 'notion-menu-item-shortcut';
            shortcutEl.textContent = item.shortcut;
            itemEl.appendChild(shortcutEl);
        }

        // Click handler
        if (!item.disabled && item.action) {
            itemEl.addEventListener('click', (e) => {
                e.preventDefault();
                e.stopPropagation();
                this.hide();
                item.action(clickPoint);
            });
        }

        return itemEl;
    }

    positionMenu(menu, event) {
        const rect = menu.getBoundingClientRect();
        const viewportWidth = window.innerWidth;
        const viewportHeight = window.innerHeight;

        let x = event.clientX;
        let y = event.clientY;

        // Adjust for viewport boundaries
        if (x + rect.width > viewportWidth) {
            x = viewportWidth - rect.width - 10;
        }

        if (y + rect.height > viewportHeight) {
            y = viewportHeight - rect.height - 10;
        }

        // Ensure minimum distance from edges
        x = Math.max(10, x);
        y = Math.max(10, y);

        menu.style.left = `${x}px`;
        menu.style.top = `${y}px`;
    }

    hide() {
        if (this.activeMenu) {
            this.activeMenu.remove();
            this.activeMenu = null;
        }
    }

    handleClickOutside(event) {
        if (this.activeMenu && !this.activeMenu.contains(event.target)) {
            this.hide();
        }
    }

    bindEvents() {
        // Keyboard shortcuts
        document.addEventListener('keydown', (e) => {
            if ((e.metaKey || e.ctrlKey) && !e.shiftKey && !e.altKey) {
                switch (e.key.toLowerCase()) {
                    case 'x':
                        if (this.selectionState.hasSelection) {
                            e.preventDefault();
                            this.executeAction('cut');
                        }
                        break;
                    case 'c':
                        if (this.selectionState.hasSelection) {
                            e.preventDefault();
                            this.executeAction('copy');
                        }
                        break;
                    case 'v':
                        if (this.canPaste()) {
                            e.preventDefault();
                            this.executeAction('paste');
                        }
                        break;
                    case 'd':
                        if (this.selectionState.hasSelection) {
                            e.preventDefault();
                            this.executeAction('duplicate');
                        }
                        break;
                }
            }
        });

        // Hide menu on escape
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape') {
                this.hide();
            }
        });
    }

    // Utility Methods
    getCanvasPoint(event) {
        const canvas = this.editor.canvas;
        if (!canvas) return { x: 0, y: 0 };

        const rect = canvas.getBoundingClientRect();
        return {
            x: event.clientX - rect.left,
            y: event.clientY - rect.top
        };
    }

    getSelectedShapes() {
        if (this.editor.getSelectedShapes) {
            return this.editor.getSelectedShapes();
        }

        // Fallback to store access
        if (this.editor.store && this.editor.store.query) {
            return this.editor.store.query.selectedShapes();
        }

        return [];
    }

    getSelectionType(shapes) {
        if (shapes.length === 0) return null;
        if (shapes.length === 1) return shapes[0].type;
        return 'multiple';
    }

    canPaste() {
        return this.clipboardSupported || (window.clipboardData && window.clipboardData.getData);
    }

    getShortcutText(modifier, key) {
        const isMac = navigator.platform.toLowerCase().includes('mac');
        const modifierText = isMac ? modifier : modifier.replace('âŒ˜', 'Ctrl+');
        return `${modifierText}${key}`;
    }

    // Action Methods
    executeAction(action) {
        console.log('ğŸ¨ Executing action:', action);

        // CRITICAL FIX: Ensure we have a valid editor reference
        if (!this.editor) {
            console.error('âŒ No editor available for action:', action);
            // Try to find editor globally
            this.editor = window.tlEditor || window.canvasEditor || window.editor;
            if (!this.editor) {
                alert('Canvas editor not available. Please refresh the page.');
                return;
            }
        }

        switch (action) {
            case 'cut':
                this.cutSelection();
                break;
            case 'copy':
                this.copySelection();
                break;
            case 'paste':
                this.pasteContent();
                break;
            case 'duplicate':
                this.duplicateSelection();
                break;
            case 'delete':
                this.deleteSelection();
                break;
            default:
                console.warn('ğŸ¨ Unknown action:', action);
        }
    }

    cutSelection() {
        this.copySelection();
        this.deleteSelection();
    }

    copySelection() {
        if (!this.selectionState.hasSelection) return;

        // Delegate to editor's copy functionality
        if (this.editor.copy) {
            this.editor.copy();
        } else if (this.editor.copyShapes) {
            this.editor.copyShapes(this.selectionState.selectedShapes);
        }
    }

    pasteContent() {
        if (this.editor.paste) {
            this.editor.paste();
        }
    }

    duplicateSelection() {
        if (!this.selectionState.hasSelection) return;

        if (this.editor.duplicate) {
            this.editor.duplicate();
        } else if (this.editor.duplicateShapes) {
            this.editor.duplicateShapes(this.selectionState.selectedShapes);
        }
    }

    deleteSelection() {
        if (!this.selectionState.hasSelection) return;

        if (this.editor.deleteShapes) {
            this.editor.deleteShapes(this.selectionState.selectedShapes);
        } else if (this.editor.delete) {
            this.editor.delete();
        }
    }

    createShape(type, point) {
        console.log('ğŸ¨ Creating shape:', type, 'at:', point);

        // CRITICAL FIX: Proper shape creation with error handling
        try {
            if (!this.editor) {
                this.editor = window.tlEditor || window.canvasEditor || window.editor;
            }

            if (!this.editor) {
                console.error('âŒ No editor available for shape creation');
                alert('Canvas editor not available');
                return;
            }

            // Method 1: Direct shape creation API
            if (this.editor.createShapes) {
                const shapeData = this.getShapeData(type, point);
                console.log('ğŸ¨ Creating shape with data:', shapeData);

                const createdShapes = this.editor.createShapes([shapeData]);
                console.log('âœ… Shape created successfully:', createdShapes);
                return createdShapes;
            }

            // Method 2: Tool-based creation
            if (this.editor.setCurrentTool) {
                console.log('ğŸ¨ Setting tool to:', type);
                this.editor.setCurrentTool(type);

                // Update toolbar UI
                this.updateToolbarUI(type);

                // If we have a point, try to create at that location
                if (point && this.editor.rootState) {
                    this.createShapeAtPoint(type, point);
                }
            }

        } catch (error) {
            console.error('âŒ Shape creation failed:', error);
            alert('Failed to create shape: ' + error.message);
        }
    }

    getShapeData(type, point) {
        const baseData = {
            x: point.x,
            y: point.y
        };

        switch (type) {
            case 'text':
                return {
                    type: 'text',
                    ...baseData,
                    props: {
                        text: 'Type here...',
                        color: 'black',
                        size: 'medium'
                    }
                };
            case 'rectangle':
                return {
                    type: 'geo',
                    ...baseData,
                    props: {
                        geo: 'rectangle',
                        w: 100,
                        h: 60,
                        color: 'black',
                        fill: 'none'
                    }
                };
            case 'ellipse':
                return {
                    type: 'geo',
                    ...baseData,
                    props: {
                        geo: 'ellipse',
                        w: 100,
                        h: 100,
                        color: 'black',
                        fill: 'none'
                    }
                };
            case 'arrow':
                return {
                    type: 'arrow',
                    ...baseData,
                    props: {
                        start: { x: 0, y: 0 },
                        end: { x: 100, y: 0 },
                        color: 'black',
                        arrowheadStart: 'none',
                        arrowheadEnd: 'arrow'
                    }
                };
            default:
                return {
                    type: 'geo',
                    ...baseData,
                    props: {
                        geo: 'rectangle',
                        w: 100,
                        h: 60,
                        color: 'black',
                        fill: 'none'
                    }
                };
        }
    }

    updateToolbarUI(tool) {
        const toolButtons = document.querySelectorAll('.toolbar-btn[data-tool]');
        toolButtons.forEach(btn => {
            if (btn.dataset.tool === tool) {
                btn.classList.add('active');
            } else {
                btn.classList.remove('active');
            }
        });
    }

    createShapeAtPoint(type, point) {
        try {
            // Use the tool system to create shape at specific point
            const toolState = this.editor.rootState;
            if (!toolState) return;

            if (type === 'rectangle' || type === 'ellipse') {
                const geoTool = toolState.geoTool;
                if (geoTool) {
                    const creating = geoTool.children.get('creating');
                    if (creating && creating.createGeoShape) {
                        const shape = creating.createGeoShape(point);
                        creating.currentShape = shape;
                        creating.startPoint = point;
                        // Auto-complete with reasonable size
                        creating.updateShape({ x: point.x + 100, y: point.y + 60 });
                        console.log('âœ… Shape created via tool system');
                    }
                }
            }
        } catch (error) {
            console.error('âŒ Tool-based shape creation failed:', error);
        }
    }

    showEmbedModal(embedType, point) {
        console.log('ğŸ¨ Showing embed modal:', embedType, 'at:', point);

        if (this.editor.showEmbedModal) {
            this.editor.showEmbedModal(embedType, point);
        } else if (window.showEmbedModal) {
            window.showEmbedModal(embedType, point);
        }
    }

    setTool(toolId) {
        console.log('ğŸ¨ Setting tool:', toolId);

        // CRITICAL FIX: Ensure editor reference and proper tool setting
        try {
            if (!this.editor) {
                this.editor = window.tlEditor || window.canvasEditor || window.editor;
            }

            if (!this.editor) {
                console.error('âŒ No editor available for tool setting');
                alert('Canvas editor not available');
                return;
            }

            // Method 1: Direct setCurrentTool
            if (this.editor.setCurrentTool) {
                this.editor.setCurrentTool(toolId);
                console.log('âœ… Tool set via setCurrentTool:', toolId);
            } 
            // Method 2: Tool manager
            else if (this.editor.toolManager && this.editor.toolManager.setTool) {
                this.editor.toolManager.setTool(toolId);
                console.log('âœ… Tool set via toolManager:', toolId);
            }
            // Method 3: Direct property assignment
            else if (this.editor.currentTool !== undefined) {
                this.editor.currentTool = toolId;
                console.log('âœ… Tool set via property:', toolId);
            }

            // Update toolbar UI
            this.updateToolbarUI(toolId);

            // Verify tool was set
            setTimeout(() => {
                const currentTool = this.editor.getCurrentTool ? this.editor.getCurrentTool() : 
                                  this.editor.currentTool || 'unknown';
                console.log('ğŸ¨ Tool verification - expected:', toolId, 'actual:', currentTool);

                if (currentTool !== toolId) {
                    console.warn('âš ï¸ Tool mismatch detected');
                }
            }, 100);

        } catch (error) {
            console.error('âŒ Tool setting failed:', error);
            alert('Failed to set tool: ' + error.message);
        }
    }
}

// Auto-initialize when script loads
if (typeof window !== 'undefined') {
    window.NotionContextMenuSystem = NotionContextMenuSystem;
    console.log('ğŸ¨ Notion Context Menu System class available globally');
}

// Export for module systems
if (typeof module !== 'undefined' && module.exports) {
    module.exports = NotionContextMenuSystem;
}

