    border-color: var(--accent-green, #65626F) !important;
    transform: scale(1.05) !important;
}

.theme-btn:hover::before {
    transform: translateX(100%) rotate(360deg);
}

.theme-btn:hover svg {
    transform: rotate(180deg) scale(1.1) !important;
    transition: transform 0.3s cubic-bezier(0.16, 1, 0.3, 1) !important;
}

/* Toolbar Button Enhancements */
.toolbar-btn {
    background: var(--bg-secondary, rgba(30, 35, 40, 0.8)) !important;
    border: 1px solid var(--border-color, rgba(69, 85, 102, 0.3)) !important;
    color: var(--text-primary, rgba(255, 255, 255, 0.8)) !important;
    border-radius: 8px !important;
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    position: relative !important;
    overflow: hidden !important;
    backdrop-filter: blur(8px) !important;
}

.toolbar-btn::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    transition: left 0.3s ease;
}

.toolbar-btn:hover {
    background: var(--bg-tertiary, rgba(44, 52, 64, 0.9)) !important;
    border-color: var(--accent-green, #65626F) !important;
    color: var(--text-primary, #ffffff) !important;
    transform: translateY(-2px) scale(1.05) !important;
    box-shadow: 
        0 4px 16px rgba(0, 0, 0, 0.2),
        inset 0 1px 0 rgba(255, 255, 255, 0.1) !important;
}

.toolbar-btn:hover::before {
    left: 100%;
}

.toolbar-btn.active {
    background: linear-gradient(135deg, var(--accent-green, #65626F), #7a7785) !important;
    border-color: var(--accent-green, #65626F) !important;
    color: var(--text-primary, #ffffff) !important;
    box-shadow: 
        0 4px 16px rgba(101, 98, 111, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.2) !important;
}

.toolbar-btn:active {
    transform: translateY(0) scale(0.95) !important;
    transition: all 0.08s ease !important;
}

/* Navigation Link Enhancements */
.nav-link {
    position: relative !important;
    padding: 8px 16px !important;
    border-radius: 6px !important;
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    font-weight: 500 !important;
}

.nav-link::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    width: 0;
    height: 2px;
    background: var(--accent-green, #65626F);
    border-radius: 1px;
    transform: translateX(-50%);
    transition: width 0.2s ease;
}

.nav-link:hover {
    background: var(--bg-tertiary, rgba(44, 52, 64, 0.4)) !important;
    color: var(--text-primary, #ffffff) !important;
    transform: translateY(-1px) !important;
}

.nav-link:hover::before {
    width: 80%;
}

/* Canvas Title Input Enhancement */
.canvas-title-input {
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    position: relative !important;
}

.canvas-title-input::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--accent-green, #65626F);
    transition: width 0.3s ease;
}

.canvas-title-input:focus {
    transform: scale(1.02) !important;
    box-shadow: 
        0 0 0 3px rgba(101, 98, 111, 0.2),
        0 4px 12px rgba(0, 0, 0, 0.1) !important;
}

.canvas-title-input:focus::before {
    width: 100%;
}

/* User Avatar Enhancement */
.user-menu {
    position: relative !important;
}

.user-avatar {
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    position: relative !important;
    overflow: hidden !important;
}

.user-avatar::after {
    content: '';
    position: absolute;
    top: -2px;
    right: -2px;
    width: 8px;
    height: 8px;
    background: #4CAF50;
    border: 2px solid var(--bg-secondary);
    border-radius: 50%;
    opacity: 0;
    transform: scale(0);
    transition: all 0.2s ease;
}

.user-avatar:hover::after {
    opacity: 1;
    transform: scale(1);
}

/* Logo Enhancement */
.logo {
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    position: relative !important;
    display: inline-block !important;
}

.logo::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, transparent, rgba(101, 98, 111, 0.2), transparent);
    transform: translateX(-100%);
    transition: transform 0.3s ease;
}

.logo:hover {
    transform: scale(1.05) !important;
    text-shadow: 0 0 20px rgba(101, 98, 111, 0.5) !important;
}

.logo:hover::before {
    transform: translateX(100%);
}

/* Canvas Container Enhancement */
.canvas-container {
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    position: relative !important;
}

.canvas-container::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: 2px solid transparent;
    border-radius: inherit;
    transition: all 0.2s ease;
    pointer-events: none;
}

.canvas-container:hover::before {
    border-color: var(--accent-green, #65626F);
    box-shadow: 
        0 0 20px rgba(101, 98, 111, 0.2),
        inset 0 1px 0 rgba(255, 255, 255, 0.05);
}

/* Modal Enhancements */
.modal {
    backdrop-filter: blur(12px) !important;
    animation: modalFadeIn 0.2s cubic-bezier(0.16, 1, 0.3, 1) !important;
}

.modal-content {
    background: var(--bg-secondary, rgba(30, 35, 40, 0.98)) !important;
    border: 1px solid var(--border-color, rgba(69, 85, 102, 0.3)) !important;
    border-radius: 16px !important;
    box-shadow: 
        0 20px 60px rgba(0, 0, 0, 0.4),
        inset 0 1px 0 rgba(255, 255, 255, 0.05) !important;
    animation: modalSlideIn 0.3s cubic-bezier(0.16, 1, 0.3, 1) !important;
    position: relative !important;
}

.modal-content::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
        radial-gradient(circle at 1px 1px, rgba(255,255,255,0.08) 1px, transparent 0);
    background-size: 20px 20px;
    opacity: 0.3;
    pointer-events: none;
    border-radius: inherit;
}

/* Form Input Enhancements */
input[type="text"],
input[type="url"],
input[type="email"],
select,
textarea {
    background: var(--bg-tertiary, rgba(44, 52, 64, 0.6)) !important;
    border: 1px solid var(--border-color, rgba(69, 85, 102, 0.3)) !important;
    color: var(--text-primary, #ffffff) !important;
    border-radius: 8px !important;
    transition: all 0.12s cubic-bezier(0.16, 1, 0.3, 1) !important;
    font-family: 'Inter', system-ui, sans-serif !important;
    position: relative !important;
}

input[type="text"]:focus,
input[type="url"]:focus,
input[type="email"]:focus,
select:focus,
textarea:focus {
    border-color: var(--accent-green, #65626F) !important;
    box-shadow: 
        0 0 0 3px rgba(101, 98, 111, 0.2),
        0 4px 12px rgba(0, 0, 0, 0.1) !important;
    transform: scale(1.01) !important;
    background: var(--bg-tertiary, rgba(44, 52, 64, 0.8)) !important;
}

/* Scrollbar Enhancement */
::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}

::-webkit-scrollbar-track {
    background: var(--bg-secondary, rgba(30, 35, 40, 0.5));
    border-radius: 4px;
}

::-webkit-scrollbar-thumb {
    background: var(--border-color, rgba(69, 85, 102, 0.6));
    border-radius: 4px;
    transition: background 0.2s ease;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--accent-green, #65626F);
}

/* Animation Keyframes */
@keyframes modalFadeIn {
    from {
        opacity: 0;
        backdrop-filter: blur(0px);
    }
    to {
        opacity: 1;
        backdrop-filter: blur(12px);
    }
}

@keyframes modalSlideIn {
    from {
        opacity: 0;
        transform: scale(0.9) translateY(-20px);
    }
    to {
        opacity: 1;
        transform: scale(1) translateY(0);
    }
}

/* Responsive Adjustments */
@media (max-width: 768px) {
    .btn {
        padding: 10px 16px !important;
        font-size: 16px !important;
    }

    .toolbar-btn {
        min-width: 44px !important;
        min-height: 44px !important;
    }

    .nav-link {
        padding: 12px 16px !important;
    }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    .btn,
    .toolbar-btn,
    .nav-link {
        border-width: 2px !important;
    }

    .btn:hover,
    .toolbar-btn:hover,
    .nav-link:hover {
        box-shadow: none !important;
        border-width: 3px !important;
    }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

