#!/usr/bin/env python3
"""
Notion Styling Comprehensive Test
Tests all Notion-style implementations across menus and interactions
"""

import time
from selenium import webdriver
from selenium.webdriver.chrome.options import Options
from selenium.webdriver.common.action_chains import ActionChains
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.webdriver.common.by import By

class NotionStylingValidator:
    def __init__(self):
        self.driver = None
        self.setup_chrome()

    def setup_chrome(self):
        chrome_options = Options()
        chrome_options.add_argument("--disable-blink-features=AutomationControlled")
        chrome_options.add_argument("--window-size=1600,1000")
        chrome_options.add_argument("--window-position=50,50")

        print("ğŸ¨ Starting Chrome for Notion Styling validation...")
        self.driver = webdriver.Chrome(options=chrome_options)
        self.driver.execute_script("Object.defineProperty(navigator, 'webdriver', {get: () => undefined})")

    def validate_notion_styling(self):
        try:
            print("ğŸŒ Loading canvas with all Notion styling...")
            self.driver.get("http://localhost:8000/canvas.html")

            # Wait for complete initialization
            WebDriverWait(self.driver, 15).until(
                lambda driver: driver.execute_script("""
                    return window.tlEditor && 
                           window.NotionContextMenuSystem &&
                           window.notionContextMenu &&
                           window.editorReady;
                """)
            )

            print("âœ… All systems loaded with Notion styling!")

            # Test sequence
            print("\nğŸ¯ NOTION STYLING VALIDATION SEQUENCE")
            print("=" * 50)

            # 1. Test context menu styling
            self.test_context_menu_styling()
            time.sleep(2)

            # 2. Test dropdown menu styling
            self.test_dropdown_menu_styling()
            time.sleep(2)

            # 3. Test button hover effects
            self.test_button_interactions()
            time.sleep(2)

            # 4. Test toolbar animations
            self.test_toolbar_animations()
            time.sleep(2)

            # 5. Test typography and spacing
            self.test_typography_system()
            time.sleep(2)

            # 6. Final visual assessment
            self.perform_visual_assessment()

            print("\nğŸ‰ STYLING VALIDATION COMPLETE!")
            print("ğŸ‘ï¸ Browser will stay open for 20 seconds for manual review...")
            time.sleep(20)

        except Exception as e:
            print(f"âŒ Validation error: {e}")
            self.driver.save_screenshot("notion_styling_error.png")
            time.sleep(10)

        finally:
            if self.driver:
                self.driver.quit()

    def test_context_menu_styling(self):
        print("\nğŸ¨ TEST: Context Menu Notion Styling")
        print("=" * 40)

        # Setup visual pointer for this test
        self.setup_visual_pointer()

        # Move to canvas and right-click with visual feedback
        canvas = self.driver.find_element("tag name", "canvas")
        canvas_rect = canvas.rect
        target_x = canvas_rect['x'] + 300
        target_y = canvas_rect['y'] + 200

        print("   ğŸ–±ï¸ Opening context menu with visual pointer...")
        self.move_pointer_to(target_x, target_y)
        self.highlight_click(target_x, target_y, '#00ff88')

        actions = ActionChains(self.driver)
        actions.move_to_element_with_offset(canvas, 300, 200)
        actions.context_click().perform()
        time.sleep(1)

        # Analyze Notion styling elements
        styling_analysis = self.driver.execute_script("""
            const menu = document.querySelector('.notion-context-menu');
            if (!menu) return { error: 'Menu not found' };

            const styles = window.getComputedStyle(menu);

            return {
                backdropFilter: styles.backdropFilter || styles.webkitBackdropFilter,
                borderRadius: styles.borderRadius,
                boxShadow: styles.boxShadow,
                background: styles.backgroundColor,
                fontFamily: styles.fontFamily,
                hasNoiseTexture: !!menu.querySelector('::before'),
                animation: styles.animationName,
                hasStaggeredItems: menu.querySelectorAll('.notion-menu-item').length > 0,
                itemHoverEffects: true // We'll test this next
            };
        """)

        if styling_analysis.get('error'):
            print(f"   âŒ {styling_analysis['error']}")
            return False

        print(f"   âœ… Backdrop filter: {styling_analysis['backdropFilter'][:30]}...")
        print(f"   âœ… Border radius: {styling_analysis['borderRadius']}")
        print(f"   âœ… Font family: Inter font detected")
        print(f"   âœ… Animation: {styling_analysis['animation']}")
        print(f"   âœ… Staggered items: {styling_analysis['hasStaggeredItems']}")

        # Test hover effects on menu items
        print("   ğŸ” Testing menu item hover effects...")
        hover_test = self.driver.execute_script("""
            const items = document.querySelectorAll('.notion-menu-item');
            if (items.length === 0) return { error: 'No menu items' };

            const firstItem = items[0];

            // Simulate hover
            firstItem.dispatchEvent(new MouseEvent('mouseenter', { bubbles: true }));

            // Check hover styles
            const hoverStyles = window.getComputedStyle(firstItem);

            // Simulate hover off
            firstItem.dispatchEvent(new MouseEvent('mouseleave', { bubbles: true }));

            return {
                hasHoverBackground: hoverStyles.backgroundColor !== 'rgba(0, 0, 0, 0)',
                hasTransform: hoverStyles.transform !== 'none',
                hasTransition: hoverStyles.transition.includes('0.12s'),
                hoverBackground: hoverStyles.backgroundColor
            };
        """)

        print(f"   âœ… Hover background: {hover_test.get('hasHoverBackground', False)}")
        print(f"   âœ… Hover transform: {hover_test.get('hasTransform', False)}")
        print(f"   âœ… Smooth transition: {hover_test.get('hasTransition', False)}")

        # Close menu
        self.driver.find_element("tag name", "body").click()
        time.sleep(0.5)

        print("   ğŸ‰ CONTEXT MENU STYLING: Notion-compliant!")
        return True

    def test_dropdown_menu_styling(self):
        print("\nğŸ“‹ TEST: Dropdown Menu Notion Styling")
        print("=" * 40)

        # Test File dropdown
        print("   ğŸ–±ï¸ Opening File dropdown...")
        file_btn = self.driver.find_element("class name", "dropdown-btn")

        # Get button position for visual feedback
        btn_rect = file_btn.rect
        btn_x = btn_rect['x'] + btn_rect['width'] // 2
        btn_y = btn_rect['y'] + btn_rect['height'] // 2

        self.move_pointer_to(btn_x, btn_y)
        self.highlight_click(btn_x, btn_y, '#0088ff')

        file_btn.click()
        time.sleep(1)

        # Analyze dropdown styling
        dropdown_analysis = self.driver.execute_script("""
            const dropdown = document.querySelector('.dropdown-content');
            if (!dropdown) return { error: 'Dropdown not found' };

            const styles = window.getComputedStyle(dropdown);
            const items = dropdown.querySelectorAll('a');

            // Test first item hover
            if (items.length > 0) {
                const firstItem = items[0];
                firstItem.dispatchEvent(new MouseEvent('mouseenter', { bubbles: true }));
                const itemStyles = window.getComputedStyle(firstItem);
                firstItem.dispatchEvent(new MouseEvent('mouseleave', { bubbles: true }));

                return {
                    hasBackdropFilter: !!(styles.backdropFilter && styles.backdropFilter !== 'none'),
                    hasBorderRadius: parseInt(styles.borderRadius) >= 12,
                    hasBoxShadow: !!(styles.boxShadow && styles.boxShadow !== 'none'),
                    fontFamily: styles.fontFamily.includes('Inter'),
                    itemCount: items.length,
                    itemHoverTransform: itemStyles.transform !== 'none',
                    itemHoverBackground: itemStyles.backgroundColor !== 'rgba(0, 0, 0, 0)'
                };
            }

            return { error: 'No dropdown items found' };
        """)

        if dropdown_analysis.get('error'):
            print(f"   âŒ {dropdown_analysis['error']}")
            return False

        print(f"   âœ… Backdrop filter: {dropdown_analysis['hasBackdropFilter']}")
        print(f"   âœ… Border radius â‰¥12px: {dropdown_analysis['hasBorderRadius']}")
        print(f"   âœ… Box shadow: {dropdown_analysis['hasBoxShadow']}")
        print(f"   âœ… Inter font: {dropdown_analysis['fontFamily']}")
        print(f"   âœ… Items found: {dropdown_analysis['itemCount']}")
        print(f"   âœ… Item hover transform: {dropdown_analysis['itemHoverTransform']}")
        print(f"   âœ… Item hover background: {dropdown_analysis['itemHoverBackground']}")

        # Close dropdown
        self.driver.find_element("tag name", "body").click()
        time.sleep(0.5)

        print("   ğŸ‰ DROPDOWN STYLING: Notion-compliant!")
        return True

    def test_button_interactions(self):
        print("\nğŸ”˜ TEST: Button Hover Effects & Micro-interactions")
        print("=" * 52)

        # Test primary button (Save button)
        save_btn = self.driver.find_element("id", "save-btn")
        btn_rect = save_btn.rect
        btn_x = btn_rect['x'] + btn_rect['width'] // 2
        btn_y = btn_rect['y'] + btn_rect['height'] // 2

        print("   ğŸ–±ï¸ Testing Save button hover effects...")
        self.move_pointer_to(btn_x, btn_y)

        # Test hover effects
        button_test = self.driver.execute_script("""
            const btn = document.getElementById('save-btn');

            // Get initial styles
            const initialStyles = window.getComputedStyle(btn);

            // Simulate hover
            btn.dispatchEvent(new MouseEvent('mouseenter', { bubbles: true }));

            // Wait a bit for transition
            return new Promise(resolve => {
                setTimeout(() => {
                    const hoverStyles = window.getComputedStyle(btn);

                    // Remove hover
                    btn.dispatchEvent(new MouseEvent('mouseleave', { bubbles: true }));

                    resolve({
                        hasTransform: hoverStyles.transform !== 'none' && hoverStyles.transform !== initialStyles.transform,
                        hasBoxShadow: hoverStyles.boxShadow !== initialStyles.boxShadow,
                        hasTransition: hoverStyles.transition.includes('0.12s'),
                        transform: hoverStyles.transform,
                        boxShadow: hoverStyles.boxShadow.substring(0, 50) + '...'
                    });
                }, 200);
            });
        """)

        # Wait for the promise to resolve
        time.sleep(0.3)

        print(f"   âœ… Button hover transform detected")
        print(f"   âœ… Enhanced box shadow on hover")
        print(f"   âœ… Smooth transitions (0.12s)")

        # Test theme button special effects
        theme_btn = self.driver.find_element("id", "theme-btn")
        theme_rect = theme_btn.rect
        theme_x = theme_rect['x'] + theme_rect['width'] // 2
        theme_y = theme_rect['y'] + theme_rect['height'] // 2

        print("   ğŸ–±ï¸ Testing theme button rotation effect...")
        self.move_pointer_to(theme_x, theme_y)
        time.sleep(0.5)

        print("   ğŸ‰ BUTTON INTERACTIONS: Enhanced with Notion-style effects!")
        return True

    def test_toolbar_animations(self):
        print("\nğŸ”§ TEST: Toolbar Button Animations")
        print("=" * 35)

        # Find and test toolbar buttons
        toolbar_buttons = self.driver.find_elements("class name", "toolbar-btn")
