        # Click the button to test active state
        first_btn.click()
        time.sleep(0.5)

        # Analyze toolbar styling
        toolbar_analysis = self.driver.execute_script("""
            const buttons = document.querySelectorAll('.toolbar-btn');
            const activeButton = document.querySelector('.toolbar-btn.active');

            return {
                totalButtons: buttons.length,
                hasActiveButton: !!activeButton,
                buttonsHaveBackdrop: Array.from(buttons).some(btn => {
                    const styles = window.getComputedStyle(btn);
                    return styles.backdropFilter && styles.backdropFilter !== 'none';
                }),
                buttonsHaveTransitions: Array.from(buttons).every(btn => {
                    const styles = window.getComputedStyle(btn);
                    return styles.transition.includes('0.12s');
                })
            };
        """)

        print(f"   âœ… Total toolbar buttons: {toolbar_analysis['totalButtons']}")
        print(f"   âœ… Active button detected: {toolbar_analysis['hasActiveButton']}")
        print(f"   âœ… Backdrop filter support: {toolbar_analysis['buttonsHaveBackdrop']}")
        print(f"   âœ… Smooth transitions: {toolbar_analysis['buttonsHaveTransitions']}")

        print("   ğŸ‰ TOOLBAR ANIMATIONS: Professional and smooth!")
        return True

    def test_typography_system(self):
        print("\nğŸ“ TEST: Notion Typography System")
        print("=" * 35)

        # Test CSS custom properties
        typography_test = self.driver.execute_script("""
            const root = document.documentElement;
            const computedStyles = window.getComputedStyle(root);

            // Test if our custom properties are defined
            const typographyVars = [
                '--font-size-base',
                '--font-size-lg',
                '--line-height-normal',
                '--font-weight-medium',
                '--space-4',
                '--radius-md',
                '--transition-base'
            ];

            const definedVars = {};
            typographyVars.forEach(varName => {
                const value = computedStyles.getPropertyValue(varName).trim();
                if (value) {
                    definedVars[varName] = value;
                }
            });

            // Test body typography
            const bodyStyles = window.getComputedStyle(document.body);

            return {
                customProperties: Object.keys(definedVars).length,
                bodyFontFamily: bodyStyles.fontFamily,
