            "instructions": self.generate_claude_instructions(task_type, context)
        }

        self.pipeline_state["claude_work_assignments"].append(assignment)
        return assignment

    def extract_latest_metrics(self) -> Dict[str, Any]:
        """Extract latest metrics from all components for Claude context"""
        latest_metrics = {}

        for component, output in self.pipeline_state["component_outputs"].items():
            if "metrics" in output:
                latest_metrics[component] = output["metrics"]

        # Add current issue count
        latest_metrics["current_issues"] = self.count_issues_in_latest_report()
        latest_metrics["latest_lint_report"] = str(self.get_latest_lint_report()) if self.get_latest_lint_report() else None

        return latest_metrics

    def execute_claude_guardrail_fixing(self, lint_report_path: Path, max_fixes: int) -> Dict[str, Any]:
        """Execute Active Claude-Guardrail fixing process with real-time validation"""
        self.logger.info("ü§ñ Initializing ACTIVE Claude-Guardrail System...")

        try:
            # Import and initialize the quality patcher as active guardrail
            import sys
            import threading
            import time
            import hashlib
            sys.path.append(str(self.repo_path / "scripts"))
            from claude_quality_patcher import ClaudeQualityPatcher

            # Initialize the active guardrail patcher
            patcher = ClaudeQualityPatcher(
                repo_path=self.repo_path,
                lint_report_path=lint_report_path
            )

            # Start active guardrail session
            session_info = patcher.start_claude_session()

            # Get the fixes that need to be applied
            priority_fixes = patcher.get_priority_fixes()

            if not priority_fixes:
                self.logger.info("‚úÖ No fixes found - codebase is clean!")
                return {"success": True, "fixes_applied": 0, "message": "No fixes needed"}

            self.logger.info(f"üìã Found {len(priority_fixes)} fixes to apply")
            self.logger.info("üõ°Ô∏è ACTIVE GUARDRAIL SYSTEM ENABLED")
            self.logger.info("üîÑ Quality patcher will monitor and validate ALL Claude edits")

            # Initialize active guardrail state
            guardrail_state = {
                "active": True,
                "current_fix": 0,
                "fixes_applied": 0,
                "fixes_failed": 0,
                "validation_errors": [],
                "file_checksums": {},
                "monitoring": True
            }

            # Start file monitoring thread for real-time validation
            def monitor_file_changes():
                """Monitor file changes and validate them in real-time"""
                while guardrail_state["monitoring"]:
                    try:
                        # Check if Claude is making changes to target files
                        for fix_item in priority_fixes[guardrail_state["current_fix"]:guardrail_state["current_fix"]+1]:
                            target_file = patcher.extract_target_file(fix_item.get("fix", {}))
                            if target_file and Path(target_file).exists():
                                current_checksum = self.calculate_file_checksum(target_file)
                                stored_checksum = guardrail_state["file_checksums"].get(target_file)

                                if stored_checksum and current_checksum != stored_checksum:
                                    # File changed - validate the change
                                    self.logger.info(f"üîç GUARDRAIL: Detecting change in {target_file}")
                                    validation_result = self.validate_claude_edit_real_time(
                                        patcher, fix_item, target_file, stored_checksum, current_checksum
                                    )

                                    if not validation_result["valid"]:
                                        self.logger.error(f"üö® GUARDRAIL VIOLATION: {validation_result['error']}")
                                        guardrail_state["validation_errors"].append(validation_result)
                                    else:
                                        self.logger.info(f"‚úÖ GUARDRAIL: Change validated successfully")
                                        guardrail_state["file_checksums"][target_file] = current_checksum

                        time.sleep(0.5)  # Check every 500ms
                    except Exception as e:
                        self.logger.error(f"‚ö†Ô∏è Guardrail monitoring error: {e}")
                        time.sleep(1)

            # Start the active monitoring thread
            monitor_thread = threading.Thread(target=monitor_file_changes, daemon=True)
            monitor_thread.start()
            self.logger.info("üõ°Ô∏è Active guardrail monitoring started")

            # Execute active Claude-Guardrail fixing loop
            return self.execute_active_fixing_loop(patcher, priority_fixes, max_fixes, guardrail_state)

        except Exception as e:
            self.logger.error(f"üí• Active Claude-Guardrail system error: {e}")
            import traceback
            traceback.print_exc()
            return {
                "success": False,
                "error": str(e),
                "message": "Active Claude-Guardrail system encountered an error"
            }

