def validate_claude_edit_real_time(self, patcher, fix_item, target_file) -> Dict[str, Any]:
    """Validate Claude's edit in real-time using quality patcher"""
    try:
        # Use the quality patcher's validation methods
        validation_result = patcher.validate_fix_applied(fix_item, target_file)

