            "details": validation_result
        }

    except Exception as e:
        return {
            "valid": False,
            "error": f"Validation error: {str(e)}",
            "type": "validation_error"
        }

def check_for_hallucinations(self, target_file: str, fix_item: Dict[str, Any]) -> Dict[str, Any]:
    """Check for common Claude hallucination patterns"""
    try:
        with open(target_file, 'r') as f:
            content = f.read()

        # Check for hallucinated imports
        if "import" in content:
            # Look for non-existent or inappropriate imports
            import_lines = [line.strip() for line in content.split('\n') if line.strip().startswith('import') or line.strip().startswith('from')]
