            if any(pattern in content for pattern in ['eval(', 'exec(', 'subprocess.call(', '__import__(']):
                return {"valid": False, "reason": "Security fix introduced new security vulnerability"}

        return {"valid": True, "reason": "No hallucinations detected"}

    except Exception as e:
        return {"valid": True, "reason": f"Could not check for hallucinations: {e}"}

