    def start_active_guardrail_monitoring(self, patcher) -> Dict[str, Any]:
        """Start active guardrail monitoring system"""
        import threading
        import time

        self.logger.info("üõ°Ô∏è Starting active guardrail monitoring...")

        guardrail_state = {
            "monitoring": True,
            "patcher": patcher,
            "current_target_file": None,
            "current_fix_item": None,
            "file_checksums": {},
            "validation_results": [],
            "thread_active": False
        }

        def monitor_changes():
            """Monitor file changes in real-time"""
            guardrail_state["thread_active"] = True
            self.logger.info("üîÑ Guardrail monitoring thread started")

            while guardrail_state["monitoring"]:
                try:
                    if guardrail_state["current_target_file"]:
                        target_file = guardrail_state["current_target_file"]
                        if Path(target_file).exists():
                            current_checksum = self.calculate_file_checksum(target_file)
                            stored_checksum = guardrail_state["file_checksums"].get(target_file)

                            if stored_checksum and current_checksum != stored_checksum:
                                # File changed - validate immediately
                                self.logger.info(f"üîç GUARDRAIL: Change detected in {target_file}")
                                validation_result = self.validate_claude_edit_real_time(
                                    patcher, guardrail_state["current_fix_item"], target_file
                                )

                                guardrail_state["validation_results"].append(validation_result)
                                guardrail_state["file_checksums"][target_file] = current_checksum

                                if not validation_result["valid"]:
                                    self.logger.error(f"üö® GUARDRAIL VIOLATION: {validation_result['error']}")
                                else:
                                    self.logger.info(f"‚úÖ GUARDRAIL: Change validated")

                    time.sleep(0.5)  # Check every 500ms for real-time monitoring
                except Exception as e:
                    self.logger.error(f"‚ö†Ô∏è Guardrail monitoring error: {e}")
                    time.sleep(1)

            guardrail_state["thread_active"] = False
            self.logger.info("üõ°Ô∏è Guardrail monitoring thread stopped")

        # Start monitoring thread
        monitor_thread = threading.Thread(target=monitor_changes, daemon=True)
        monitor_thread.start()

        # Store reference for later control
        self.guardrail_state = guardrail_state

        return guardrail_state

    def setup_fix_specific_guardrail(self, patcher, fix_item, target_file):
        """Setup guardrail for a specific fix"""
        if hasattr(self, 'guardrail_state'):
            self.logger.info(f"üõ°Ô∏è Guardrail now monitoring: {target_file}")
            self.guardrail_state["current_target_file"] = target_file
            self.guardrail_state["current_fix_item"] = fix_item

            # Calculate initial checksum
            if Path(target_file).exists():
                initial_checksum = self.calculate_file_checksum(target_file)
                self.guardrail_state["file_checksums"][target_file] = initial_checksum
                self.logger.info(f"üîí Baseline checksum established for {target_file}")

    def calculate_file_checksum(self, file_path: str) -> str:
        """Calculate file checksum for change detection"""
        import hashlib
        try:
            with open(file_path, 'rb') as f:
                return hashlib.md5(f.read()).hexdigest()
        except Exception:
            return ""

    def validate_claude_edit_real_time(self, patcher, fix_item, target_file) -> Dict[str, Any]:
        """Validate Claude's edit in real-time using quality patcher"""
        try:
            # Use the quality patcher's validation methods
            validation_result = patcher.validate_fix_applied(fix_item, target_file)

            # Additional anti-hallucination checks
            if validation_result.get("syntax_valid", False):
                # Check for common hallucination patterns
                hallucination_check = self.check_for_hallucinations(target_file, fix_item)
                if not hallucination_check["valid"]:
                    return {
                        "valid": False,
                        "error": f"Hallucination detected: {hallucination_check['reason']}",
                        "type": "hallucination_prevention"
                    }

            return {
                "valid": validation_result.get("syntax_valid", False) and validation_result.get("fix_applied", False),
                "error": None if validation_result.get("syntax_valid", False) else "Syntax error or fix not applied",
                "type": "real_time_validation",
                "details": validation_result
            }

        except Exception as e:
            return {
                "valid": False,
                "error": f"Validation error: {str(e)}",
                "type": "validation_error"
            }

    def check_for_hallucinations(self, target_file: str, fix_item: Dict[str, Any]) -> Dict[str, Any]:
        """Check for common Claude hallucination patterns"""
        try:
            with open(target_file, 'r') as f:
                content = f.read()

            # Check for hallucinated imports
            if "import" in content:
                # Look for non-existent or inappropriate imports
                import_lines = [line.strip() for line in content.split('\n') if line.strip().startswith('import') or line.strip().startswith('from')]
                for import_line in import_lines:
                    if any(suspicious in import_line.lower() for suspicious in ['magical', 'auto_', 'claude_', 'ai_']):
                        return {"valid": False, "reason": f"Suspicious import detected: {import_line}"}

            # Check for hallucinated function calls
            fix_type = fix_item.get("fix", {}).get("type", "")
            if fix_type == "security_fix":
                # For security fixes, ensure no new security vulnerabilities introduced
                if any(pattern in content for pattern in ['eval(', 'exec(', 'subprocess.call(', '__import__(']):
                    return {"valid": False, "reason": "Security fix introduced new security vulnerability"}

            return {"valid": True, "reason": "No hallucinations detected"}

        except Exception as e:
            return {"valid": True, "reason": f"Could not check for hallucinations: {e}"}

    def execute_claude_guardrail_fixing(self, lint_report_path: Path, max_fixes: int) -> Dict[str, Any]:
        """Execute Claude-Guardrail fixing process where Claude applies fixes with guardrail validation"""
        self.logger.info("ü§ñ Initializing Claude-Guardrail fixing system...")

        try:
            # Import and initialize the quality patcher as a library (not subprocess)
            import sys
            sys.path.append(str(self.repo_path / "scripts"))
            from claude_quality_patcher import ClaudeQualityPatcher

            # Initialize the patcher
            patcher = ClaudeQualityPatcher(
                repo_path=self.repo_path,
                lint_report_path=lint_report_path
            )

            # Get the fixes that need to be applied
            priority_fixes = patcher.get_priority_fixes()

            if not priority_fixes:
                self.logger.info("‚úÖ No fixes found - codebase is clean!")
                return {"success": True, "fixes_applied": 0, "message": "No fixes needed"}

            self.logger.info(f"üìã Found {len(priority_fixes)} fixes to apply")

            # START ACTIVE GUARDRAIL SYSTEM
            self.logger.info("üõ°Ô∏è ACTIVATING REAL-TIME GUARDRAIL SYSTEM")
            self.logger.info("üîÑ Quality patcher will monitor and validate ALL Claude edits")

            # Initialize active guardrail monitoring
            guardrail_active = self.start_active_guardrail_monitoring(patcher)

            fixes_applied = 0
            fixes_failed = 0

            # Process each fix in the Claude-Guardrail loop with active monitoring
            for i, fix_item in enumerate(priority_fixes[:max_fixes]):
                self.logger.info(f"\nüîß FIX {i+1}/{min(max_fixes, len(priority_fixes))}")
                self.logger.info("="*60)

                # Generate the Claude prompt for this specific fix
                claude_prompt = patcher.generate_claude_fix_prompt(fix_item)

                # Extract target file
                target_file = patcher.extract_target_file(fix_item.get("fix", {}))

                if not target_file:
                    self.logger.error(f"‚ùå Could not identify target file for fix {i+1}")
                    fixes_failed += 1
                    continue

                self.logger.info(f"üìÅ Target file: {target_file}")

                # Set up active guardrail for this specific fix
                self.setup_fix_specific_guardrail(patcher, fix_item, target_file)

                # Present the fix to Claude with clear instructions
                self.logger.info("\n" + "="*80)
                self.logger.info("ü§ñ CLAUDE: APPLY THIS FIX NOW")
                self.logger.info("üõ°Ô∏è ACTIVE GUARDRAIL MONITORING THIS EDIT")
                self.logger.info("="*80)
                self.logger.info(claude_prompt)
                self.logger.info("="*80)
                self.logger.info("‚ö° ACTION REQUIRED:")
                self.logger.info(f"1. Use Read tool to examine: {target_file}")
                self.logger.info("2. Use Edit/MultiEdit tool to apply the fix shown above")
                self.logger.info("3. Active guardrail will validate your changes in REAL-TIME")
                self.logger.info("4. Guardrail will prevent hallucinations and invalid changes")
                self.logger.info("="*80)

                # Create backup before Claude applies the fix
                backup_path = patcher.create_backup(target_file)

                # STOP PIPELINE EXECUTION HERE - Claude must apply the fix with active guardrail
                self.logger.info("\nüõë PIPELINE PAUSED - ACTIVE GUARDRAIL ENGAGED")
                self.logger.info("üëÜ Claude MUST use Write/Edit tools to apply the fix above")
                self.logger.info("üõ°Ô∏è Quality patcher is actively monitoring and will validate changes")
                self.logger.info("üîÑ Pipeline will resume automatically after Claude makes the change")

                # Return control to Claude with clear instructions and active guardrail info
                return {
                    "success": False,  # Not complete yet - waiting for Claude
                    "status": "waiting_for_claude_with_active_guardrail",
                    "message": "Pipeline paused - Claude must apply fix with active guardrail monitoring",
                    "current_fix": i + 1,
                    "total_fixes": min(max_fixes, len(priority_fixes)),
                    "claude_instructions": claude_prompt,
                    "target_file": target_file,
                    "backup_path": backup_path,
                    "fix_item": fix_item,
                    "guardrail_active": guardrail_active,
                    "remaining_fixes": priority_fixes[i+1:max_fixes] if i+1 < max_fixes else []
                }

        except Exception as e:
            self.logger.error(f"üí• Claude-Guardrail system error: {e}")
            import traceback
            traceback.print_exc()
            return {
                "success": False,
                "error": str(e),
                "message": "Claude-Guardrail system encountered an error"
            }

