// Main JavaScript file for the web app

// DOM Content Loaded event listener
document.addEventListener('DOMContentLoaded', function() {
    console.log('My Web App loaded successfully!');

    // Initialize the app
    initializeApp();
});

// Initialize application
function initializeApp() {
    setupEventListeners();
    setupSmoothScrolling();
}

// Setup event listeners
function setupEventListeners() {
    const ctaButton = document.getElementById('cta-button');

    if (ctaButton) {
        ctaButton.addEventListener('click', handleCTAClick);
    }

    // Navigation links
    const navLinks = document.querySelectorAll('nav a[href^="#"]');
    navLinks.forEach(link => {
        link.addEventListener('click', handleNavClick);
    });
}

// Handle CTA button click
function handleCTAClick(event) {
    event.preventDefault();

    // Add button animation
    const button = event.target;
    button.style.transform = 'scale(0.95)';

    setTimeout(() => {
        button.style.transform = '';
        showWelcomeMessage();
    }, 150);
}

// Show welcome message
function showWelcomeMessage() {
    const message = document.createElement('div');
    message.innerHTML = `
        <div style="
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            background: white;
            padding: 2rem;
            border-radius: 10px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.3);
            z-index: 1000;
            text-align: center;
            max-width: 400px;
        ">
            <h3 style="margin-bottom: 1rem; color: #007bff;">Welcome!</h3>
            <p style="margin-bottom: 1rem; color: #666;">Thanks for trying out the web app!</p>
            <button onclick="this.parentElement.parentElement.remove()" 
                    style="background: #007bff; color: white; border: none; padding: 0.5rem 1rem; border-radius: 5px; cursor: pointer;">
                Close
            </button>
        </div>
        <div style="
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: rgba(0,0,0,0.5);
            z-index: 999;
        " onclick="this.parentElement.remove()"></div>
    `;

    document.body.appendChild(message);
}

// Handle navigation clicks
function handleNavClick(event) {
    event.preventDefault();

    const targetId = event.target.getAttribute('href');
    const targetElement = document.querySelector(targetId);

    if (targetElement) {
        targetElement.scrollIntoView({
            behavior: 'smooth',
            block: 'start'
        });
    }
}

// Setup smooth scrolling
function setupSmoothScrolling() {
    // Add smooth scrolling behavior to html element
    document.documentElement.style.scrollBehavior = 'smooth';
}

// Utility function for API calls (example)
async function fetchData(url) {
    try {
        const response = await fetch(url);
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        return await response.json();
    } catch (error) {
        console.error('Fetch error:', error);
        throw error;
    }
}

// Example of a simple animation function
function animateElement(element, animation = 'fadeIn') {
    element.style.animation = `${animation} 0.5s ease-in-out`;
}

// Export functions for testing or external use
if (typeof module !== 'undefined' && module.exports) {
    module.exports = {
        initializeApp,
        fetchData,
        animateElement
    };
}

