const CDP = require('chrome-remote-interface');
const WebSocket = require('ws');
const { EventEmitter } = require('events');
const net = require('net');

class ConnectionPool extends EventEmitter {
    constructor() {
        super();
        this.activeSessions = new Map();
        this.userMessageQueue = [];
        this.chromePort = null;
        this.wsServer = null;
    }

    // Helper to check if a port is available
    _isPortInUse(port) {
        return new Promise((resolve, reject) => {
            const server = net.createServer();
            server.once('error', (err) => {
                if (err.code === 'EADDRINUSE') {
                    resolve(true); // Port is definitely in use
                } else {
                    reject(err); // Some other error occurred
                }
            });
            server.once('listening', () => {
                server.close(() => {
                    resolve(false); // Port is free
                });
            });
            server.listen(port);
        });
    }

    async initialize(chromePort) {
        this.chromePort = chromePort;
        console.log(`ğŸ”— Initializing connection pool for Chrome port ${chromePort}`);

        // Start WebSocket server for Slither connections
        await this.startWebSocketServer();

        console.log('âœ… Connection pool initialized');
    }

    // **FIX**: Check if server is already running
    isServerRunning() {
        return this.wsServer !== null;
    }

    async startWebSocketServer() {
        // **FIX**: Prevent starting a server if one is already running
        if (this.isServerRunning()) {
            console.log(`âœ… WebSocket server is already running on port 9223`);
            return;
        }

        // Use a different port for the WebSocket server
        const wsPort = 9223;

        console.log(`ğŸŒ Starting WebSocket server on port ${wsPort}...`);
        this.wsServer = new WebSocket.Server({ 
            port: wsPort,
            verifyClient: (info) => {
                // Simple verification - could be enhanced with tokens
                return info.req.headers['user-agent']?.includes('Slither') || 
                       info.req.headers['x-slither-session'];
            }
        });

        this.wsServer.on('connection', (ws, req) => {
            const sessionId = this.generateSessionId();
            console.log(`ğŸ”Œ New Slither connection: ${sessionId}`);

            this.activeSessions.set(sessionId, {
                id: sessionId,
                websocket: ws,
                connected: true,
                startTime: Date.now(),
                lastActivity: Date.now()
            });

            // Handle messages from Slither
            ws.on('message', (data) => {
                this.handleSlitherMessage(sessionId, data);
            });

            // Handle disconnect
            ws.on('close', () => {
                console.log(`ğŸ”Œ Slither disconnected: ${sessionId}`);
                this.activeSessions.delete(sessionId);
                this.emit('session-disconnected', sessionId);
            });

            // Send welcome message
            ws.send(JSON.stringify({
                type: 'connection-established',
                sessionId: sessionId,
                chromePort: this.chromePort,
                timestamp: Date.now()
            }));

            this.emit('session-connected', sessionId);
        });

        this.wsServer.on('error', (error) => {
            console.error('âŒ WebSocket Server Error:', error);
            this.wsServer = null; // Reset on error
        });

        this.wsServer.on('close', () => {
            console.log('ğŸšª WebSocket server closed.');
            this.wsServer = null;
        });

        console.log(`ğŸŒ WebSocket server listening on port ${wsPort} for Slither connections`);
    }

    handleSlitherMessage(sessionId, data) {
        try {
            const message = JSON.parse(data);
            console.log(`ğŸ“¨ Message from ${sessionId}:`, message.type);

            const session = this.activeSessions.get(sessionId);
            if (session) {
                session.lastActivity = Date.now();
            }

            switch (message.type) {
                case 'chrome-command':
                    this.handleChromeCommand(sessionId, message);
                    break;

                case 'get-user-messages':
                    this.sendUserMessages(sessionId);
                    break;

                case 'session-status':
                    this.sendSessionStatus(sessionId);
                    break;

                default:
                    console.log(`â“ Unknown message type: ${message.type}`);
            }
        } catch (error) {
            console.error(`âŒ Error handling message from ${sessionId}:`, error);
        }
    }

    async handleChromeCommand(sessionId, message) {
        try {
            // Forward Chrome DevTools Protocol commands
            const client = await CDP({ port: this.chromePort });

            const result = await client.send(message.command, message.params || {});

            await client.close();

            // Send result back to Slither
            this.sendToSession(sessionId, {
                type: 'chrome-command-result',
                requestId: message.requestId,
                result: result
            });

        } catch (error) {
            console.error(`âŒ Chrome command failed:`, error);

            this.sendToSession(sessionId, {
                type: 'chrome-command-error',
                requestId: message.requestId,
                error: error.message
            });
        }
    }

    sendUserMessages(sessionId) {
        const messages = [...this.userMessageQueue];
        this.userMessageQueue = []; // Clear queue after sending

        this.sendToSession(sessionId, {
            type: 'user-messages',
            messages: messages,
            timestamp: Date.now()
        });
    }

    sendSessionStatus(sessionId) {
        const session = this.activeSessions.get(sessionId);

        this.sendToSession(sessionId, {
            type: 'session-status-response',
            status: {
                sessionId: sessionId,
                connected: true,
                startTime: session?.startTime || Date.now(),
                lastActivity: session?.lastActivity || Date.now(),
                chromePort: this.chromePort,
                totalSessions: this.activeSessions.size
            }
        });
    }

    sendToSession(sessionId, message) {
        const session = this.activeSessions.get(sessionId);
        if (session && session.websocket.readyState === WebSocket.OPEN) {
            session.websocket.send(JSON.stringify(message));
        }
    }

    queueUserMessage(message) {
        this.userMessageQueue.push({
            content: message.content,
            type: message.type || 'info',
            urgent: message.urgent || false,
            timestamp: Date.now()
        });

        console.log(`ğŸ’¬ User message queued: ${message.content}`);

        // Notify all active sessions
        this.activeSessions.forEach((session, sessionId) => {
            if (message.urgent) {
                this.sendToSession(sessionId, {
                    type: 'urgent-user-message',
                    message: message
                });
            }
        });
    }

    generateSessionId() {
        return `SLITHER_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    }

    getActiveConnectionCount() {
        return this.activeSessions.size;
    }

    getActiveSessions() {
        const sessions = [];
        this.activeSessions.forEach((session, sessionId) => {
            sessions.push({
                id: sessionId,
                startTime: session.startTime,
                lastActivity: session.lastActivity,
                duration: Date.now() - session.startTime
            });
        });
        return sessions;
    }

    async clearAllSessions() {
        console.log(`ğŸ§¹ Clearing ${this.activeSessions.size} active sessions...`);

        // **FIX**: Properly terminate all client connections
        for (const session of this.activeSessions.values()) {
            try {
                session.websocket.terminate();
            } catch (error) {
                // Ignore errors when terminating connections
            }
        }

        this.activeSessions.clear();
        this.userMessageQueue = [];

        console.log('âœ… All sessions cleared');
    }

    async cleanup() {
        console.log('ğŸ§¹ Cleaning up connection pool...');

        await this.clearAllSessions();

        // **FIX**: Properly close WebSocket server with Promise
        if (this.wsServer) {
            console.log('ğŸ§¹ Shutting down WebSocket server...');
            return new Promise((resolve) => {
                this.wsServer.close(() => {
                    this.wsServer = null;
                    console.log('âœ… WebSocket server cleanup complete.');
                    resolve();
                });
            });
        } else {
            console.log('âœ… Connection pool cleanup complete');
        }
    }

    // Helper method for Slither to connect
    getConnectionInfo() {
        return {
            chromeDebugPort: this.chromePort,
            slitherWebSocketPort: 9223,
            connectionUrl: 'ws://localhost:9223',
            activeConnections: this.activeSessions.size
        };
    }

    isInitialized() {
        return this.chromePort !== null && this.wsServer !== null;
    }
}

module.exports = ConnectionPool;

