// preload.js - Full version with xterm libraries

const { contextBridge, ipcRenderer } = require('electron');

console.log('ğŸ”§ Starting preload script...');

// Xterm libraries are loaded via CDN in the renderer instead
let Terminal = null, FitAddon = null;

// Expose APIs to renderer process
