// Handle DevTools reconnection (new handler)
ipcMain.handle('reconnect-devtools', async (event) => {
    try {
        console.log('ğŸ”— Reconnecting to Chrome DevTools...');

        if (!this.chromeInstance || !this.chromeInstance.port) {
            throw new Error('No Chrome instance available to reconnect to');
        }

        // Close existing CDP connection if it exists
        if (this.cdpClient) {
            try {
                await this.cdpClient.close();
                console.log('ğŸ”´ Closed existing CDP connection');
            } catch (error) {
                // Ignore errors closing old connection
                console.log('âš ï¸ Error closing old CDP connection (ignored):', error.message);
            }
            this.cdpClient = null;
        }

        // Wait for Chrome to be ready
        await this.waitForChromeReady(this.chromeInstance.port, 5000);

        // Re-establish CDP connection
        const connected = await this.connectToChrome(this.chromeInstance.port);

        if (connected) {
            console.log('âœ… DevTools reconnection successful');
            this.updateRendererStatus();
            return { success: true, message: 'DevTools reconnected successfully' };
        } else {
            throw new Error('Failed to establish CDP connection');
        }

    } catch (error) {
        console.error('âŒ DevTools reconnection failed:', error);
        this.updateRendererStatus();
        return { success: false, error: error.message };
    }
});

