// renderer/app.js - FINAL Corrected Version

console.log('ğŸš€ Loading app.js - CDN Terminal Version v13');

// --- 1. GET LIBRARIES AND UI ELEMENTS ---
// Wait for DOM and electronAPI to be available
document.addEventListener('DOMContentLoaded', () => {
    console.log('ğŸ” Checking window.electronAPI availability...');
    console.log('window.electronAPI:', window.electronAPI);
    console.log('Available window properties:', Object.keys(window));

    // Declare terminal variables at higher scope for tab switching access
    let term = null;
    let fitAddon = null;

    if (!window.electronAPI) {
        console.error('âŒ window.electronAPI is not available');
        console.error('This means the preload script failed to load or contextBridge failed');
        alert('FATAL ERROR: Preload script failed to load.\n\nCheck browser console for details.');
        return;
    }

    console.log('âœ… window.electronAPI is available with keys:', Object.keys(window.electronAPI));

    // Test the bridge connection
    if (window.electronAPI.testConnection) {
        console.log('ğŸ”— Bridge test:', window.electronAPI.testConnection());
    }

    // Check if Terminal libraries are available (from global scope or electronAPI)
    // Note: CDN version has FitAddon.FitAddon constructor
    const terminalLibrariesAvailable = (window.Terminal && (window.FitAddon || window.FitAddon?.FitAddon)) || 
                                      (window.electronAPI?.Terminal && window.electronAPI?.FitAddon);
    if (!terminalLibrariesAvailable) {
        console.log('âš ï¸ Terminal libraries not available - checking for globals...');
        console.log('window.Terminal:', typeof window.Terminal);
        console.log('window.FitAddon:', typeof window.FitAddon);
        console.log('window.FitAddon?.FitAddon:', typeof window.FitAddon?.FitAddon);
    }

    // Get all element references from the HTML
    const initializeBtn = document.getElementById('initialize-chrome');
    const restartBtn = document.getElementById('restart-chrome');
    const reconnectDevToolsBtn = document.getElementById('reconnect-devtools');
    const canvasBtn = document.getElementById('navigate-canvas');
    const chromePlaceholder = document.getElementById('chrome-placeholder');
    const chromeContainer = document.querySelector('.chrome-container');
    let chromeViewport = document.getElementById('chrome-viewport'); // Use 'let' to allow reassignment
    const chromeStatusIndicator = document.getElementById('chrome-status');
    const cdpStatus = document.getElementById('cdp-connection');
    const debugPortDisplay = document.getElementById('debug-port');
    const memoryDisplay = document.getElementById('memory-usage');
    const importBtn = document.getElementById('import-md');
    const guidanceContent = document.getElementById('guidance-content');
    const tabButtons = document.querySelectorAll('.tab-btn');
    const tabContents = document.querySelectorAll('.tab-content');
    const terminalContainer = document.getElementById('terminal');

    // Message elements
    const userMessageInput = document.getElementById('user-message');
    const sendMessageBtn = document.getElementById('send-message');
    const queueMessageBtn = document.getElementById('queue-message');
    const urgentMessageBtn = document.getElementById('urgent-message');
    const queuedMessagesContainer = document.getElementById('queued-messages');

    // Error log elements
    const errorLog = document.getElementById('error-log');
    const clearErrorsBtn = document.getElementById('clear-errors');
    const exportErrorsBtn = document.getElementById('export-errors');
    const totalErrorsDisplay = document.getElementById('total-errors');
    const chromeErrorsDisplay = document.getElementById('chrome-errors');
    const appErrorsDisplay = document.getElementById('app-errors');

    // Session management elements
    const clearSessionsBtn = document.getElementById('clear-sessions');
    const refreshStatusBtn = document.getElementById('refresh-status');
    const exportLogsBtn = document.getElementById('export-logs');

    // Performance metrics elements
    const cpuUsageDisplay = document.getElementById('cpu-usage');
    const liveConnectionsDisplay = document.getElementById('live-connections');
    const chromeTabsDisplay = document.getElementById('chrome-tabs');

    // AI tracking elements
    const aiStatusIcon = document.getElementById('ai-status-icon');
    const aiStatusText = document.getElementById('ai-status-text');
    const aiLastAction = document.getElementById('ai-last-action');

    // --- 2. THE CENTRAL UI UPDATE FUNCTION ---
    function updateUI(status) {
        if (!status) return;
        console.log("ğŸ”„ Updating UI with live metrics:", status);

        // --- Update Live Performance Metrics ---
        if (status.cpu !== undefined) {
            if (cpuUsageDisplay) cpuUsageDisplay.textContent = `${status.cpu}%`;
            if (document.getElementById('cpu-bar')) {
                const cpuBar = document.getElementById('cpu-bar');
                cpuBar.style.width = `${status.cpu}%`;
                cpuBar.style.transition = 'width 0.5s ease';
                // Color code based on CPU usage
                if (status.cpu > 80) cpuBar.style.backgroundColor = '#e74c3c';
                else if (status.cpu > 50) cpuBar.style.backgroundColor = '#f39c12';
                else cpuBar.style.backgroundColor = '#27ae60';
            }
        }

        if (status.memory !== undefined) {
            if (memoryDisplay) memoryDisplay.textContent = `${status.memory} MB`;
            if (document.getElementById('memory-bar')) {
                const memoryBar = document.getElementById('memory-bar');
                // Animate the bar, assuming a max of 1024MB for visualization
                const memPercentage = Math.min(100, (status.memory / 1024) * 100);
                memoryBar.style.width = `${memPercentage}%`;
                memoryBar.style.transition = 'width 0.5s ease';
                // Color code based on memory usage
                if (memPercentage > 80) memoryBar.style.backgroundColor = '#e74c3c';
                else if (memPercentage > 50) memoryBar.style.backgroundColor = '#f39c12';
                else memoryBar.style.backgroundColor = '#3498db';
            }
        }

        if (status.chromeTabs !== undefined) {
            if (chromeTabsDisplay) chromeTabsDisplay.textContent = status.chromeTabs;
        }

        if (status.connections !== undefined) {
            if (liveConnectionsDisplay) liveConnectionsDisplay.textContent = status.connections;
        }

        // --- Update Chrome Status (existing logic) ---
        if (status.running) {
            // Chrome is running - SHOW the viewport, HIDE the placeholder
            chromePlaceholder.classList.add('hidden');
            chromeViewport.classList.remove('hidden');

            // Point the webview to the running Chrome instance's DevTools frontend
            // First get the available tabs, then use the DevTools frontend URL
            setupChromeViewport(status.port);

            // Update button states
            initializeBtn.disabled = true;
            restartBtn.disabled = false;
            canvasBtn.disabled = false;

            // Update status text
            chromeStatusIndicator.innerHTML = `
                <span class="status-icon" style="color: #27ae60;">ğŸŸ¢</span>
                <span class="status-text">Chrome: Running (PID: ${status.pid})</span>`;

            cdpStatus.className = 'connection-status connected';
            cdpStatus.textContent = `Port ${status.port}`;
            debugPortDisplay.textContent = status.port;

        } else {
            // Chrome is NOT running - HIDE the viewport, SHOW the placeholder
            chromePlaceholder.classList.remove('hidden');
            chromeViewport.classList.add('hidden');
            chromeViewport.src = 'about:blank'; // Clear the source

            // Update button states
            initializeBtn.disabled = false;
            restartBtn.disabled = true;
            canvasBtn.disabled = true;

            // Update status text
            chromeStatusIndicator.innerHTML = `
                <span class="status-icon" style="color: #e74c3c;">ğŸ”´</span>
                <span class="status-text">${status.error ? 'Error' : 'Not Running'}</span>`;

            cdpStatus.className = 'connection-status disconnected';
            cdpStatus.textContent = 'Disconnected';
            debugPortDisplay.textContent = '--';
        }

        if (memoryDisplay && status.memory) {
            memoryDisplay.textContent = `${status.memory} MB`;
        }
    }

    // --- 3. HELPER FUNCTIONS ---
    function displayMessage(message) {
        if (!queuedMessagesContainer) return;
        const messageEl = document.createElement('div');
        messageEl.className = 'message-item';
        messageEl.innerHTML = `
            <span class="message-time">${new Date().toLocaleTimeString()}</span>
            <span class="message-type">${message.type || 'info'}</span>
            <span class="message-content">${message.content}</span>
        `;
        queuedMessagesContainer.appendChild(messageEl);
    }

    function logAIAction(action, status = 'active') {
        const timestamp = new Date().toLocaleTimeString();

        if (aiStatusIcon) aiStatusIcon.className = `status-dot ${status}`;
        if (aiStatusText) aiStatusText.textContent = action;
        if (aiLastAction) aiLastAction.textContent = `${action} at ${timestamp}`;

        console.log(`ğŸ¤– AI Action: ${action}`);
    }

    function updateErrorCounts(total, chrome, app) {
        if (totalErrorsDisplay) totalErrorsDisplay.textContent = total;
        if (chromeErrorsDisplay) chromeErrorsDisplay.textContent = chrome;
        if (appErrorsDisplay) appErrorsDisplay.textContent = app;
    }

    // Function to add error log entries to the UI
    function addErrorLogEntry(error) {
        console.log('ğŸš¨ addErrorLogEntry called with error:', error);
        console.log('ğŸ” errorLog element:', errorLog);

        if (!errorLog) {
            console.error('âŒ errorLog element not found!');
            return;
        }

        // Remove the "No errors" placeholder if it exists
        const placeholder = errorLog.querySelector('.no-errors');
        if (placeholder) {
            console.log('ğŸ—‘ï¸ Removing no-errors placeholder');
            placeholder.remove();
        }

        const errorItem = document.createElement('div');
        errorItem.className = 'error-item';

        const errorType = error.type || 'error';
        errorItem.classList.add(errorType);

        errorItem.innerHTML = `
            <div class="error-header">
                <span class="error-type ${errorType}">${error.source}</span>
                <span class="error-timestamp">${new Date(error.timestamp).toLocaleTimeString()}</span>
            </div>
            <div class="error-message">${error.message}</div>
        `;

        // Add to the top of the log
        errorLog.insertBefore(errorItem, errorLog.firstChild);
        console.log('âœ… Error log entry added to UI');
    }

    // **THE FIX**: Function to completely recreate the webview element
    function recreateWebview() {
        if (!chromeContainer) return;

        // 1. Completely remove the old webview element from the DOM
        if (chromeViewport) {
            chromeViewport.remove();
        }

        // 2. Create a brand new webview element from scratch
        const newWebview = document.createElement('webview');
        newWebview.id = 'chrome-viewport';
        newWebview.className = 'chrome-webview hidden'; // Start hidden
        newWebview.src = 'about:blank';

        // 3. Append the new, clean webview to the container
        chromeContainer.appendChild(newWebview);

        // 4. Update our global reference to point to the new element
        chromeViewport = newWebview;
        console.log('âœ… Viewport has been fully re-created.');

        // 5. Setup error listeners for the new webview
        setupWebviewListeners();
    }

    // Function to listen for webview console errors
    function setupWebviewListeners() {
        if (!chromeViewport) return;

        // Listen for console messages inside the webview
        chromeViewport.addEventListener('console-message', (e) => {
            console.log('Webview Console:', e.message);

            // Filter for errors (level 3) and report them to the backend
            if (e.level === 3) {
                const error = {
                    source: 'DevTools Viewport',
                    message: e.message
                };
                window.electronAPI.reportWebviewError(error);
            }
        });

        console.log('âœ… Webview error listener attached.');
    }

    async function updateConnectionStatus() {
        if (!window.electronAPI) return;

        try {
            const status = await window.electronAPI.getConnectionStatus();

            if (liveConnectionsDisplay) liveConnectionsDisplay.textContent = status.activeConnections || 0;

            // Update performance metrics if available
            if (status.metrics) {
                if (cpuUsageDisplay) cpuUsageDisplay.textContent = `${status.metrics.cpu || '--'}%`;
                if (chromeTabsDisplay) chromeTabsDisplay.textContent = status.metrics.tabs || '--';
            }

            console.log('ğŸ“Š Connection status updated:', status);

        } catch (error) {
            console.error('Failed to update connection status:', error);
        }
    }

    // --- 4. CHROME VIEWPORT HELPER FUNCTIONS ---
    async function setupChromeViewport(port) {
        try {
            console.log(`ğŸŒ Setting up Chrome viewport for port ${port}`);

            // Get list of available tabs from Chrome DevTools
            const response = await fetch(`http://localhost:${port}/json`);
            if (!response.ok) {
                throw new Error(`Failed to fetch tabs: ${response.status}`);
            }

            const tabs = await response.json();
            console.log(`ğŸ“‹ Found ${tabs.length} Chrome tabs:`, tabs);

            // Find the first tab that's not a DevTools page
            const targetTab = tabs.find(tab => 
                !tab.url.startsWith('devtools://') && 
                !tab.url.startsWith('chrome-extension://') &&
                tab.webSocketDebuggerUrl
            );

            if (targetTab) {
                // Use the DevTools frontend URL for this specific tab
                const devtoolsUrl = `http://localhost:${port}/devtools/inspector.html?ws=${encodeURIComponent(targetTab.webSocketDebuggerUrl.replace('ws://localhost:', ''))}`;
                console.log(`ğŸ”— Loading DevTools URL: ${devtoolsUrl}`);

                if (chromeViewport.src !== devtoolsUrl) {
                    chromeViewport.src = devtoolsUrl;
                }
            } else {
                // Fallback: load the DevTools page list
                const fallbackUrl = `http://localhost:${port}/json`;
                console.log(`ğŸ“‹ Loading DevTools tab list: ${fallbackUrl}`);
                chromeViewport.src = fallbackUrl;
            }

        } catch (error) {
            console.error('âŒ Failed to setup Chrome viewport:', error);
            // Fallback to simple port URL
            const fallbackUrl = `http://localhost:${port}`;
            console.log(`ğŸ”„ Falling back to: ${fallbackUrl}`);
            chromeViewport.src = fallbackUrl;
        }
    }

    // --- 5. DRAG AND DROP HELPER FUNCTIONS ---
    function setupTerminalDragAndDrop(container, terminal) {
        console.log('ğŸ¯ Setting up drag-and-drop for terminal...');

        // Visual feedback styles
        const dropZoneClass = 'terminal-drop-zone';
        const style = document.createElement('style');
        style.textContent = `
            .${dropZoneClass} {
                position: relative;
            }
            .${dropZoneClass}::after {
                content: "ğŸ“ Drop files here to insert paths";
                position: absolute;
                top: 0;
                left: 0;
                right: 0;
                bottom: 0;
                background: rgba(52, 152, 219, 0.9);
                color: white;
                display: flex;
                align-items: center;
                justify-content: center;
                font-family: monospace;
                font-size: 16px;
                font-weight: bold;
                z-index: 1000;
                pointer-events: none;
                border: 3px dashed #fff;
                border-radius: 4px;
            }
        `;
        document.head.appendChild(style);

        // Prevent default drag behaviors on the container
        ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
            container.addEventListener(eventName, preventDefaults, false);
        });

        function preventDefaults(e) {
            e.preventDefault();
            e.stopPropagation();
        }

        // Highlight drop zone when item is dragged over it
        ['dragenter', 'dragover'].forEach(eventName => {
            container.addEventListener(eventName, highlight, false);
        });

        ['dragleave', 'drop'].forEach(eventName => {
            container.addEventListener(eventName, unhighlight, false);
        });

        function highlight() {
            container.classList.add(dropZoneClass);
        }

        function unhighlight() {
            container.classList.remove(dropZoneClass);
        }

        // Handle dropped files
        container.addEventListener('drop', handleDrop, false);

        function handleDrop(e) {
            const dt = e.dataTransfer;
            const files = dt.files;

            handleFiles(files, terminal);
        }

        function handleFiles(files, terminal) {
            if (!files.length) return;

            console.log(`ğŸ“ Processing ${files.length} dropped file(s)`);

            // Process each file
            Array.from(files).forEach((file, index) => {
                const filePath = file.path || file.name;
                console.log(`ğŸ“‚ File ${index + 1}: ${filePath}`);

                // Insert the file path into the terminal with proper escaping
                const escapedPath = escapeShellPath(filePath);

                // Add a space before if not at the start of line, and space after
                const pathToInsert = ` "${escapedPath}" `;

                // Write to terminal (this will appear at cursor position)
                terminal.write(pathToInsert);

                // Also send to backend PTY so it's part of the actual command
                window.electronAPI.writeToTerminal(pathToInsert);
            });

            // Show success feedback
            showDropFeedback(files.length);
        }

        function escapeShellPath(path) {
            // Escape special characters for shell safety
            return path.replace(/(["\s'$`\\])/g, '\\$1');
        }

        function showDropFeedback(fileCount) {
            // Create temporary feedback message
            const feedback = document.createElement('div');
            feedback.style.cssText = `
                position: fixed;
                top: 20px;
                right: 20px;
                background: #27ae60;
                color: white;
                padding: 10px 20px;
                border-radius: 4px;
                font-family: monospace;
                font-size: 14px;
                z-index: 10000;
                box-shadow: 0 2px 8px rgba(0,0,0,0.3);
                transition: opacity 0.3s ease;
            `;
            feedback.textContent = `âœ… Inserted ${fileCount} file path${fileCount > 1 ? 's' : ''}`;

            document.body.appendChild(feedback);

            // Fade out and remove after 3 seconds
            setTimeout(() => {
                feedback.style.opacity = '0';
                setTimeout(() => {
                    if (feedback.parentNode) {
                        feedback.parentNode.removeChild(feedback);
                    }
                }, 300);
            }, 3000);
        }

        console.log('âœ… Terminal drag-and-drop setup complete');
    }

    // --- 6. SETUP EVENT LISTENERS ---

    // Main listener for backend state changes
    window.electronAPI.onChromeStatusUpdate(updateUI);

    // --- Button Click Handlers ---
    if (initializeBtn) {
        initializeBtn.addEventListener('click', async () => {
            console.log('ğŸš€ Initialize Chrome clicked');
            initializeBtn.disabled = true;
            logAIAction('Initializing Chrome');

            try {
                const result = await window.electronAPI.initializeChrome();
                console.log('Initialize result:', result);
            } catch (error) {
                console.error('Initialize error:', error);
            }
        });
    }

    if (restartBtn) {
        restartBtn.addEventListener('click', async () => {
            logAIAction('Restarting Chrome');
            restartBtn.disabled = true;

            // **THE FIX**: Re-create the webview element to ensure a clean session.
            recreateWebview();

            // Now, tell the backend to restart its process.
            await window.electronAPI.restartChrome();
        });
    }

    if (reconnectDevToolsBtn) {
        reconnectDevToolsBtn.addEventListener('click', async () => {
            console.log('ğŸ”— Reconnect DevTools button clicked - starting call chain');
            console.log('ğŸ” Checking if electronAPI.reconnectDevTools exists:', typeof window.electronAPI.reconnectDevTools);
            reconnectDevToolsBtn.disabled = true;
            logAIAction('Reconnecting DevTools');

            try {
                console.log('ğŸ“ Calling window.electronAPI.reconnectDevTools()...');
                const result = await window.electronAPI.reconnectDevTools();
                console.log('ğŸ“¥ Received result:', result);
                if (result.success) {
                    logAIAction('DevTools reconnected successfully');
                } else {
                    logAIAction(`DevTools reconnection failed: ${result.error}`);
                }
            } catch (error) {
                console.error('âŒ Reconnect DevTools error:', error);
                logAIAction(`DevTools reconnection error: ${error.message}`);
            }

            reconnectDevToolsBtn.disabled = false;
        });
    }

    if (canvasBtn) {
        canvasBtn.addEventListener('click', async () => {
            console.log('ğŸ¨ Navigate to Canvas clicked');
            logAIAction('Navigating to Canvas');

            try {
                const result = await window.electronAPI.navigateCanvas();
                console.log('Canvas navigation result:', result);
            } catch (error) {
                console.error('Canvas navigation error:', error);
            }
        });
    }

    if (importBtn) {
        importBtn.addEventListener('click', async () => {
            const result = await window.electronAPI.importMdFile();
            if (result.success && result.files.length > 0) {
                guidanceContent.value = result.files[0].content;
            }
        });
    }

    // --- Message Handling ---
    if (sendMessageBtn && userMessageInput) {
        sendMessageBtn.addEventListener('click', async () => {
            const content = userMessageInput.value.trim();
            if (content) {
                const message = { type: 'user', content, timestamp: Date.now() };
                await window.electronAPI.sendMessage(message);
                displayMessage(message);
                userMessageInput.value = '';
                logAIAction('Sent user message');
            }
        });
    }

    if (queueMessageBtn && userMessageInput) {
        queueMessageBtn.addEventListener('click', async () => {
            const content = userMessageInput.value.trim();
            if (content) {
                const message = { type: 'queued', content, timestamp: Date.now() };
                await window.electronAPI.queueUserMessage(message);
                displayMessage(message);
                userMessageInput.value = '';
                logAIAction('Queued user message');
            }
        });
    }

    if (urgentMessageBtn && userMessageInput) {
        urgentMessageBtn.addEventListener('click', async () => {
            const content = userMessageInput.value.trim();
            if (content) {
                const message = { type: 'urgent', content, timestamp: Date.now(), urgent: true };
                await window.electronAPI.queueUserMessage(message);
                displayMessage(message);
                userMessageInput.value = '';
                logAIAction('Sent urgent message');
            }
        });
    }

    // --- Error Log Handling ---
    if (clearErrorsBtn && errorLog) {
        clearErrorsBtn.addEventListener('click', () => {
            errorLog.innerHTML = '<div class="no-errors"><span class="no-errors-icon">âœ…</span><p>No errors detected</p></div>';
            updateErrorCounts(0, 0, 0);
        });
    }

    if (exportErrorsBtn) {
        exportErrorsBtn.addEventListener('click', async () => {
            const result = await window.electronAPI.exportLogs();
            if (result.success) {
                logAIAction(`Exported error logs to ${result.filePath}`);
            }
        });
    }

    // --- Session Management ---
    if (clearSessionsBtn) {
        clearSessionsBtn.addEventListener('click', async () => {
            await window.electronAPI.clearSessions();
            await updateConnectionStatus();
            logAIAction('Cleared all sessions');
        });
    }

    if (refreshStatusBtn) {
        refreshStatusBtn.addEventListener('click', async () => {
            await updateConnectionStatus();
            const chromeStatus = await window.electronAPI.getChromeStatus();
            updateUI(chromeStatus);
            logAIAction('Refreshed status');
        });
    }

    if (exportLogsBtn) {
        exportLogsBtn.addEventListener('click', async () => {
            const result = await window.electronAPI.exportLogs();
            if (result.success) {
                logAIAction(`Exported logs to ${result.filePath}`);
            }
        });
    }

    // --- Tab Switching Logic ---
    tabButtons.forEach(button => {
        button.addEventListener('click', () => {
            const tabName = button.dataset.tab;
            tabButtons.forEach(btn => btn.classList.remove('active'));
            tabContents.forEach(content => content.classList.remove('active'));
            button.classList.add('active');
            document.getElementById(`${tabName}-tab`).classList.add('active');

            // If the terminal tab was just made visible, focus it and resize it
            if (tabName === 'terminal' && term && fitAddon) {
                // This setTimeout is a small hack to ensure the element is fully
                // visible before we try to focus and fit it
                setTimeout(() => {
                    term.focus();
                    fitAddon.fit();
                    console.log('ğŸ¯ Terminal focused and resized');
                }, 0);
            }
        });
    });

    // --- REAL, LIVE TERMINAL SETUP ---
    if (terminalContainer) {
        if (terminalLibrariesAvailable) {
            console.log('ğŸ”§ Setting up real terminal with libraries from electronAPI...');

            // Debug what's available
            console.log('electronAPI.Terminal:', window.electronAPI.Terminal);
            console.log('electronAPI.FitAddon:', window.electronAPI.FitAddon);

            // Use Terminal classes from electronAPI (loaded from node_modules)
            const Terminal = window.electronAPI.Terminal;
            const FitAddon = window.electronAPI.FitAddon;

            console.log('Using Terminal:', Terminal);
            console.log('Using FitAddon:', FitAddon);

            term = new Terminal({
                cursorBlink: true,
                fontFamily: "Menlo, 'DejaVu Sans Mono', Consolas, 'Lucida Console', monospace",
                fontSize: 13,
                theme: {
                    background: '#1e1e1e',
                    foreground: '#d4d4d4',
                    cursor: '#cccccc',
                    selection: '#ffffff40',
                    black: '#000000',
                    red: '#cd3131',
                    green: '#0dbc79',
                    yellow: '#e5e510',
                    blue: '#2472c8',
                    magenta: '#bc3fbc',
                    cyan: '#11a8cd',
                    white: '#e5e5e5',
                    brightBlack: '#666666',
                    brightRed: '#f14c4c',
                    brightGreen: '#23d18b',
                    brightYellow: '#f5f543',
                    brightBlue: '#3b8eea',
                    brightMagenta: '#d670d6',
                    brightCyan: '#29b8db',
                    brightWhite: '#ffffff'
                },
                cols: 80,
                rows: 24,
                scrollback: 1000,
                convertEol: true,
                allowTransparency: false
            });

            fitAddon = new FitAddon();
            term.loadAddon(fitAddon);
            term.open(terminalContainer);

            // Ensure container is visible before fitting
            setTimeout(() => {
                fitAddon.fit();
                const dims = fitAddon.proposeDimensions();
                if (dims && window.electronAPI) {
                    window.electronAPI.resizeTerminal(dims.cols, dims.rows);
                }
                console.log('ğŸ¯ Terminal fitted with dimensions:', dims);
            }, 100);

            // Enhanced resize handling with debouncing
            let resizeTimeout;
            window.addEventListener('resize', () => {
                clearTimeout(resizeTimeout);
                resizeTimeout = setTimeout(() => {
                    if (fitAddon && term) {
                        try {
                            fitAddon.fit();
                            const dims = fitAddon.proposeDimensions();
                            if (dims && window.electronAPI) {
                                window.electronAPI.resizeTerminal(dims.cols, dims.rows);
                            }
                            console.log('ğŸ”„ Terminal resized to:', dims);
                        } catch (error) {
                            console.warn('âš ï¸ Terminal resize failed:', error);
                        }
                    }
                }, 100);
            });

            // Pipe data from backend to terminal UI
            window.electronAPI.onTerminalData(data => {
                console.log('ğŸ“¥ Terminal received data:', data);
                term.write(data);
            });

            // Pipe data from terminal UI to backend
            term.onData(data => {
                console.log('ğŸ“¤ Terminal sending data:', data);
                window.electronAPI.writeToTerminal(data);
            });

            // Initial prompt request - send newline to trigger bash prompt
            setTimeout(() => {
                console.log('ğŸ”„ Requesting initial bash prompt...');
                window.electronAPI.writeToTerminal('\n');
            }, 500);

            console.log('âœ… Real, live terminal initialized.');

            // Add drag-and-drop functionality to terminal
            setupTerminalDragAndDrop(terminalContainer, term);

            // Give initial focus to terminal since it's the active tab
            term.focus();
        } else {
            console.log('âš ï¸ Terminal libraries not available - showing placeholder');
            terminalContainer.innerHTML = `
                <div style="
                    display: flex; 
                    align-items: center; 
                    justify-content: center; 
                    height: 100%; 
                    color: #888; 
                    font-family: monospace;
                    background: #1e1e1e;
                ">
                    Terminal libraries loading... Please restart the app.
                </div>
            `;
        }
    } else {
        console.error("âŒ Terminal container div not found!");
    }

    // --- Initial Status Check ---
    console.log("ğŸ” Requesting initial Chrome status...");
    window.electronAPI.getChromeStatus().then(updateUI);
    updateConnectionStatus();

    // Set up periodic updates
    setInterval(updateConnectionStatus, 5000); // Update every 5 seconds

    // Setup webview error listeners for initial webview
    setupWebviewListeners();

    // **ADD THIS LISTENER FOR ERROR LOGS**
    console.log('ğŸ”§ Setting up error log listener...');
    console.log('ğŸ” Checking if onNewErrorLog exists:', typeof window.electronAPI.onNewErrorLog);
    console.log('ğŸ” Full electronAPI object:', Object.keys(window.electronAPI));

    // **TEST**: Verify IPC bridge is actually working
    setTimeout(() => {
        console.log('ğŸ§ª Testing basic IPC bridge...');
        console.log('ğŸ” testConnection result:', window.electronAPI.testConnection());
    }, 1000);

    if (window.electronAPI.onNewErrorLog) {
        window.electronAPI.onNewErrorLog((error) => {
            console.log('ğŸ“¥ Received new error log:', error);
            addErrorLogEntry(error);
        });
        console.log('âœ… Error log listener setup complete');
    } else {
        console.error('âŒ onNewErrorLog method not available in electronAPI');
    }

    // **MANUAL TEST**: Add a test error after 5 seconds
    setTimeout(() => {
        console.log('ğŸ§ª Adding manual test error...');
        const testError = {
            source: 'Manual Test',
            message: 'This is a manual test error to verify UI display works',
            timestamp: new Date().toISOString(),
            type: 'error'
        };
        addErrorLogEntry(testError);
    }, 5000);

    console.log('âœ… App.js initialization complete with all UI connections');
});

console.log('ğŸ“ App.js loaded - waiting for DOMContentLoaded...');

