forceKillAllConnections() {
    console.log('ğŸš¨ FORCE KILL: Terminating all WebSocket connections');

    // Kill all client sessions
    this.activeSessions.forEach((session, sessionId) => {
        console.log(`ğŸ”¥ Force killing session: ${sessionId}`);
        try {
            session.websocket.terminate(); // Immediate termination
        } catch (error) {
            console.log(`âš ï¸ Error force killing session ${sessionId}:`, error.message);
        }
