    // Clear sessions map
    this.activeSessions.clear();

    // Kill server if it exists
    if (this.wsServer) {
        console.log('ğŸ”¥ Force killing WebSocket server');
        this.wsServer.clients.forEach((ws) => {
            try {
                ws.terminate();
            } catch (error) {
                console.log('âš ï¸ Error terminating client:', error.message);
            }
        });

        // Force close server without graceful shutdown
        try {
            this.wsServer.close();
        } catch (error) {
            console.log('âš ï¸ Error closing server:', error.message);
        }
        this.wsServer = null;
    }

    console.log('âœ… Force kill complete');
}

// **NEW**: Switch to a new WebSocket server (restart functionality)
async switchToNewServer() {
    console.log('ğŸ”„ Switching to new WebSocket server...');

    // Force kill existing connections
    this.forceKillAllConnections();

    // Wait a moment for cleanup
    await new Promise(resolve => setTimeout(resolve, 500));

    // Start new server
    await this.startWebSocketServer();

    console.log('âœ… WebSocket server switch complete');
}

