const { app, BrowserWindow, ipcMain, dialog } = require('electron');
const path = require('path');
const os = require('os');
const pty = require('node-pty');
const CDP = require('chrome-remote-interface');
const NativeChromeManager = require('./chrome-manager/native-chrome-launcher');
const ConnectionPool = require('./chrome-manager/connection-pool');

// Enforce single instance of the application
const gotTheLock = app.requestSingleInstanceLock();

if (!gotTheLock) {
  app.quit();
} else {
  app.on('second-instance', (event, commandLine, workingDirectory) => {
    // Someone tried to run a second instance, we should focus our window.
    if (containerApp.mainWindow) {
      if (containerApp.mainWindow.isMinimized()) containerApp.mainWindow.restore();
      containerApp.mainWindow.focus();
    }
  });
}

// Global error handlers to prevent crashes from EPIPE and other stream errors
process.on('uncaughtException', (error) => {
    if (error.code === 'EPIPE' || error.errno === 'EPIPE') {
        // Ignore EPIPE errors (broken pipe to console/stdout)
        console.error('âš ï¸ EPIPE error ignored:', error.message);
        return;
    }
    // Log other uncaught exceptions but don't crash
    console.error('âŒ Uncaught Exception:', error);
});

process.on('unhandledRejection', (reason, promise) => {
    console.error('âŒ Unhandled Rejection at:', promise, 'reason:', reason);
});

// Safe logging utility to prevent EPIPE errors
function safeLog(...args) {
    try {
        console.log(...args);
    } catch (error) {
        // Ignore EPIPE and other console write errors
    }
}

function safeError(...args) {
    try {
        console.error(...args);
    } catch (error) {
        // Ignore EPIPE and other console write errors
    }
}

class SlitherChromeContainer {
    constructor() {
        this.mainWindow = null;
        this.chromeLauncher = new NativeChromeManager();
        this.connectionPool = new ConnectionPool();
        this.chromeInstance = null;
        this.messageQueue = [];
        this.ptyProcess = null; // Real terminal process
        this.metricsInterval = null; // Live metrics timer
        this.cdpClient = null; // CDP connection client
    }

    async createMainWindow() {
        this.mainWindow = new BrowserWindow({
            width: 1400,
            height: 900,
            webPreferences: {
                preload: path.join(__dirname, 'preload.js'),
                contextIsolation: true,  // Secure by default
                nodeIntegration: false,  // Secure by default
                webviewTag: true         // Enable <webview> for Chrome DevTools
            },
            icon: path.join(__dirname, 'assets', 'icon.png'),
            title: 'Slither Chrome Container v1.0'
        });

        await this.mainWindow.loadFile('renderer/index.html');

        // Force hard refresh to load new terminal interface - clear ALL cache
        this.mainWindow.webContents.session.clearCache();
        this.mainWindow.webContents.reloadIgnoringCache();

        // Enable dev tools to debug preload issues
        this.mainWindow.webContents.openDevTools();

        // Handle window closed - but don't cleanup, container should stay running
        this.mainWindow.on('closed', () => {
            console.log('ğŸ”„ Main window closed, but container continues running...');
            this.mainWindow = null;
        });
    }

    // Wait for Chrome to be ready before accepting connections
    async waitForChromeReady(port, timeout = 10000) {
        const startTime = Date.now();
        while (Date.now() - startTime < timeout) {
            try {
                const response = await fetch(`http://localhost:${port}/json/version`);
                if (response.ok) {
                    console.log(`âœ… Chrome on port ${port} is ready.`);
                    return true;
                }
            } catch (error) {
                // Ignore connection errors and retry
            }
            // Wait 200ms before trying again
            await new Promise(resolve => setTimeout(resolve, 200));
        }
        throw new Error(`Chrome on port ${port} did not become ready in time.`);
    }

    async connectToChrome(port) {
        if (this.cdpClient) {
            try {
                await this.cdpClient.close();
            } catch (e) {
                // Ignore errors closing old client
            }
            this.cdpClient = null;
        }

        try {
            console.log(`ğŸ”— Connecting to Chrome on port ${port} using CDP...`);
            // Connect to the first available target (usually the primary page)
            this.cdpClient = await CDP({ port });

            // Enable Page and Runtime domains for commands
            await Promise.all([
                this.cdpClient.Page.enable(),
                this.cdpClient.Runtime.enable(),
                this.cdpClient.Target.setDiscoverTargets({ discover: true })
            ]);

            // Listen for disconnection events
            this.cdpClient.on('disconnect', () => {
                console.log('ğŸ”´ CDP client disconnected.');
                this.cdpClient = null;
                this.updateRendererStatus();
            });

            // **ADD THIS BLOCK TO LISTEN FOR BACKEND ERRORS**
            this.cdpClient.on('error', (err) => {
                console.error('ğŸ”´ CDP Client Error:', err);
                const errorObject = {
                    source: 'CDP Backend',
                    message: err.message,
                    timestamp: new Date().toISOString(),
                    type: 'error'
                };
                if (this.mainWindow && !this.mainWindow.isDestroyed()) {
                    this.mainWindow.webContents.send('new-error-log', errorObject);
                }
            });

            console.log('âœ… CDP connection successful.');
            return true;
        } catch (error) {
            console.error('âŒ CDP connection failed:', error);
            this.cdpClient = null;
            return false;
        }
    }

    async initializeChrome() {
        // **ANTI-GHOSTING FIX**: Always kill any lingering instances before starting.
        console.log('ğŸ§¹ Checking for and killing any lingering Chrome instances...');
        if (this.chromeInstances && this.chromeInstances.length > 0) {
            const instancesToKill = [...this.chromeInstances];
            for (const instance of instancesToKill) {
                await this.chromeLauncher.killChromeInstance(instance);
            }
            await new Promise(resolve => setTimeout(resolve, 500)); // Give processes time to die
            this.chromeInstances = [];
            this.chromeInstance = null;
            console.log('âœ… Lingering instances cleaned up.');
        }

        try {
            console.log('ğŸš€ Initializing a new Chrome instance...');
            if (!this.chromeInstances) this.chromeInstances = [];

            const chromeResult = await this.chromeLauncher.launchChrome();
            if (!chromeResult.success) throw new Error(chromeResult.error || 'Failed to launch Chrome');

            const newInstance = chromeResult.chromeInstance;
            await this.waitForChromeReady(newInstance.port);

            this.chromeInstances.push(newInstance);
            if (!this.chromeInstance) this.chromeInstance = newInstance;
            if (!this.connectionPool.isInitialized()) await this.connectionPool.initialize(this.chromeInstance.port);

            // Establish CDP connection for stable WebSocket communication
            await this.connectToChrome(newInstance.port);

            console.log(`âœ… Chrome instance #${this.chromeInstances.length} launched on port: ${newInstance.port} (PID: ${newInstance.pid})`);

            newInstance.process.on('exit', (code) => {
                console.log(`ğŸ”´ Chrome instance with PID ${newInstance.pid} exited with code: ${code}.`);
                this.chromeInstances = this.chromeInstances.filter(inst => inst.pid !== newInstance.pid);
                if (this.chromeInstance && this.chromeInstance.pid === newInstance.pid) {
                    this.chromeInstance = this.chromeInstances[0] || null;
                }
                this.updateRendererStatus();
            });

            this.updateRendererStatus();
            return { success: true };
        } catch (error) {
            console.error('âŒ Failed to initialize Chrome:', error);
            this.updateRendererStatus({ error: error.message });
            return { success: false, error: error.message };
        }
    }

    setupIPC() {
        console.log('ğŸ”Œ Setting up IPC handlers...');

        // Handle get chrome status request (register this first)
        console.log('ğŸ“ Registering handler: get-chrome-status');
        ipcMain.handle('get-chrome-status', async (event) => {
            try {
                const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
                const status = {
                    running: isRunning,
                    pid: this.chromeInstance?.pid || null,
                    port: this.chromeInstance?.port || null,
                    instanceCount: this.chromeInstances?.length || 0,
                    memory: Math.round(process.memoryUsage().heapUsed / 1024 / 1024)
                };

                safeLog('ğŸ“¡ Sending chrome status:', status);
                return status;
            } catch (error) {
                console.error('âŒ Error getting chrome status:', error);
                return {
                    running: false,
                    error: error.message,
                    instanceCount: 0
                };
            }
        });

        // Handle DevTools reconnection (full implementation)
        console.log('ğŸ“ Registering handler: reconnect-devtools');
        ipcMain.handle('reconnect-devtools', async (event) => {
            try {
                console.log('ğŸ”— Reconnecting to Chrome DevTools...');

                if (!this.chromeInstance || !this.chromeInstance.port) {
                    throw new Error('No Chrome instance available to reconnect to');
                }

                // Close existing CDP connection if it exists
                if (this.cdpClient) {
                    try {
                        await this.cdpClient.close();
                        console.log('ğŸ”´ Closed existing CDP connection');
                    } catch (error) {
                        console.log('âš ï¸ Error closing old CDP connection (ignored):', error.message);
                    }
                    this.cdpClient = null;
                }

                // Wait for Chrome to be ready
                await this.waitForChromeReady(this.chromeInstance.port, 5000);

                // Re-establish CDP connection
                const connected = await this.connectToChrome(this.chromeInstance.port);

                if (connected) {
                    console.log('âœ… DevTools reconnection successful');
                    this.updateRendererStatus();
                    return { success: true, message: 'DevTools reconnected successfully' };
                } else {
                    throw new Error('Failed to establish CDP connection');
                }

            } catch (error) {
                console.error('âŒ DevTools reconnection failed:', error);
                this.updateRendererStatus();
                return { success: false, error: error.message };
            }
        });
        console.log('âœ… reconnect-devtools handler registered successfully');

        // Handle MD file import
        ipcMain.handle('import-md-file', async () => {
            try {
                const result = await dialog.showOpenDialog(this.mainWindow, {
                    properties: ['openFile', 'multiSelections'],
                    filters: [
                        { name: 'Markdown Files', extensions: ['md'] },
                        { name: 'Text Files', extensions: ['txt'] },
                        { name: 'All Files', extensions: ['*'] }
                    ]
                });

                if (!result.canceled && result.filePaths.length > 0) {
                    const fs = require('fs');
                    const path = require('path');

                    const files = [];

                    for (const filePath of result.filePaths) {
                        try {
                            const content = fs.readFileSync(filePath, 'utf8');
                            const fileName = path.basename(filePath);

                            files.push({
                                name: fileName,
                                path: filePath,
                                content: content,
                                size: content.length
                            });

                            console.log(`âœ… Loaded file: ${fileName} (${content.length} chars)`);
                        } catch (error) {
                            console.error(`âŒ Error reading file ${filePath}:`, error);
                        }
                    }

                    return {
                        success: true,
                        files: files,
                        count: files.length
                    };
                } else {
                    return {
                        success: false,
                        error: 'No files selected',
                        files: []
                    };
                }
            } catch (error) {
                console.error('âŒ MD import error:', error);
                return {
                    success: false,
                    error: error.message,
                    files: []
                };
            }
        });

        // Handle connection status requests
        ipcMain.handle('get-connection-status', async () => {
            try {
                // Get real Chrome status
                const chromeRunning = this.chromeInstance !== null;
                const chromePid = this.chromeInstance?.pid || null;
                const debugPort = this.chromeInstance?.port || null;

                // Get real connection data
                const activeConnections = this.connectionPool.getActiveConnectionCount();
                const sessions = this.connectionPool.getActiveSessions();

                // Check if Chrome is actually responsive
                let chromeResponsive = false;
                if (chromeRunning && debugPort) {
                    try {
                        const response = await fetch(`http://localhost:${debugPort}/json/version`, {
                            signal: AbortSignal.timeout(2000)
                        });
                        chromeResponsive = response.ok;
                    } catch (error) {
                        chromeResponsive = false;
                    }
                }

                // Check WebSocket server status
                let websocketRunning = false;
                try {
                    const wsResponse = await fetch('http://localhost:9223/health', {
                        signal: AbortSignal.timeout(2000)
                    });
                    websocketRunning = wsResponse.ok;
                } catch (error) {
                    websocketRunning = false;
                }

                // Check Slither connection
                let slitherConnected = false;
                try {
                    const slitherResponse = await fetch('http://localhost:9223/status', {
                        signal: AbortSignal.timeout(2000)
                    });
                    if (slitherResponse.ok) {
                        const data = await slitherResponse.json();
                        slitherConnected = data.connected || false;
                    }
                } catch (error) {
                    slitherConnected = false;
                }

                // Check if Chrome needs installation (only if not running)
                let needsInstallation = false;
                if (!chromeRunning) {
                    needsInstallation = !(await this.chromeLauncher.isChromeInstalled());
                }

                return {
                    chromeRunning,
                    chromePid,
                    debugPort,
                    chromeResponsive,
                    websocketRunning,
                    slitherConnected,
                    activeConnections,
                    sessions,
                    error: null,
                    needsInstallation
                };
            } catch (error) {
                console.error('Error getting connection status:', error);
                return {
                    chromeRunning: false,
                    chromePid: null,
                    debugPort: null,
                    chromeResponsive: false,
                    websocketRunning: false,
                    slitherConnected: false,
                    activeConnections: 0,
                    sessions: [],
                    error: error.message,
                    needsInstallation: false
                };
            }
        });

        // Handle clear sessions request
        ipcMain.handle('clear-sessions', async () => {
            console.log('ğŸ§¹ Clearing all Slither sessions...');
            await this.connectionPool.clearAllSessions();
            return true;
        });

        // Handle user message queue
        ipcMain.handle('queue-user-message', (event, message) => {
            console.log('ğŸ’¬ User message queued:', message);
            // Store message for Slither to retrieve
            this.connectionPool.queueUserMessage(message);
            return true;
        });

        // Handle real terminal streaming - write user input to PTY
        ipcMain.on('terminal-write', (event, data) => {
            console.log('ğŸ“¤ Received terminal write:', JSON.stringify(data));
            if (this.ptyProcess) {
                this.ptyProcess.write(data);
            } else {
                console.warn('âš ï¸ No PTY process available for write');
            }
        });

        // Handle terminal resize
        ipcMain.on('terminal-resize', (event, { cols, rows }) => {
            if (this.ptyProcess) {
                this.ptyProcess.resize(cols, rows);
            }
        });

        // Handle webview errors
        ipcMain.on('webview-error', (event, error) => {
            console.error(`âŒ Error from DevTools Viewport: ${error.message}`);
            const errorObject = {
                source: error.source || 'WebView',
                message: error.message,
                timestamp: new Date().toISOString(),
                type: 'error'
            };
            // Broadcast the formatted error back to the renderer
            if (this.mainWindow && !this.mainWindow.isDestroyed()) {
                this.mainWindow.webContents.send('new-error-log', errorObject);
            }
        });

        // Handle Chrome initialization (on-demand)
        ipcMain.handle('initialize-chrome', async (event) => {
            try {
                console.log('ğŸš€ User/AI requested Chrome initialization...');
                const success = await this.initializeChrome();
                // CRITICAL: Update the UI after initialization
                this.updateRendererStatus();
                return { success };
            } catch (error) {
                console.error('âŒ Chrome initialization error:', error);
                // Update UI even on error to show error state
                this.updateRendererStatus();
                return { success: false, error: error.message };
            }
        });

        // Handle Chrome restart
        ipcMain.handle('restart-chrome', async (event) => {
            try {
                console.log('ğŸ”„ Restarting Chrome via full cleanup and re-initialization...');

                // 1. Perform a full cleanup to shut down everything gracefully.
                await this.cleanup();

                // 2. Wait a moment for the OS to release ports and processes.
                await new Promise(resolve => setTimeout(resolve, 500));

                // 3. Re-initialize a completely fresh Chrome instance and all connections.
                const result = await this.initializeChrome();

                this.updateRendererStatus(); // Inform the UI of the new state
                return { success: result.success };

            } catch (error) {
                console.error('âŒ A critical error occurred during Chrome restart:', error);
                this.updateRendererStatus({ error: error.message });
                return { success: false, error: error.message };
            }
        });


        // Handle navigate to canvas using CDP
        ipcMain.handle('navigate-canvas', async (event) => {
            try {
                if (this.cdpClient) {
                    const canvasUrl = 'http://localhost:8000/canvas.html';
                    console.log(`ğŸ¨ Navigating to canvas via CDP: ${canvasUrl}`);
                    await this.cdpClient.Page.navigate({ url: canvasUrl });
                    console.log('âœ… Navigated to Canvas using CDP');
                    return { success: true };
                } else {
                    throw new Error('Chrome is not connected. Please initialize first.');
                }
            } catch (error) {
                console.error('âŒ Canvas navigation failed:', error);
                return { success: false, error: error.message };
            }
        });



        // Handle send message
        ipcMain.handle('send-message', async (event, messageData) => {
            try {
                console.log(`ğŸ“¤ Message received: ${messageData.type} - ${messageData.content}`);
                // Store message for Slither to read
                this.messageQueue.push(messageData);
                return { success: true };
            } catch (error) {
                console.error('âŒ Message handling failed:', error);
                return { success: false, error: error.message };
            }
        });

        // Handle export logs
        ipcMain.handle('export-logs', async (event) => {
            try {
                const fs = require('fs');
                const path = require('path');

                const logData = {
                    timestamp: new Date().toISOString(),
                    chromeStatus: this.chromeInstance ? {
                        pid: this.chromeInstance.pid,
                        port: this.chromeInstance.port,
                        running: true
                    } : { running: false },
                    connections: this.connectionPool.getActiveConnectionCount(),
                    sessions: this.connectionPool.getActiveSessions(),
                    messageQueue: this.messageQueue
                };

                const logPath = path.join(process.cwd(), `container-logs-${Date.now()}.json`);
                fs.writeFileSync(logPath, JSON.stringify(logData, null, 2));

                console.log(`âœ… Logs exported to: ${logPath}`);
                return { success: true, filePath: logPath };
            } catch (error) {
                console.error('âŒ Log export failed:', error);
                return { success: false, error: error.message };
            }
        });

        console.log('âœ… All IPC handlers registered successfully');

        // Debug: Test if handler is accessible
        try {
            const testResult = ipcMain._invokeHandlers?.get('reconnect-devtools');
            console.log('ğŸ“‹ Handler test result:', testResult ? 'FOUND' : 'NOT FOUND');
        } catch (error) {
            console.log('ğŸ“‹ Handler test error:', error.message);
        }

        // **TEST ERROR LOGGING PIPELINE**
        setTimeout(() => {
            console.log('ğŸ§ª Testing error logging pipeline...');
            const testError = {
                source: 'Test Pipeline',
                message: 'This is a test error to verify the logging pipeline works',
                timestamp: new Date().toISOString(),
                type: 'error'
            };
            if (this.mainWindow && !this.mainWindow.isDestroyed()) {
                console.log('ğŸ“¤ Sending test error to renderer...');
                this.mainWindow.webContents.send('new-error-log', testError);
            }
        }, 8000); // Increased delay to ensure renderer is fully loaded
    }

    // OLD executeSystemCommand REMOVED - Using real PTY terminal now

    setupTerminal() {
        try {
            const shell = os.platform() === 'win32' ? 'powershell.exe' : 'bash';
            this.ptyProcess = pty.spawn(shell, [], {
                name: 'xterm-color',
                cols: 80,
                rows: 30,
                cwd: app.getPath('home'),
                env: process.env
            });

            this.ptyProcess.onData(data => {
                // **RACE CONDITION FIX**: Check if the window still exists before sending.
                if (this.mainWindow && !this.mainWindow.isDestroyed()) {
                    this.mainWindow.webContents.send('terminal-data', data);
                }
            });

            // **FIXED RESPAWN LOGIC**: Only respawn on an actual error (non-zero exit code).
            this.ptyProcess.onExit(({ exitCode }) => {
                console.log(`ğŸ”´ Terminal process exited with code ${exitCode}`);
                if (exitCode !== 0) {
                    console.log('Respawning terminal due to error...');
                    this.setupTerminal();
                }
            });

            this.ptyProcess.write('\r'); // Kickstart the terminal
        } catch (error) {
            console.error('âŒ PTY process failed to spawn:', error);
        }
    }

    // Live metrics gathering function
    async gatherMetrics() {
        let cpuUsage = 0;
        try {
            // app.getCPUUsage() may not be available in all Electron versions
            if (typeof app.getCPUUsage === 'function') {
                cpuUsage = app.getCPUUsage().percentCPUUsage;
            } else {
                // Fallback: use process CPU usage estimation
                cpuUsage = process.cpuUsage().user / 1000000; // Convert to percentage approximation
            }
        } catch (error) {
            cpuUsage = 0; // Fallback to 0 if unable to get CPU usage
        }

        const metrics = {
            cpu: cpuUsage.toFixed(1),
            memory: (process.memoryUsage().heapUsed / 1024 / 1024).toFixed(1),
            chromeTabs: 0,
            connections: this.connectionPool.getActiveConnectionCount()
        };

        if (this.cdpClient) {
            try {
                // Use CDP to get tab information instead of fetch
                const targets = await this.cdpClient.Target.getTargets();
                // Count page targets (not service workers, etc.)
                metrics.chromeTabs = targets.targetInfos.filter(target => 
                    target.type === 'page' && !target.url.startsWith('devtools://')
                ).length;
            } catch (error) {
                // Ignore errors if Chrome is closing or not responsive
                metrics.chromeTabs = 0;
            }
        }
        return metrics;
    }

    // Centralized status updater - single source of truth for UI updates
    async updateRendererStatus() {
        if (this.mainWindow && !this.mainWindow.isDestroyed()) {
            const isRunning = this.chromeInstances && this.chromeInstances.length > 0;
            const metrics = await this.gatherMetrics(); // Gather fresh metrics

            const status = {
                running: isRunning,
                pid: this.chromeInstance?.pid || null,
                port: this.chromeInstance?.port || null,
                webview: true,
                instanceCount: this.chromeInstances?.length || 0,
                error: null,
                ...metrics // Add all the live metrics to the status object
            };

            safeLog('ğŸ“¡ Sending chrome-status with live metrics:', status);
            this.mainWindow.webContents.send('chrome-status', status);
        }
    }

    async cleanup() {
        console.log('ğŸ§¹ Cleaning up Chrome container...');

        clearInterval(this.metricsInterval); // Clear live metrics timer

        if (this.cdpClient) {
            try {
                await this.cdpClient.close();
            } catch (e) {
                // Ignore errors closing CDP client
            }
            this.cdpClient = null;
        }

        if (this.connectionPool) {
            await this.connectionPool.cleanup();
        }

        if (this.chromeInstance) {
            await this.chromeLauncher.killChrome();
        }

        if (this.ptyProcess) {
            this.ptyProcess.kill();
            this.ptyProcess = null;
        }

        this.mainWindow = null;

        // Don't quit the app - let it stay running as a container
        console.log('âœ… Container cleanup complete, app continues running...');
    }
}

// Create app instance
const containerApp = new SlitherChromeContainer();

// App event handlers
